  // Convert Sales Order to Journal Entry
  const convertSalesOrderToJournalEntry = useCallback((order, accountsList) => {
    const entries = [];
    const subTotal = parseFloat(order.subTotal) || 0;
    const taxAmount = parseFloat(order.taxAmount) || 0;
    const total = parseFloat(order.total) || 0;
    if (!order.paid || !order.fulfilled || order.status !== 'Closed') {
      return null;
    }

    // Enhanced function to find specific accounts from your chart of accounts
    const findSpecificAccount = accountCode => {
      const account = accountsList.find(acc => acc.code === accountCode || acc.id === accountCode);
      return account || null;
    };
    const findAccountByType = (type, preferredCode = null) => {
      // First try to find the preferred specific account
      if (preferredCode) {
        const specificAccount = findSpecificAccount(preferredCode);
        if (specificAccount) return specificAccount;
      }

      // Then try to find by type
      const matchingAccount = accountsList.find(acc => acc.type && acc.type.toLowerCase() === type.toLowerCase());
      if (matchingAccount) return matchingAccount;

      // Fallback with proper account codes from your chart
      const fallbackAccounts = {
        'assets': {
          id: '1112',
          code: '1112',
          name: 'Bank - Main Account',
          type: 'assets'
        },
        'revenue': {
          id: '4110',
          code: '4110',
          name: 'Mobile Service Revenue',
          type: 'revenue'
        },
        'liabilities': {
          id: '2130',
          code: '2130',
          name: 'Sales Tax Payable',
          type: 'liabilities'
        },
        'expenses': {
          id: '5100',
          code: '5100',
          name: 'Cost of Goods Sold',
          type: 'expenses'
        }
      };
      return fallbackAccounts[type.toLowerCase()] || {
        id: 'UNKNOWN',
        name: `Unknown ${type} Account`,
        code: 'N/A'
      };
    };
    const cashAccount = findAccountByType('assets');
    entries.push({
      accountId: cashAccount.id,
      accountName: cashAccount.name,
      accountCode: cashAccount.code,
      debit: total,
      credit: 0,
      description: `Cash received from Order #${order.orderId} - ${order.lineItems.map(item => item.productName).join(', ')}`
    });
    if (subTotal > 0) {
      const revenueAccount = findAccountByType('revenue');
      entries.push({
        accountId: revenueAccount.id,
        accountName: revenueAccount.name,
        accountCode: revenueAccount.code,
        debit: 0,
        credit: subTotal,
        description: `Revenue from Order #${order.orderId}`
      });
    }
    if (taxAmount > 0) {
      const taxAccount = findAccountByType('liabilities');
      entries.push({
        accountId: taxAccount.id,
        accountName: taxAccount.name,
        accountCode: taxAccount.code,
        debit: 0,
        credit: taxAmount,
        description: `${order.taxRate}% tax collected on Order #${order.orderId}`
      });
    }
    let totalCOGS = 0;
    order.lineItems.forEach(item => {
      if (item.fulfilled && item.status === 'Fulfilled') {
        const costPrice = parseFloat(item.costPrice) || 0;
        const quantity = parseFloat(item.quantity) || 0;
        const itemCOGS = costPrice * quantity;
        totalCOGS += itemCOGS;
      }
    });
    if (totalCOGS > 0) {
      const cogsAccount = findAccountByType('expenses');
      entries.push({
        accountId: cogsAccount.id,
        accountName: cogsAccount.name,
        accountCode: cogsAccount.code,
        debit: totalCOGS,
        credit: 0,
        description: `COGS for Order #${order.orderId}`
      });
      const inventoryAccount = findAccountByType('assets');
      entries.push({
        accountId: inventoryAccount.id,
        accountName: inventoryAccount.name,
        accountCode: inventoryAccount.code,
        debit: 0,
        credit: totalCOGS,
        description: `Inventory reduction - Order #${order.orderId}`
      });
    }
    const totalDebit = entries.reduce((sum, entry) => sum + parseFloat(entry.debit || 0), 0);
    const totalCredit = entries.reduce((sum, entry) => sum + parseFloat(entry.credit || 0), 0);
    return {
      id: `JE-ORDER-${order.orderId}`,
      date: formatDateFromCreatedAt(order.createdAt),
      description: `Sales Order #${order.orderId} - ${order.lineItems.map(item => `${item.productName} (${item.quantity})`).join(', ')}`,
      reference: `ORDER-${order.orderId}`,
      transactionType: 'automatic',
      source: 'POS Sales',
      status: 'posted',
      entries: entries,
      totalDebit: Math.round(totalDebit * 100) / 100,
      totalCredit: Math.round(totalCredit * 100) / 100,
      createdAt: order.createdAt,
      originalOrder: order
    };
  }, []);

  // Load Journal Entries from API and Local Storage
  const loadJournalEntriesFromAPI = useCallback(async () => {
    setLoading(prev => ({
      ...prev,
      entries: true
    }));
    setErrors(prev => ({
      ...prev,
      entries: null
    }));
    try {
      const salesOrders = await fetchOrderData();
      if (!salesOrders) {
        throw new Error('Failed to fetch sales orders');
      }
      const journalEntriesFromSales = salesOrders.filter(order => order.paid && order.fulfilled && order.status === 'Closed').map(order => convertSalesOrderToJournalEntry(order, accounts)).filter(entry => entry !== null);
      let manualEntries = [];
      try {
        const storedEntries = localStorage.getItem('manualJournalEntries');
        manualEntries = storedEntries ? JSON.parse(storedEntries) : [];
      } catch (error) {
        console.warn('Error loading manual entries from localStorage:', error);
        manualEntries = [];
      }
      const allEntries = [...journalEntriesFromSales, ...manualEntries];
      setJournalEntries(allEntries);
      setFilteredEntries(allEntries);
      setLoading(prev => ({
        ...prev,
        entries: false
      }));
    } catch (error) {
      console.error('Error loading journal entries:', error);
      setErrors(prev => ({
        ...prev,
        entries: error.message
      }));
      setLoading(prev => ({
        ...prev,
        entries: false
      }));
    }
  }, [accounts, convertSalesOrderToJournalEntry]);

  // Filter entries
  const filterEntries = useCallback(() => {
    let filtered = [...journalEntries];
    if (filters.dateFrom) {
      filtered = filtered.filter(entry => entry.date >= filters.dateFrom);
    }
    if (filters.dateTo) {
      filtered = filtered.filter(entry => entry.date <= filters.dateTo);
    }
    if (filters.accountId) {
      filtered = filtered.filter(entry => {
        return entry.entries.some(e => e.accountId === filters.accountId);
      });
    }
    if (filters.transactionType !== 'all') {
      filtered = filtered.filter(entry => entry.transactionType === filters.transactionType);
    }
    if (filters.searchTerm) {
      const term = filters.searchTerm.toLowerCase();
      filtered = filtered.filter(entry => entry.description.toLowerCase().includes(term) || entry.reference.toLowerCase().includes(term) || entry.entries.some(e => e.description.toLowerCase().includes(term)));
    }
    setFilteredEntries(filtered);
  }, [journalEntries, filters]);

  // Generate trial balance
  const generateTrialBalance = useCallback(() => {
    const balances = {};
    const requiredAccountIds = new Set();
    journalEntries.forEach(entry => {
      if (entry.status === 'posted') {
        entry.entries.forEach(entryLine => {
          if (entryLine.accountId) {
            requiredAccountIds.add(entryLine.accountId);
          }
        });
      }
    });
    const accountTypesMap = {};
    accounts.forEach(account => {
      accountTypesMap[account.id] = account.type;
      accountTypesMap[account.code] = account.type;
    });
    accounts.forEach(account => {
      if (account.category !== 'header') {
        balances[account.id] = {
          accountId: account.id,
          accountCode: account.code,
          accountName: account.name,
          accountType: account.type || 'unknown',
          debit: 0,
          credit: 0,
          balance: 0
        };
      }
    });
    requiredAccountIds.forEach(accountId => {
      if (!balances[accountId]) {
        let accountName = '';
        let accountType = '';
        journalEntries.forEach(entry => {
          entry.entries.forEach(entryLine => {
            if (entryLine.accountId === accountId) {
              if (entryLine.accountName) accountName = entryLine.accountName;
            }
          });
        });
        const prefix = accountId.toString().substring(0, 1);
        if (prefix === '1') accountType = 'assets';else if (prefix === '2') accountType = 'liabilities';else if (prefix === '3') accountType = 'equity';else if (prefix === '4') accountType = 'revenue';else if (prefix === '5') accountType = 'expenses';else accountType = accountTypesMap[accountId] || 'unknown';
        balances[accountId] = {
          accountId: accountId,
          accountCode: accountId,
          accountName: accountName || `Account ${accountId}`,
          accountType: accountType,
          debit: 0,
          credit: 0,
          balance: 0
        };
      }
    });
    journalEntries.forEach(entry => {
      if (entry.status === 'posted') {
        entry.entries.forEach(entryLine => {
          const accountId = entryLine.accountId;
          if (balances[accountId]) {
            balances[accountId].debit += parseFloat(entryLine.debit || 0);
            balances[accountId].credit += parseFloat(entryLine.credit || 0);
            if (entryLine.accountName && !balances[accountId].accountName) {
              balances[accountId].accountName = entryLine.accountName;
            }
          }
        });
      }
    });
    Object.values(balances).forEach(account => {
      if (account.accountType === 'assets' || account.accountType === 'expenses') {
        account.balance = account.debit - account.credit;
      } else {
        account.balance = account.credit - account.debit;
      }
    });
    const trialBalanceData = Object.values(balances).filter(account => account.debit > 0 || account.credit > 0).sort((a, b) => a.accountCode.localeCompare(b.accountCode));
    setTrialBalance(trialBalanceData);
    setShowTrialBalance(true);
  }, [accounts, journalEntries]);

  // Helper functions for entry management
  const addEntryLine = () => {
    setNewEntry({
      ...newEntry,
      entries: [...newEntry.entries, {
        accountId: '',
        debit: 0,
        credit: 0,
        description: ''
      }]
    });
  };
  const removeEntryLine = index => {
    if (newEntry.entries.length > 2) {
      const updatedEntries = newEntry.entries.filter((_, i) => i !== index);
      setNewEntry({
        ...newEntry,
        entries: updatedEntries
      });
    }
  };
  const updateEntryLine = (index, field, value) => {
    const updatedEntries = [...newEntry.entries];
    updatedEntries[index] = {
      ...updatedEntries[index],
      [field]: value
    };
    setNewEntry({
      ...newEntry,
      entries: updatedEntries
