const GeneralLedger = () => {
  const [journalEntries, setJournalEntries] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const [filteredEntries, setFilteredEntries] = useState([]);
  const [selectedEntry, setSelectedEntry] = useState(null);
  const [isAddingEntry, setIsAddingEntry] = useState(false);
  const [loading, setLoading] = useState({
    entries: false,
    accounts: false
  });
  const [errors, setErrors] = useState({
    entries: null,
    accounts: null
  });
  const [filters, setFilters] = useState({
    dateFrom: '',
    dateTo: '',
    accountId: '',
    transactionType: 'all',
    searchTerm: ''
  });
  const [trialBalance, setTrialBalance] = useState([]);
  const [showTrialBalance, setShowTrialBalance] = useState(false);
  const [newEntry, setNewEntry] = useState({
    date: new Date().toISOString().split('T')[0],
    description: '',
    reference: '',
    entries: [
      { accountId: '', debit: 0, credit: 0, description: '' },
      { accountId: '', debit: 0, credit: 0, description: '' }
    ]
  });

  // API Configuration
  const API_BASE_URL = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/orders?start=0&size=200';
  const API_BASE_URL_CHARTS_OF_ACCOUNTS = 'http://localhost:8080/api/accounts';

  // Fetch order data from API
  const fetchOrderData = async () => {
    try {
      const response = await fetch(API_BASE_URL, {
        method: 'GET',
        headers: {
          'Authorization': 'Bearer Hamid',
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`API request failed with status ${response.status}`);
      }
      
      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error fetching order data:', error);
      return null;
    }
  };

  // Format date from API format
  const formatDateFromCreatedAt = (createdAt) => {
    try {
      const parts = createdAt.split('.');
      if (parts.length >= 3) {
        return `${parts[0]}-${parts[1].padStart(2, '0')}-${parts[2].padStart(2, '0')}`;
      }
      return new Date().toISOString().split('T')[0];
    } catch (error) {
      return new Date().toISOString().split('T')[0];
    }
  };

  // Fetch Charts of Accounts from API
  const loadChartsOfAccounts = useCallback(async () => {
    setLoading(prev => ({ ...prev, accounts: true }));
    setErrors(prev => ({ ...prev, accounts: null }));
    
    try {
      const response = await fetch(API_BASE_URL_CHARTS_OF_ACCOUNTS, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`Failed to fetch accounts: ${response.status}`);
      }
      
      const data = await response.json();
      console.log('Raw accounts data from API:', data);
      
      // Check if the data is in the expected format
      if (!Array.isArray(data)) {
        throw new Error('API response is not an array');
      }
      
      let formattedAccounts = [];
      
      // Your API returns accounts in object format like: 
      // {id: '1', code: '1000', name: 'Assets', type: 'assets', category: 'header', ...}
      if (data.length > 0) {
        const firstItem = data[0];
        
        if (typeof firstItem === 'object' && firstItem.hasOwnProperty('id')) {
          console.log('Processing object format from API');
          console.log('Sample account:', firstItem);
          
          // Process all accounts including usable ones
          formattedAccounts = data.map(account => ({
            id: account.id,
            code: account.code,
            name: account.name,
            type: account.type,
            category: account.category || 'account',
            parentId: account.parentId,
            level: account.level || 0,
            description: account.description || '',
            active: account.active !== undefined ? account.active : true
          }));
          
          console.log('All formatted accounts:', formattedAccounts);
          
          // Don't filter out headers completely, but mark usable accounts
          const usableAccounts = formattedAccounts.filter(account => 
            account.category === 'account' && 
            account.id && 
            account.code && 
            account.name && 
            account.type
          );
          
          console.log('Usable accounts (category=account):', usableAccounts);
          
          // If we have usable accounts, use them, otherwise use all accounts
          formattedAccounts = usableAccounts.length > 0 ? usableAccounts : formattedAccounts;
          
        } else if (Array.isArray(firstItem)) {
          // Data is in array format - convert to objects
          console.log('Converting array format from API');
          formattedAccounts = data
            .filter(account => account[4] === 'account') // Only account category
            .map(account => ({
              id: account[0],
              code: account[1],
              name: account[2],
              type: account[3],
              category: account[4] || 'account',
              parentId: account[5],
              level: parseInt(account[6]) || 0,
              description: account[7] || ''
            }));
        } else {
          console.warn('Unknown API data format, using fallback');
          throw new Error('Unknown API data format');
        }
      }
      
      // Filter out any invalid accounts and ensure we have valid data
      formattedAccounts = formattedAccounts.filter(account => 
        account.id && 
        account.code && 
        account.name && 
        account.type &&
        account.category === 'account' // Only use actual accounts, not headers
      );
      
      console.log('Final filtered accounts:', formattedAccounts);
      
      // If no valid accounts found, throw error to trigger fallback
      if (formattedAccounts.length === 0) {
        console.log('No usable accounts found in API response');
        throw new Error('No usable accounts returned from API - only headers found');
      }
      
      // Sort by account code
      formattedAccounts.sort((a, b) => a.code.localeCompare(b.code));
      
      setAccounts(formattedAccounts);
      setLoading(prev => ({ ...prev, accounts: false }));
      
      console.log(`Successfully loaded ${formattedAccounts.length} accounts from API`);
      return formattedAccounts;
      
