import React, { useState, useEffect } from "react";
import {
  CreditCard,
  BarChart2,
  FileText,
  Percent,
  BookOpen,
  List,
  Menu,
  X,
  ChevronRight,
  Home,
  Settings,
  HelpCircle,
  LogOut,
  User,
  Bell,
  Search,
  TrendingUp,
  DollarSign,
  Activity,
} from "lucide-react";

// Page imports (mock components for demo)
const AccountsReceivable = () => <div className="p-6"><h2 className="text-2xl font-bold">Accounts Receivable</h2></div>;
const AccountsPayable = () => <div className="p-6"><h2 className="text-2xl font-bold">Accounts Payable</h2></div>;
const FinancialStatements = () => <div className="p-6"><h2 className="text-2xl font-bold">Financial Statements</h2></div>;
const ManagementReports = () => <div className="p-6"><h2 className="text-2xl font-bold">Management Reports</h2></div>;
const TaxReporting = () => <div className="p-6"><h2 className="text-2xl font-bold">Tax Reporting</h2></div>;
const GeneralLedger = () => <div className="p-6"><h2 className="text-2xl font-bold">General Ledger</h2></div>;
const ChartOfAccounts = () => <div className="p-6"><h2 className="text-2xl font-bold">Chart of Accounts</h2></div>;
const Footer = () => <div>© 2024 HULM Finance. All rights reserved.</div>;

const navOptions = [
  {
    key: "dashboard",
    label: "Dashboard",
    icon: <Home size={20} />,
    component: () => (
      <div className="p-6">
        <h2 className="text-2xl font-bold mb-6">Financial Dashboard</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {[
            { label: "Total Revenue", value: "$125,430", icon: <DollarSign />, change: "+12.5%", color: "green" },
            { label: "Expenses", value: "$48,200", icon: <TrendingUp />, change: "-5.2%", color: "red" },
            { label: "Net Profit", value: "$77,230", icon: <Activity />, change: "+18.3%", color: "green" },
            { label: "Cash Flow", value: "$92,150", icon: <BarChart2 />, change: "+7.8%", color: "green" },
          ].map((stat, idx) => (
            <div key={idx} className="bg-white rounded-xl shadow-sm border border-gray-100 p-6 hover:shadow-md transition-shadow">
              <div className="flex items-center justify-between mb-4">
                <div className={`p-2 rounded-lg ${stat.color === 'green' ? 'bg-green-100 text-green-600' : 'bg-red-100 text-red-600'}`}>
                  {stat.icon}
                </div>
                <span className={`text-sm font-semibold ${stat.color === 'green' ? 'text-green-600' : 'text-red-600'}`}>
                  {stat.change}
                </span>
              </div>
              <h3 className="text-gray-600 text-sm mb-1">{stat.label}</h3>
              <p className="text-2xl font-bold text-gray-900">{stat.value}</p>
            </div>
          ))}
        </div>
      </div>
    ),
    badge: null,
  },
  {
    type: "separator",
    label: "Accounting",
  },
  {
    key: "COA",
    label: "Chart of Accounts",
    icon: <List size={20} />,
    component: ChartOfAccounts,
    badge: null,
  },
  {
    key: "ledger",
    label: "General Ledger",
    icon: <BookOpen size={20} />,
    component: GeneralLedger,
    badge: "New",
  },
  {
    key: "ar",
    label: "Accounts Receivable",
    icon: <CreditCard size={20} />,
    component: AccountsReceivable,
    badge: "3",
  },
  {
    key: "ap",
    label: "Accounts Payable",
    icon: <CreditCard size={20} />,
    component: AccountsPayable,
    badge: null,
  },
  {
    type: "separator",
    label: "Reporting",
  },
  {
    key: "statements",
    label: "Financial Statements",
    icon: <FileText size={20} />,
    component: FinancialStatements,
    badge: null,
  },
  {
    key: "mgmt-reports",
    label: "Management Reports",
    icon: <BarChart2 size={20} />,
    component: ManagementReports,
    badge: null,
  },
  {
    key: "tax",
    label: "Tax Reporting",
    icon: <Percent size={20} />,
    component: TaxReporting,
    badge: null,
  },
];

const SidebarLayout = () => {
  const [selectedKey, setSelectedKey] = useState("dashboard");
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [notifications, setNotifications] = useState(3);
  
  // Check screen size for responsive behavior
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 768) {
        setIsSidebarOpen(false);
        setIsCollapsed(false);
      } else if (window.innerWidth < 1024) {
        setIsCollapsed(true);
      } else {
        setIsCollapsed(false);
      }
    };

    handleResize();
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const SelectedComponent =
    navOptions.find((opt) => opt.key === selectedKey)?.component || (() => <div>Select a page</div>);

  const filteredOptions = navOptions.filter(opt => 
    !opt.type && opt.label.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-br from-gray-50 to-gray-100">
      {/* Top Navigation Bar */}
      <header className="bg-white shadow-sm border-b border-gray-200 px-4 lg:px-6 h-16 flex items-center justify-between fixed w-full top-0 z-40">
        <div className="flex items-center gap-4">
          {/* Mobile menu button */}
          <button
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            className="lg:hidden p-2 rounded-lg hover:bg-gray-100 transition-colors"
            aria-label="Toggle menu"
          >
            <Menu size={24} />
          </button>
          
          {/* Desktop collapse button */}
          <button
            onClick={() => setIsCollapsed(!isCollapsed)}
            className="hidden lg:block p-2 rounded-lg hover:bg-gray-100 transition-colors"
            aria-label="Toggle sidebar"
          >
            <Menu size={24} />
          </button>

          {/* Logo */}
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-lg flex items-center justify-center shadow-lg">
              <BarChart2 size={24} className="text-white" />
            </div>
            <div className="hidden sm:block">
              <h1 className="font-bold text-xl text-gray-900">HULM Finance</h1>
              <p className="text-xs text-gray-500">Enterprise Accounting Suite</p>
            </div>
          </div>
        </div>

        {/* Right side of header */}
        <div className="flex items-center gap-3">
          {/* Search bar - desktop only */}
          <div className="hidden md:flex items-center bg-gray-100 rounded-lg px-3 py-2 w-64">
            <Search size={18} className="text-gray-400 mr-2" />
            <input
              type="text"
              placeholder="Search..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="bg-transparent outline-none flex-1 text-sm"
            />
          </div>

          {/* Notifications */}
          <button className="relative p-2 rounded-lg hover:bg-gray-100 transition-colors">
            <Bell size={20} />
            {notifications > 0 && (
              <span className="absolute -top-1 -right-1 w-5 h-5 bg-red-500 text-white text-xs rounded-full flex items-center justify-center">
                {notifications}
              </span>
            )}
          </button>

          {/* Profile dropdown */}
          <div className="relative">
            <button
              onClick={() => setShowProfileMenu(!showProfileMenu)}
              className="flex items-center gap-2 p-2 rounded-lg hover:bg-gray-100 transition-colors"
            >
              <div className="w-8 h-8 bg-gradient-to-br from-purple-500 to-pink-500 rounded-full flex items-center justify-center">
                <User size={16} className="text-white" />
              </div>
              <ChevronRight 
                size={16} 
                className={`text-gray-600 transition-transform ${showProfileMenu ? 'rotate-90' : ''}`} 
              />
            </button>

            {/* Profile dropdown menu */}
            {showProfileMenu && (
              <div className="absolute right-0 mt-2 w-56 bg-white rounded-lg shadow-lg border border-gray-200 py-2">
                <div className="px-4 py-2 border-b border-gray-100">
                  <p className="font-semibold text-gray-900">John Doe</p>
                  <p className="text-sm text-gray-500">john.doe@hulm.com</p>
                </div>
                <button className="w-full px-4 py-2 text-left hover:bg-gray-50 flex items-center gap-3 text-gray-700">
                  <User size={16} /> Profile
                </button>
                <button className="w-full px-4 py-2 text-left hover:bg-gray-50 flex items-center gap-3 text-gray-700">
                  <Settings size={16} /> Settings
                </button>
                <button className="w-full px-4 py-2 text-left hover:bg-gray-50 flex items-center gap-3 text-gray-700">
                  <HelpCircle size={16} /> Help & Support
                </button>
                <div className="border-t border-gray-100 mt-2 pt-2">
                  <button className="w-full px-4 py-2 text-left hover:bg-gray-50 flex items-center gap-3 text-red-600">
                    <LogOut size={16} /> Sign Out
                  </button>
                </div>
              </div>
            )}
          </div>
        </div>
      </header>

      {/* Main Layout */}
      <div className="flex flex-1 pt-16">
        {/* Overlay for mobile */}
        {isSidebarOpen && (
          <div
            className="fixed inset-0 bg-black bg-opacity-50 z-30 lg:hidden"
            onClick={() => setIsSidebarOpen(false)}
          />
        )}

        {/* Sidebar */}
        <aside
          className={`
            ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'}
            lg:translate-x-0
            fixed lg:sticky top-16 left-0 h-[calc(100vh-4rem)]
            ${isCollapsed ? 'w-20' : 'w-72'}
            bg-white shadow-xl lg:shadow-sm border-r border-gray-200
            transition-all duration-300 ease-in-out
            z-40 lg:z-20
            flex flex-col
          `}
        >
          {/* Search - mobile only, in sidebar */}
          <div className={`md:hidden p-4 border-b border-gray-100 ${isCollapsed ? 'hidden' : ''}`}>
            <div className="flex items-center bg-gray-100 rounded-lg px-3 py-2">
              <Search size={18} className="text-gray-400 mr-2" />
              <input
                type="text"
                placeholder="Search..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="bg-transparent outline-none flex-1 text-sm"
              />
            </div>
          </div>

          {/* Navigation */}
          <nav className="flex-1 py-4 px-3 overflow-y-auto custom-scroll">
            <ul className="space-y-1">
              {(searchQuery ? filteredOptions : navOptions).map((opt, index) => {
                if (opt.type === "separator") {
                  return (
                    <li key={index} className={`${isCollapsed ? 'hidden' : ''}`}>
                      <div className="px-3 py-2 mt-4 mb-2">
                        <p className="text-xs font-semibold text-gray-400 uppercase tracking-wider">
                          {opt.label}
                        </p>
                      </div>
                    </li>
                  );
                }

                return (
                  <li key={opt.key}>
                    <button
                      aria-label={opt.label}
                      className={`
                        group w-full flex items-center gap-3 px-3 py-2.5 rounded-lg
                        font-medium transition-all duration-200 relative
                        ${selectedKey === opt.key
                          ? "bg-gradient-to-r from-emerald-50 to-teal-50 text-emerald-700 shadow-sm"
                          : "text-gray-700 hover:bg-gray-50"
                        }
                      `}
                      onClick={() => {
                        setSelectedKey(opt.key);
                        if (window.innerWidth < 768) {
                          setIsSidebarOpen(false);
                        }
                      }}
                    >
                      {/* Active indicator */}
                      <span
                        className={`
                          absolute left-0 top-1/2 -translate-y-1/2 h-8 w-1 rounded-r-md
                          transition-all duration-200
                          ${selectedKey === opt.key 
                            ? "bg-gradient-to-b from-emerald-500 to-teal-600" 
                            : "bg-transparent group-hover:bg-gray-300"
                          }
                        `}
                      />
                      
                      {/* Icon */}
                      <span className={`
                        ${selectedKey === opt.key ? "text-emerald-600" : "text-gray-500 group-hover:text-gray-700"}
                        ${isCollapsed ? 'mx-auto' : ''}
                      `}>
                        {opt.icon}
                      </span>
                      
                      {/* Label */}
                      {!isCollapsed && (
                        <>
                          <span className="flex-1 text-left">{opt.label}</span>
                          
                          {/* Badge */}
                          {opt.badge && (
                            <span className={`
                              px-2 py-0.5 text-xs rounded-full
                              ${opt.badge === 'New' 
                                ? 'bg-emerald-100 text-emerald-700' 
                                : 'bg-gray-100 text-gray-700'
                              }
                            `}>
                              {opt.badge}
                            </span>
                          )}
                        </>
                      )}
                    </button>

                    {/* Tooltip for collapsed state */}
                    {isCollapsed && (
                      <div className="absolute left-20 top-1/2 -translate-y-1/2 ml-2 px-2 py-1 bg-gray-900 text-white text-sm rounded opacity-0 group-hover:opacity-100 pointer-events-none transition-opacity whitespace-nowrap z-50">
                        {opt.label}
                      </div>
                    )}
                  </li>
                );
              })}
            </ul>
          </nav>

          {/* Sidebar footer */}
          <div className={`p-4 border-t border-gray-100 ${isCollapsed ? 'hidden' : ''}`}>
            <div className="bg-gradient-to-r from-emerald-50 to-teal-50 rounded-lg p-4">
              <div className="flex items-center gap-3 mb-3">
                <div className="w-10 h-10 bg-gradient-to-br from-emerald-500 to-teal-600 rounded-full flex items-center justify-center">
                  <TrendingUp size={20} className="text-white" />
                </div>
                <div>
                  <p className="font-semibold text-gray-900">Pro Plan</p>
                  <p className="text-xs text-gray-500">Upgrade for more features</p>
                </div>
              </div>
              <button className="w-full bg-gradient-to-r from-emerald-600 to-teal-600 text-white rounded-lg py-2 text-sm font-medium hover:shadow-lg transition-shadow">
                Upgrade Now
              </button>
            </div>
          </div>
        </aside>

        {/* Main Content Area */}
        <main className="flex-1 flex flex-col">
          <div className="flex-1 p-4 lg:p-8 overflow-y-auto">
            <div className="w-full max-w-7xl mx-auto">
              <div className="bg-white rounded-xl shadow-sm border border-gray-200 min-h-[calc(100vh-12rem)]">
                <SelectedComponent />
              </div>
            </div>
          </div>

          {/* Footer */}
          <footer className="bg-white border-t border-gray-200 px-6 py-4">
            <div className="max-w-7xl mx-auto flex flex-col sm:flex-row items-center justify-between text-sm text-gray-500">
              <Footer />
              <div className="flex items-center gap-4 mt-2 sm:mt-0">
                <a href="#" className="hover:text-gray-700 transition-colors">Privacy Policy</a>
                <a href="#" className="hover:text-gray-700 transition-colors">Terms of Service</a>
                <a href="#" className="hover:text-gray-700 transition-colors">Contact</a>
              </div>
            </div>
          </footer>
        </main>
      </div>

      <style jsx>{`
        .custom-scroll::-webkit-scrollbar {
          width: 6px;
        }
        .custom-scroll::-webkit-scrollbar-track {
          background: #f1f1f1;
          border-radius: 10px;
        }
        .custom-scroll::-webkit-scrollbar-thumb {
          background: #888;
          border-radius: 10px;
        }
        .custom-scroll::-webkit-scrollbar-thumb:hover {
          background: #555;
        }
      `}</style>
    </div>
  );
};

export default SidebarLayout;
