import React, { useState } from 'react';
import { 
  XCircle, 
  CheckCircle, 
  Plus, 
  User,
  Calendar,
  DollarSign,
  FileText,
  AlertCircle,
  CreditCard,
  Info
} from 'lucide-react';

const CreateInvoiceModal = ({ customers, onClose, onCreate }) => {
  const [form, setForm] = useState({
    customerId: '',
    dueDate: '',
    items: [{ description: '', quantity: 1, price: 0, total: 0 }],
    notes: ''
  });

  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);

  const addItem = () => {
    setForm({ 
      ...form, 
      items: [...form.items, { description: '', quantity: 1, price: 0, total: 0 }] 
    });
  };

  const removeItem = (idx) => {
    if (form.items.length > 1) {
      setForm({ ...form, items: form.items.filter((_, i) => i !== idx) });
    }
  };

  const updateItem = (idx, field, value) => {
    const updated = [...form.items];
    updated[idx] = { ...updated[idx], [field]: value };
    
    if (field === 'quantity' || field === 'price') {
      const qty = field === 'quantity' ? parseFloat(value) || 0 : updated[idx].quantity;
      const price = field === 'price' ? parseFloat(value) || 0 : updated[idx].price;
      updated[idx].total = qty * price;
    }
    
    setForm({ ...form, items: updated });
    
    // Clear item-specific errors
    if (errors.items) {
      setErrors({ ...errors, items: null });
    }
  };

  const calcTotal = () => form.items.reduce((sum, i) => sum + (i.total || 0), 0);

  const validateForm = () => {
    const newErrors = {};

    if (!form.customerId) {
      newErrors.customerId = 'Please select a customer';
    }

    if (!form.dueDate) {
      newErrors.dueDate = 'Please select a due date';
    }

    const validItems = form.items.filter(item => 
      item.description && item.quantity > 0 && item.price > 0
    );

    if (validItems.length === 0) {
      newErrors.items = 'Please add at least one valid item with description, quantity, and price';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const getSelectedCustomer = () => {
    return customers.find(c => c.id === form.customerId);
  };

  const handleSubmit = async () => {
    if (!validateForm()) {
      return;
    }

    const selectedCustomer = getSelectedCustomer();
    const total = calcTotal();

    // Credit limit validation
    if (selectedCustomer && selectedCustomer.creditLimit > 0) {
      const availableCredit = selectedCustomer.creditLimit - (selectedCustomer.totalOwed || 0);
      if (total > availableCredit) {
        setErrors({
          creditLimit: `Invoice amount ($${total.toFixed(2)}) exceeds available credit limit ($${availableCredit.toFixed(2)})`
        });
        return;
      }
    }

    setIsSubmitting(true);
    
    try {
      const invoiceData = {
        ...form,
        amount: total,
        paid: 0,
        balance: total,
        status: 'outstanding',
        date: new Date().toISOString().substr(0, 10),
        customerName: selectedCustomer?.name || '',
        items: form.items.filter(item => item.description && item.quantity > 0),
        paymentTerms: selectedCustomer?.paymentTerms || 30
      };

      await onCreate(invoiceData);
      onClose();
    } catch (error) {
      setErrors({ submit: error.message || 'Failed to create invoice' });
    } finally {
      setIsSubmitting(false);
    }
  };

  const selectedCustomer = getSelectedCustomer();
  const totalAmount = calcTotal();

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl max-w-4xl w-full max-h-[95vh] overflow-hidden shadow-2xl animate-in fade-in duration-300">
        {/* Header */}
        <div className="bg-gradient-to-r from-emerald-50 to-blue-50 px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-emerald-100 rounded-lg">
                <FileText className="text-emerald-600" size={20} />
              </div>
              <div>
                <h3 className="text-xl font-bold text-gray-900">Create New Invoice</h3>
                <p className="text-sm text-gray-600">Generate invoice for customer purchase</p>
              </div>
            </div>
            <button
              onClick={onClose}
              className="p-2 text-gray-400 hover:text-gray-600 hover:bg-gray-100 rounded-lg transition-colors"
            >
              <XCircle size={20} />
            </button>
          </div>
        </div>

        {/* Content */}
        <div className="overflow-y-auto max-h-[calc(95vh-80px)]">
          <div className="p-6 space-y-6">
            {/* Error Messages */}
            {(errors.submit || errors.creditLimit) && (
              <div className="bg-red-50 border border-red-200 rounded-xl p-4">
                <div className="flex items-center">
                  <AlertCircle className="text-red-500 mr-2" size={16} />
                  <span className="text-red-800 text-sm font-medium">
                    {errors.submit || errors.creditLimit}
                  </span>
                </div>
              </div>
            )}

            {/* Customer & Date Selection */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              {/* Customer Selection */}
              <div className="bg-white border border-gray-200 rounded-xl p-5">
                <h4 className="font-semibold text-gray-900 mb-4 flex items-center">
                  <User size={16} className="mr-2 text-emerald-600" />
                  Customer Information
                </h4>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Select Customer *
                    </label>
                    <select
                      value={form.customerId}
                      onChange={e => {
                        setForm({ ...form, customerId: e.target.value });
                        if (errors.customerId) setErrors({ ...errors, customerId: null });
                      }}
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent transition-colors ${
                        errors.customerId ? 'border-red-300 bg-red-50' : 'border-gray-300'
                      }`}
                    >
                      <option value="">Choose a customer...</option>
                      {customers.filter(c => c.customerType !== 'cash').map(customer => (
                        <option key={customer.id} value={customer.id}>
                          {customer.name} ({customer.id})
                        </option>
                      ))}
                    </select>
                    {errors.customerId && (
                      <p className="text-red-500 text-xs mt-1 flex items-center">
                        <AlertCircle size={12} className="mr-1" />
                        {errors.customerId}
                      </p>
                    )}
                  </div>

                  {/* Customer Info Display */}
                  {selectedCustomer && (
                    <div className="mt-4 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                      <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 text-sm">
                        <div>
                          <span className="text-blue-600 font-medium">Payment Terms:</span>
                          <span className="ml-2 text-blue-800">{selectedCustomer.paymentTerms} days</span>
                        </div>
                        <div>
                          <span className="text-blue-600 font-medium">Credit Limit:</span>
                          <span className="ml-2 text-blue-800">${selectedCustomer.creditLimit?.toLocaleString()}</span>
                        </div>
                        <div className="sm:col-span-2">
                          <span className="text-blue-600 font-medium">Available Credit:</span>
                          <span className="ml-2 text-blue-800">
                            ${((selectedCustomer.creditLimit || 0) - (selectedCustomer.totalOwed || 0)).toFixed(2)}
                          </span>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              </div>

              {/* Invoice Details */}
              <div className="bg-white border border-gray-200 rounded-xl p-5">
                <h4 className="font-semibold text-gray-900 mb-4 flex items-center">
                  <Calendar size={16} className="mr-2 text-emerald-600" />
                  Invoice Details
                </h4>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Due Date *
                    </label>
                    <input
                      type="date"
                      value={form.dueDate}
                      onChange={e => {
                        setForm({ ...form, dueDate: e.target.value });
                        if (errors.dueDate) setErrors({ ...errors, dueDate: null });
                      }}
                      min={new Date().toISOString().split('T')[0]}
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent transition-colors ${
                        errors.dueDate ? 'border-red-300 bg-red-50' : 'border-gray-300'
                      }`}
                    />
                    {errors.dueDate && (
                      <p className="text-red-500 text-xs mt-1 flex items-center">
                        <AlertCircle size={12} className="mr-1" />
                        {errors.dueDate}
                      </p>
                    )}
                  </div>

                  {/* Invoice Preview */}
                  <div className="mt-4 p-4 bg-gray-50 border border-gray-200 rounded-lg">
                    <div className="grid grid-cols-2 gap-3 text-sm">
                      <div>
                        <span className="text-gray-600">Invoice Date:</span>
                        <div className="font-medium text-gray-900">{new Date().toLocaleDateString()}</div>
                      </div>
                      <div>
                        <span className="text-gray-600">Status:</span>
                        <div className="flex items-center">
                          <div className="w-2 h-2 bg-blue-500 rounded-full mr-2"></div>
                          <span className="font-medium text-blue-600">Outstanding</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            {/* Invoice Items */}
            <div className="bg-white border border-gray-200 rounded-xl p-5">
              <div className="flex items-center justify-between mb-4">
                <h4 className="font-semibold text-gray-900 flex items-center">
                  <DollarSign size={16} className="mr-2 text-emerald-600" />
                  Invoice Items
                </h4>
                <button
                  onClick={addItem}
                  className="flex items-center space-x-1 px-3 py-2 text-sm bg-emerald-100 text-emerald-700 rounded-lg hover:bg-emerald-200 transition-colors"
                >
                  <Plus size={14} />
                  <span>Add Item</span>
                </button>
              </div>

              {errors.items && (
                <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-lg">
                  <p className="text-red-800 text-sm flex items-center">
                    <AlertCircle size={14} className="mr-2" />
                    {errors.items}
                  </p>
                </div>
              )}

              {/* Items Header - Desktop Only */}
              <div className="hidden md:grid md:grid-cols-12 gap-3 mb-3 text-xs font-medium text-gray-500 uppercase tracking-wider border-b border-gray-200 pb-2">
                <div className="col-span-5">Description</div>
                <div className="col-span-2 text-center">Quantity</div>
                <div className="col-span-2 text-center">Unit Price</div>
                <div className="col-span-2 text-center">Total</div>
                <div className="col-span-1"></div>
              </div>

              {/* Items List */}
              <div className="space-y-3">
                {form.items.map((item, idx) => (
                  <div key={idx} className="border border-gray-200 rounded-lg p-4 hover:border-emerald-300 transition-colors">
                    {/* Mobile Layout */}
                    <div className="md:hidden space-y-3">
                      <div>
                        <label className="block text-xs font-medium text-gray-500 mb-1">Description</label>
                        <input
                          type="text"
                          value={item.description}
                          onChange={e => updateItem(idx, 'description', e.target.value)}
                          placeholder="Enter item description"
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
                        />
                      </div>
                      <div className="grid grid-cols-3 gap-3">
                        <div>
                          <label className="block text-xs font-medium text-gray-500 mb-1">Qty</label>
                          <input
                            type="number"
                            value={item.quantity}
                            onChange={e => updateItem(idx, 'quantity', parseFloat(e.target.value) || 0)}
                            min="0"
                            step="1"
                            className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
                          />
                        </div>
                        <div>
                          <label className="block text-xs font-medium text-gray-500 mb-1">Price</label>
                          <input
                            type="number"
                            value={item.price}
                            onChange={e => updateItem(idx, 'price', parseFloat(e.target.value) || 0)}
                            min="0"
                            step="0.01"
                            className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
                          />
                        </div>
                        <div>
                          <label className="block text-xs font-medium text-gray-500 mb-1">Total</label>
                          <input
                            type="text"
                            value={`$${item.total.toFixed(2)}`}
                            readOnly
                            className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm bg-gray-50 font-medium"
                          />
                        </div>
                      </div>
                      {form.items.length > 1 && (
                        <button
                          onClick={() => removeItem(idx)}
                          className="w-full mt-2 p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors text-sm flex items-center justify-center"
                        >
                          <XCircle size={14} className="mr-1" />
                          Remove Item
                        </button>
                      )}
                    </div>

                    {/* Desktop Layout */}
                    <div className="hidden md:grid md:grid-cols-12 gap-3 items-center">
                      <div className="col-span-5">
                        <input
                          type="text"
                          value={item.description}
                          onChange={e => updateItem(idx, 'description', e.target.value)}
                          placeholder="Enter item description"
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
                        />
                      </div>
                      <div className="col-span-2">
                        <input
                          type="number"
                          value={item.quantity}
                          onChange={e => updateItem(idx, 'quantity', parseFloat(e.target.value) || 0)}
                          min="0"
                          step="1"
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent text-center"
                        />
                      </div>
                      <div className="col-span-2">
                        <input
                          type="number"
                          value={item.price}
                          onChange={e => updateItem(idx, 'price', parseFloat(e.target.value) || 0)}
                          min="0"
                          step="0.01"
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent text-center"
                        />
                      </div>
                      <div className="col-span-2">
                        <input
                          type="text"
                          value={`$${item.total.toFixed(2)}`}
                          readOnly
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg text-sm bg-gray-50 font-medium text-center"
                        />
                      </div>
                      <div className="col-span-1 flex justify-center">
                        {form.items.length > 1 && (
                          <button
                            onClick={() => removeItem(idx)}
                            className="p-2 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                          >
                            <XCircle size={16} />
                          </button>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* Total Section */}
              <div className="mt-6 flex justify-end">
                <div className="w-full sm:w-64 space-y-2">
                  <div className="flex justify-between items-center py-2 border-t border-gray-200">
                    <span className="text-lg font-semibold text-gray-900">Total Amount:</span>
                    <span className="text-2xl font-bold text-emerald-600">${totalAmount.toFixed(2)}</span>
                  </div>
                  {selectedCustomer && totalAmount > 0 && (
                    <div className="text-xs text-gray-500 text-right">
                      Remaining Credit: ${((selectedCustomer.creditLimit || 0) - (selectedCustomer.totalOwed || 0) - totalAmount).toFixed(2)}
                    </div>
                  )}
                </div>
              </div>
            </div>

            {/* Notes */}
            <div className="bg-white border border-gray-200 rounded-xl p-5">
              <h4 className="font-semibold text-gray-900 mb-4 flex items-center">
                <Info size={16} className="mr-2 text-emerald-600" />
                Additional Notes
              </h4>
              <textarea
                value={form.notes}
                onChange={e => setForm({ ...form, notes: e.target.value })}
                rows={3}
                placeholder="Add any additional notes or special instructions..."
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent resize-none"
              />
            </div>

            {/* Action Buttons */}
            <div className="flex flex-col sm:flex-row gap-3 pt-4 border-t border-gray-200">
              <button
                onClick={handleSubmit}
                disabled={isSubmitting || totalAmount === 0}
                className="flex-1 flex items-center justify-center space-x-2 px-6 py-3 bg-emerald-600 text-white rounded-xl hover:bg-emerald-700 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors font-medium"
              >
                {isSubmitting ? (
                  <>
                    <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                    <span>Creating...</span>
                  </>
                ) : (
                  <>
                    <CheckCircle size={16} />
                    <span>Create Invoice - ${totalAmount.toFixed(2)}</span>
                  </>
                )}
              </button>
              <button
                onClick={onClose}
                disabled={isSubmitting}
                className="flex-1 sm:flex-initial flex items-center justify-center space-x-2 px-6 py-3 bg-gray-500 text-white rounded-xl hover:bg-gray-600 disabled:bg-gray-400 transition-colors font-medium"
              >
                <XCircle size={16} />
                <span>Cancel</span>
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateInvoiceModal;
