import React, { useState, useEffect } from 'react';
import { 
  Users, 
  DollarSign, 
  CreditCard, 
  AlertTriangle, 
  Clock, 
  Plus, 
  Search, 
  Filter, 
  Eye, 
  Edit, 
  Download,
  CheckCircle,
  XCircle,
  Calendar,
  FileText,
  TrendingDown,
  Mail,
  Phone,
  Loader,
  RefreshCw,
  Package
} from 'lucide-react';

// API Configuration for Accounts Payable
const API_PURCHASE_ORDERS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/PurchaseOrderManagement/purchaseOrdersWithoutDetails';
const API_VENDORS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/VendorManagement/vendors';
const API_PRODUCTS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/products?status=All&start=0&size=200';

// Replace with your actual Bearer token
const BEARER_TOKEN = 'your-bearer-token-here';

const AccountsPayable = () => {
  // State management
  const [purchaseOrders, setPurchaseOrders] = useState({ loading: false, data: [], error: null });
  const [vendors, setVendors] = useState({ loading: false, data: [], error: null });
  const [products, setProducts] = useState({ loading: false, data: [], error: null });
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedVendor, setSelectedVendor] = useState(null);
  const [selectedPO, setSelectedPO] = useState(null);
  const [viewBill, setViewBill] = useState(null);
  const [filters, setFilters] = useState({
    vendorType: 'all',
    status: 'all',
    searchTerm: ''
  });

  const createVendorPOMapping = (purchaseOrders, vendors) => {
  const vendorPOMap = new Map();
  
  // Initialize all vendors (including those without POs)
  vendors.data.forEach(vendor => {
    vendorPOMap.set(vendor.id, {
      vendor,
      purchaseOrders: []
    });
  });
  
  // Add a special entry for unknown/null vendors
  vendorPOMap.set('UNKNOWN', {
    vendor: { id: 'UNKNOWN', name: 'Unknown Vendor' },
    purchaseOrders: []
  });
  
  // Process purchase orders and assign them to appropriate vendors
  purchaseOrders.data.forEach(po => {
    const vendorKey = po.vendor_Id || 'UNKNOWN';
    
    if (vendorPOMap.has(vendorKey)) {
      vendorPOMap.get(vendorKey).purchaseOrders.push(po);
    } else {
      // Handle case where PO references a vendor that doesn't exist in vendors list
      vendorPOMap.set(vendorKey, {
        vendor: { id: vendorKey, name: 'Unknown Vendor' },
        purchaseOrders: [po]
      });
    }
  });
  
  return Array.from(vendorPOMap.values());
};


  // API Functions
  const fetchProducts = async () => {
    setProducts(prev => ({ ...prev, loading: true, error: null }));
    try {
      const response = await fetch(API_PRODUCTS, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${BEARER_TOKEN}`,
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      
      // Store products data for reference
      window.productsData = data;
      
      setProducts({ loading: false, data: data, error: null });
    } catch (error) {
      console.error('Error fetching products:', error);
      // Use sample product data if API fails
      const sampleProducts = [
        {
          id: "1",
          name: "IPHONE 15 PRO MAX",
          description: "6.1 INCH",
          costPrice: 180000,
          price: 210000,
          quantity: 49,
          status: "Active"
        },
        {
          id: "2",
          name: "LAPTOP",
          description: "14 INCH",
          costPrice: 45000,
          price: 50000,
          quantity: 55,
          status: "Active"
        },
        {
          id: "3",
          name: "AIR PODS",
          description: "BOOSTER",
          costPrice: 5000,
          price: 8000,
          quantity: 14,
          status: "Active"
        },
        {
          id: "4",
          name: "LED",
          description: "40 INCH",
          costPrice: 75000,
          price: 90000,
          quantity: 37,
          status: "Active"
        }
      ];
      window.productsData = sampleProducts;
      setProducts({ loading: false, data: sampleProducts, error: null });
    }
  };

  const fetchPurchaseOrders = async () => {
    setPurchaseOrders(prev => ({ ...prev, loading: true, error: null }));
    try {
      const response = await fetch(API_PURCHASE_ORDERS, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${BEARER_TOKEN}`,
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const allData = await response.json();
      
      // Store all data for vendor calculations
      window.allPurchaseOrdersData = allData;
      
      // Show only records that have pending status
      const filteredPOs = allData.filter(po => 
        po.status === 'pending'
      );
      
      setPurchaseOrders({ loading: false, data: filteredPOs, error: null });
    } catch (error) {
      console.error('Error fetching purchase orders:', error);
      setPurchaseOrders({ loading: false, data: [], error: error.message });
    }
  };

  const fetchVendors = async () => {
    setVendors(prev => ({ ...prev, loading: true, error: null }));
    try {
      const response = await fetch(API_VENDORS, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${BEARER_TOKEN}`,
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      
      // Use all purchase orders data for vendor calculations
      const allPOData = window.allPurchaseOrdersData || [];
      
      // Transform API vendors to match our structure with real calculated data
      const transformedVendors = data.map(vendor => {
        // Extract vendor details from complex API structure
        const vendorDetails = vendor.individuals && vendor.individuals.length > 0 
          ? vendor.individuals[0] 
          : vendor.organizations && vendor.organizations.length > 0 
            ? vendor.organizations[0] 
            : null;
        
        const contactInfo = vendorDetails?.contactMediums?.[0]?.mediumCharacteristics?.[0] || {};
        
        // Get ALL purchase orders for this vendor
        const vendorPOs = allPOData.filter(po => 
          po.vendor_Id === vendor.id || 
          po.Vendor_Id === vendor.id
        );
        
        // Calculate total payable from ALL pending purchase orders
        const totalPayable = vendorPOs
          .filter(po => po.status === 'pending')
          .reduce((sum, po) => {
            const poTotal = po.purchaseOrderItems.reduce((itemSum, item) => 
              itemSum + (item.totalPrice || 0), 0);
            return sum + poTotal;
          }, 0);
        
        // Overdue amount = total payable (since all pending POs need attention)
        const overdueAmount = totalPayable;
        
        // Total number of purchase orders for this vendor
        const billCount = vendorPOs.length;
        
        // Determine vendor type based on spending pattern
        let vendorType = 'supplies';
        const totalSpentFromPOs = vendorPOs.reduce((sum, po) => {
          const poTotal = po.purchaseOrderItems.reduce((itemSum, item) => 
            itemSum + (item.totalPrice || 0), 0);
          return sum + poTotal;
        }, 0);
        
        if (totalSpentFromPOs > 5000000) {
          vendorType = 'equipment';
        } else if (totalSpentFromPOs > 1000000) {
          vendorType = 'supplies';
        } else {
          vendorType = 'office';
        }
        
        // Set credit limit based on vendor type
        let creditLimit = 500000; // Default
        if (vendorType === 'equipment') {
          creditLimit = 2000000;
        } else if (vendorType === 'supplies') {
          creditLimit = 1000000;
        }
        
        // Determine credit status based on payable amount
        let creditStatus = 'good';
        if (totalPayable === 0) {
          creditStatus = 'excellent';
        } else if (totalPayable > creditLimit * 0.9) {
          creditStatus = 'hold';
        } else if (totalPayable > creditLimit * 0.7) {
          creditStatus = 'watch';
        }

        return {
          id: vendor.id,
          name: vendorDetails?.fullName || vendorDetails?.name || `Vendor ${vendor.id}`,
          email: contactInfo.emailAddress || 'No email provided',
          phone: contactInfo.number || 'No phone provided',
          address: `${contactInfo.street1 || ''} ${contactInfo.city || ''} ${contactInfo.country || ''}`.trim() || 'No address provided',
          vendorType: vendorType,
          creditLimit: creditLimit,
          paymentTerms: vendorType === 'equipment' ? 45 : (vendorType === 'supplies' ? 30 : 15),
          totalPayable: totalPayable,
          overdueAmount: overdueAmount,
          lastPayment: 'No payments',
          creditStatus: creditStatus,
          accountBalance: totalPayable,
          billCount: billCount,
          avgPaymentDays: 30,
          status: vendor.status || 'active',
          type: vendor.type || 'Individual'
        };
      });
      
      // Also add vendors with null vendor_Id as separate entries
      const nullVendorPOs = allPOData.filter(po => 
        !po.vendor_Id || po.vendor_Id === 'null' || 
        !po.Vendor_Id || po.Vendor_Id === 'null'
      );
      
      nullVendorPOs.forEach((po, index) => {
        const vendorNumber = index + 1;
        const poTotal = po.purchaseOrderItems.reduce((sum, item) => 
          sum + (item.totalPrice || 0), 0);
        const vendorTotalPayable = po.status === 'pending' ? poTotal : 0;
        
        transformedVendors.push({
          id: `unknown-vendor-${po.id}`,
          name: `Unknown Vendor #${vendorNumber}`,
          email: 'unknown@vendor.system',
          phone: '+000-000-0000',
          address: 'Unknown Address',
          vendorType: 'unknown',
          creditLimit: 0,
          paymentTerms: 0,
          totalPayable: vendorTotalPayable,
          overdueAmount: vendorTotalPayable,
          lastPayment: 'No payments',
          creditStatus: 'unknown',
          accountBalance: vendorTotalPayable,
          billCount: 1,
          avgPaymentDays: 0,
          status: 'Active',
          type: 'Unknown',
          poId: po.id
        });
      });
      
      setVendors({ loading: false, data: transformedVendors, error: null });
    } catch (error) {
      console.error('Error fetching vendors:', error);
      setVendors({ loading: false, data: [], error: error.message });
    }
  };

  // Helper function to get product details by ID
  const getProductDetails = (productId) => {
    if (!productId) return { name: 'Unknown Product', description: '', price: 0 };
    
    const product = (window.productsData || products.data || []).find(p => 
      p.id === productId || p.id === String(productId)
    );
    
    if (product) {
      return {
        name: product.name || 'Unknown Product',
        description: product.description || '',
        price: product.price || 0,
        costPrice: product.costPrice || 0,
        quantity: product.quantity || 0
      };
    }
    
    return { name: `Product #${productId}`, description: '', price: 0 };
  };

  // Refresh data function
  const refreshAllData = async () => {
    await fetchProducts();
    await fetchPurchaseOrders();
    setTimeout(() => fetchVendors(), 500);
  };

  // Load data on component mount
  useEffect(() => {
    const loadData = async () => {
      await fetchProducts();
      await fetchPurchaseOrders();
      setTimeout(() => fetchVendors(), 500);
    };
    loadData();
  }, []);

  // Helper function to get vendor name by ID
  const getVendorName = (vendorId) => {
    if (!vendorId || vendorId === 'null') return 'Unknown Vendor';
    const vendor = vendors.data.find(v => v.id === vendorId);
    return vendor ? vendor.name : 'Unknown Vendor';
  };

  function combineVendorsWithPurchaseOrders(vendors, purchaseOrders) {
  // Create a map for quick vendor lookup
  const vendorMap = new Map();
  
  // First, add all vendors to the map
  vendors.data.forEach(vendor => {
    vendorMap.set(vendor.id, {
      ...vendor,
      purchaseOrders: []
    });
  });
  
  // Then, process all purchase orders
  purchaseOrders.forEach(po => {
    if (po.vendor_Id !== null && po.vendor_Id !== undefined) {
      // If vendor exists, add the PO to their list
      if (vendorMap.has(po.vendor_Id)) {
        vendorMap.get(po.vendor_Id).purchaseOrders.push(po);
      } else {
        // If vendor doesn't exist, create a new vendor entry
        vendorMap.set(po.vendor_Id, {
          id: po.vendor_Id,
          name: "Unknown Vendor",
          purchaseOrders: [po]
        });
      }
    } else {
      // Handle POs with no vendor_id by creating a special vendor entry
      const unknownVendorId = `unknown-${po.id}`;
      if (!vendorMap.has(unknownVendorId)) {
        vendorMap.set(unknownVendorId, {
          id: unknownVendorId,
          name: "Unknown Vendor",
          purchaseOrders: []
        });
      }
      vendorMap.get(unknownVendorId).purchaseOrders.push(po);
    }
  });
  
  // Convert the map back to an array
  return Array.from(vendorMap.values());
}

// Example usage:
const vendorsWithPOs = combineVendorsWithPurchaseOrders(vendors, purchaseOrders.data);

  // Calculate AP summary
  const calculateAPSummary = () => {
    const totalAP = vendors.data.reduce((sum, vendor) => sum + vendor.totalPayable, 0);
    const totalOverdue = vendors.data.reduce((sum, vendor) => sum + vendor.overdueAmount, 0);
    const vendorsWithBalance = vendors.data.filter(vendor => vendor.totalPayable > 0).length;
    

    // Calculate real average days outstanding
    const allPOData = window.allPurchaseOrdersData || [];
    let totalDaysOutstanding = 0;
    let poCount = 0;
    
    allPOData.forEach(po => {
      // Default days for pending orders (since we don't have creation dates in API)
      const defaultDays = 15;
      totalDaysOutstanding += defaultDays;
      poCount++;
    });
    
    const avgDaysOutstanding = poCount > 0 ? Math.round(totalDaysOutstanding / poCount) : 0;

    return {
      totalAP,
      totalOverdue,
      vendorsWithBalance,
      avgDaysOutstanding
    };
  };

  // Filter vendors
  const getFilteredVendors = () => {
    let filtered = [...vendors.data];

    if (filters.vendorType !== 'all') {
      filtered = filtered.filter(vendor => vendor.vendorType === filters.vendorType);
    }

    if (filters.status !== 'all') {
      filtered = filtered.filter(vendor => {
        if (filters.status === 'overdue') return vendor.overdueAmount > 0;
        if (filters.status === 'current') return vendor.totalPayable > 0 && vendor.overdueAmount === 0;
        if (filters.status === 'paid_up') return vendor.totalPayable === 0;
        return true;
      });
    }

    if (filters.searchTerm) {
      const term = filters.searchTerm.toLowerCase();
      filtered = filtered.filter(vendor =>
        vendor.name.toLowerCase().includes(term) ||
        vendor.email.toLowerCase().includes(term) ||
        vendor.id.toLowerCase().includes(term)
      );
    }

    return filtered;
  };

  const getCreditStatusColor = (status) => {
    switch (status) {
      case 'excellent': return 'bg-green-100 text-green-800';
      case 'good': return 'bg-blue-100 text-blue-800';
      case 'watch': return 'bg-yellow-100 text-yellow-800';
      case 'hold': return 'bg-red-100 text-red-800';
      case 'unknown': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getPOStatusColor = (status) => {
    switch (status) {
      case 'applied': return 'bg-green-100 text-green-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-PK', {
      style: 'currency',
      currency: 'PKR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(amount);
  };

  const summary = calculateAPSummary();
  const filteredVendors = getFilteredVendors();

  // Loading state
  if (purchaseOrders.loading || vendors.loading || products.loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="flex items-center space-x-3">
          <Loader className="animate-spin text-rose-600" size={24} />
          <span className="text-gray-600">Loading data from API...</span>
        </div>
      </div>
    );
  }

  // Error state
  if (purchaseOrders.error || vendors.error) {
    return (
      <div className="max-w-7xl mx-auto p-6">
        <div className="bg-red-50 border border-red-200 rounded-lg p-6">
          <div className="flex items-center mb-4">
            <AlertTriangle className="text-red-600 mr-2" size={20} />
            <h3 className="font-medium text-red-800">API Connection Error</h3>
          </div>
          <div className="text-red-700 mb-4">
            {purchaseOrders.error && <div>Purchase Orders API Error: {purchaseOrders.error}</div>}
            {vendors.error && <div>Vendors API Error: {vendors.error}</div>}
            {products.error && <div>Products API Error: {products.error}</div>}
          </div>
          <button
            onClick={refreshAllData}
            className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 flex items-center space-x-2"
          >
            <RefreshCw size={16} />
            <span>Retry Connection</span>
          </button>
        </div>
      </div>
    );
  }

  
// Usage in component
const vendorPOData = createVendorPOMapping(purchaseOrders, vendors);


  return (
    <div className="max-w-7xl mx-auto p-6 bg-white">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2 flex items-center">
          <CreditCard className="mr-3 text-rose-600" />
          Accounts Payable
        </h1>
        <p className="text-gray-600">Manage vendor purchase orders, bills, and payments</p>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="bg-blue-50 p-6 rounded-lg border border-blue-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-blue-600 text-sm font-medium">Total A/P</p>
              <p className="text-2xl font-bold text-blue-900">{formatCurrency(summary.totalAP)}</p>
            </div>
            <DollarSign className="text-blue-600" size={24} />
          </div>
          <p className="text-sm text-blue-700 mt-2">{summary.vendorsWithBalance} vendors with pending orders</p>
        </div>

        <div className="bg-red-50 p-6 rounded-lg border border-red-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-red-600 text-sm font-medium">Pending Amount</p>
              <p className="text-2xl font-bold text-red-900">{formatCurrency(summary.totalOverdue)}</p>
            </div>
            <AlertTriangle className="text-red-600" size={24} />
          </div>
          <p className="text-sm text-red-700 mt-2">
            {summary.totalAP > 0 ? ((summary.totalOverdue / summary.totalAP) * 100).toFixed(1) : 0}% of total A/P
          </p>
        </div>

        <div className="bg-yellow-50 p-6 rounded-lg border border-yellow-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-yellow-600 text-sm font-medium">Avg. Days Outstanding</p>
              <p className="text-2xl font-bold text-yellow-900">{summary.avgDaysOutstanding}</p>
            </div>
            <Clock className="text-yellow-600" size={24} />
          </div>
          <p className="text-sm text-yellow-700 mt-2">Payment performance</p>
        </div>

        <div className="bg-purple-50 p-6 rounded-lg border border-purple-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-purple-600 text-sm font-medium">Active Vendors</p>
              <p className="text-2xl font-bold text-purple-900">{vendors.data.length}</p>
            </div>
            <Users className="text-purple-600" size={24} />
          </div>
          <p className="text-sm text-purple-700 mt-2">Total vendor accounts</p>
        </div>
      </div>

      {/* Tabs */}
      <div className="mb-6">
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            {[
              { id: 'overview', name: 'Vendor Overview', icon: Users },
              { id: 'purchase-orders', name: 'Purchase Orders', icon: FileText },
              { id: 'payments', name: 'Payment History', icon: CheckCircle },
              { id: 'products', name: 'Product Catalog', icon: Package }
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center py-2 px-1 border-b-2 font-medium text-sm ${
                  activeTab === tab.id
                    ? 'border-rose-500 text-rose-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <tab.icon className="mr-2" size={16} />
                {tab.name}
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Tab Content */}
      {activeTab === 'overview' && (
        <div>
          {/* Filters */}
          <div className="flex flex-wrap items-center gap-4 mb-6">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
              <input
                type="text"
                placeholder="Search vendors..."
                value={filters.searchTerm}
                onChange={(e) => setFilters({ ...filters, searchTerm: e.target.value })}
                className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-rose-500 focus:border-transparent"
              />
            </div>

            <select
              value={filters.vendorType}
              onChange={(e) => setFilters({ ...filters, vendorType: e.target.value })}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-rose-500 focus:border-transparent"
            >
              <option value="all">All Vendor Types</option>
              <option value="equipment">Equipment</option>
              <option value="supplies">Supplies</option>
              <option value="office">Office</option>
              <option value="unknown">Unknown</option>
            </select>

            <select
              value={filters.status}
              onChange={(e) => setFilters({ ...filters, status: e.target.value })}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-rose-500 focus:border-transparent"
            >
              <option value="all">All Statuses</option>
              <option value="current">Current</option>
              <option value="overdue">Pending</option>
              <option value="paid_up">Completed</option>
            </select>

            <button
              onClick={refreshAllData}
              className="px-4 py-2 bg-rose-600 text-white rounded-lg hover:bg-rose-700 flex items-center space-x-2"
            >
              <RefreshCw size={16} />
              <span>Refresh Data</span>
            </button>
          </div>

          {/* Vendor List */}
          <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Vendor
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Products
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Total Amount
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Remarks
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
  <tbody className="bg-white divide-y divide-gray-200">
          {vendors.map((vendor) => {
            const hasPos = vendor.purchaseOrders && vendor.purchaseOrders.length > 0;
            const vendorTotal = hasPos 
              ? vendor.purchaseOrders.reduce((sum, po) => {
                  const poTotal = po.purchaseOrderItems 
                    ? po.purchaseOrderItems.reduce((itemSum, item) => itemSum + (item.totalPrice || 0), 0)
                    : 0;
                  return sum + poTotal;
                }, 0)
              : 0;
            
            // Collect all products from all POs for this vendor
            const allProducts = [];
            if (hasPos) {
              vendor.purchaseOrders.forEach(po => {
                if (po.purchaseOrderItems) {
                  po.purchaseOrderItems.forEach(item => {
                    const product = getProductDetails(item.product_Id);
                    allProducts.push({
                      poId: po.POID,
                      name: product?.name || "Unknown Product",
                      quantity: item.quantity,
                      description: product?.description
                    });
                  });
                }
              });
            }
            
            // Get all statuses from vendor's POs
            const statuses = hasPos 
              ? vendor.purchaseOrders.map(po => ({
                  status: po.status,
                  statusColor: getPOStatusColor(po.status)
                }))
              : [];
            
            return (
              <tr key={vendor.id} className="hover:bg-gray-50">
                {/* Vendor */}
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-medium text-gray-900">
                    {vendor.name || "Unknown Vendor"}
                  </div>
                  <div className="text-xs text-gray-500">
                    {hasPos ? vendor.purchaseOrders.map(po => `PO-${po.POID}`).join(', ') : "No POs"}
                  </div>
                </td>
                
                {/* Products - All in one cell */}
                <td className="px-6 py-4">
                  {hasPos ? (
                    <div className="text-sm text-gray-900">
                      {allProducts.map((product, index) => (
                        <div key={index} className="mb-1">
                          <span className="font-medium">{product.name}</span>
                          <span className="text-gray-500 ml-2">x{product.quantity}</span>
                          {product.description && (
                            <span className="text-xs text-gray-400 ml-2">
                              ({product.description})
                            </span>
                          )}
                          <span className="text-xs text-gray-500 ml-2">
                            [PO-{product.poId}]
                          </span>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-sm text-gray-500">No purchase orders</div>
                  )}
                </td>
                
                {/* Total Amount */}
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                  {formatCurrency(vendorTotal)}
                </td>
                
                {/* Status - Show all statuses */}
                <td className="px-6 py-4 whitespace-nowrap">
                  {!hasPos ? (
                    <span className="px-2 py-1 text-xs font-medium rounded-full bg-gray-100 text-gray-800">
                      No Orders
                    </span>
                  ) : (
                    <div>
                      {statuses.map((status, index) => (
                        <div key={index} className="mb-1">
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${status.statusColor}`}>
                            {status.status}
                          </span>
                        </div>
                      ))}
                    </div>
                  )}
                </td>
                
                {/* Remarks - Combined */}
                <td className="px-6 py-4 text-sm text-gray-900">
                  {hasPos ? (
                    <div>
                      {vendor.purchaseOrders.map((po, index) => (
                        <div key={index} className="mb-1">
                          {po.remarks ? (
                            <span>PO-{po.POID}: {po.remarks}</span>
                          ) : (
                            <span className="text-gray-500">PO-{po.POID}: No remarks</span>
                          )}
                        </div>
                      ))}
                    </div>
                  ) : (
                    <span className="text-gray-500">-</span>
                  )}
                </td>
                
                {/* Actions */}
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                  {!hasPos ? (
                    <button
                      className="text-gray-400 cursor-not-allowed"
                      title="No actions available"
                      disabled
                    >
                      <Eye size={16} />
                    </button>
                  ) : (
                    <div className="flex flex-wrap gap-2">
                      {vendor.purchaseOrders.map((po, index) => (
                        <div key={index} className="flex items-center">
                          <span className="text-xs text-gray-500 mr-1">PO-{po.POID}:</span>
                          <button
                            onClick={() => {
                              // Implement your view details function here
                              // setSelectedPO(po);
                            }}
                            className="text-rose-600 hover:text-rose-900 mr-1"
                            title={`View PO-${po.POID} Details`}
                          >
                            <Eye size={16} />
                          </button>
                          <button
                            className="text-blue-600 hover:text-blue-900"
                            title={`Edit PO-${po.POID}`}
                          >
                            <Edit size={16} />
                          </button>
                        </div>
                      ))}
                    </div>
                  )}
                </td>
              </tr>
            );
          })}
          
          {/* Show POs with no vendor as a separate section if needed */}
          {/* You can add a similar implementation for orphaned POs here */}
        </tbody>

              </table>
            </div>

            {purchaseOrders.data.length === 0 && (
              <div className="text-center py-12">
                <div className="text-gray-500 text-lg">No pending purchase orders found</div>
                <p className="text-gray-400 mt-2">All purchase orders are either applied or cancelled</p>
              </div>
            )}
          </div>
        </div>
      )}

      {activeTab === 'payments' && (
        <div>
          <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
            <div className="px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-semibold text-gray-900">Payment History</h3>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      PO ID
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Vendor
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Products
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Amount
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Payment Status
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {(window.allPurchaseOrdersData || []).filter(po => po.status === 'applied').map((po) => {
                    const poTotal = po.purchaseOrderItems.reduce((sum, item) => sum + (item.totalPrice || 0), 0);
                    return (
                      <tr key={po.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          #{po.id}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {getVendorName(po.vendor_Id || po.Vendor_Id)}
                        </td>
                        <td className="px-6 py-4 text-sm text-gray-900">
                          {po.purchaseOrderItems.map((item, index) => {
                            const product = getProductDetails(item.product_Id);
                            return (
                              <span key={index} className="block">
                                {product.name} x{item.quantity}
                              </span>
                            );
                          })}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {formatCurrency(poTotal)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className="px-2 py-1 text-xs font-medium rounded-full bg-green-100 text-green-800">
                            Applied
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className="px-2 py-1 text-xs font-medium rounded-full bg-blue-100 text-blue-800">
                            Processed
                          </span>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
            
            {(!window.allPurchaseOrdersData?.filter(po => po.status === 'applied').length || window.allPurchaseOrdersData?.filter(po => po.status === 'applied').length === 0) && (
              <div className="text-center py-12">
                <div className="text-gray-500 text-lg">No payment history found</div>
                <p className="text-gray-400 mt-2">No purchase orders have been processed yet</p>
              </div>
            )}
          </div>
        </div>
      )}

      {activeTab === 'products' && (
        <div>
          <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
            <div className="px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-semibold text-gray-900">Product Catalog ({products.data.length})</h3>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Product ID
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Name
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Description
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Cost Price
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Selling Price
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Stock
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Margin
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {products.data.map((product) => {
                    const margin = product.price && product.costPrice 
                      ? ((product.price - product.costPrice) / product.costPrice * 100).toFixed(1)
                      : 0;
                    
                    return (
                      <tr key={product.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          #{product.id}
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm font-medium text-gray-900">{product.name}</div>
                          {product.POID && (
                            <div className="text-xs text-gray-500">POID: {product.POID}</div>
                          )}
                        </td>
                        <td className="px-6 py-4 text-sm text-gray-900">
                          {product.description?.replace(/<[^>]*>/g, '') || 'No description'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {formatCurrency(product.costPrice || 0)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {formatCurrency(product.price || 0)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                            product.quantity > 20 
                              ? 'bg-green-100 text-green-800' 
                              : product.quantity > 10 
                                ? 'bg-yellow-100 text-yellow-800' 
                                : 'bg-red-100 text-red-800'
                          }`}>
                            {product.quantity || 0} units
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                            product.status === 'Active' 
                              ? 'bg-green-100 text-green-800' 
                              : 'bg-gray-100 text-gray-800'
                          }`}>
                            {product.status}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`font-medium ${
                            margin > 30 ? 'text-green-600' : margin > 15 ? 'text-yellow-600' : 'text-red-600'
                          }`}>
                            {margin}%
                          </span>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>

            {products.data.length === 0 && (
              <div className="text-center py-12">
                <div className="text-gray-500 text-lg">No products found</div>
                <p className="text-gray-400 mt-2">Product catalog is empty</p>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Vendor Detail Modal */}
      {selectedVendor && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="px-6 py-4 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">
                  Vendor Details - {selectedVendor.name}
                </h3>
                <button
                  onClick={() => setSelectedVendor(null)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <XCircle size={20} />
                </button>
              </div>
            </div>
            
            <div className="px-6 py-4">
              {/* Vendor Info Grid */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Vendor ID</label>
                    <p className="text-sm text-gray-900">{selectedVendor.id}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Email</label>
                    <p className="text-sm text-gray-900">{selectedVendor.email}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Phone</label>
                    <p className="text-sm text-gray-900">{selectedVendor.phone}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Address</label>
                    <p className="text-sm text-gray-900">{selectedVendor.address}</p>
                  </div>
                </div>
                
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Vendor Type</label>
                    <p className="text-sm text-gray-900">{selectedVendor.vendorType.replace('_', ' ')}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Credit Limit</label>
                    <p className="text-sm text-gray-900">{formatCurrency(selectedVendor.creditLimit)}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Payment Terms</label>
                    <p className="text-sm text-gray-900">{selectedVendor.paymentTerms} days</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Credit Status</label>
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getCreditStatusColor(selectedVendor.creditStatus)}`}>
                      {selectedVendor.creditStatus.replace('_', ' ')}
                    </span>
                  </div>
                </div>
              </div>

              {/* Financial Summary */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-blue-600">Pending Amount</div>
                  <div className="text-xl font-bold text-blue-900">
                    {formatCurrency(selectedVendor.totalPayable)}
                  </div>
                </div>
                <div className="bg-red-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-red-600">Overdue Amount</div>
                  <div className="text-xl font-bold text-red-900">
                    {formatCurrency(selectedVendor.overdueAmount)}
                  </div>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-green-600">Credit Available</div>
                  <div className="text-xl font-bold text-green-900">
                    {formatCurrency(selectedVendor.creditLimit - selectedVendor.totalPayable)}
                  </div>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-purple-600">Avg Payment Days</div>
                  <div className="text-xl font-bold text-purple-900">
                    {selectedVendor.avgPaymentDays}
                  </div>
                </div>
              </div>

              {/* Vendor Purchase Orders */}
              <div className="mb-6">
                <h4 className="text-md font-semibold text-gray-900 mb-3">Vendor Purchase Orders</h4>
                <div className="overflow-x-auto">
                  <table className="w-full border border-gray-200 rounded">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">PO</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Products</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Amount</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Status</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Remarks</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200">
                      {(window.allPurchaseOrdersData || [])
                        .filter(po => {
                          if (selectedVendor.id.startsWith('unknown-vendor-')) {
                            return po.id === selectedVendor.poId;
                          }
                          return (po.vendor_Id === selectedVendor.id || po.Vendor_Id === selectedVendor.id);
                        })
                        .map((po) => {
                          const poTotal = po.purchaseOrderItems.reduce((sum, item) => sum + (item.totalPrice || 0), 0);
                          return (
                            <tr key={po.id}>
                              <td className="px-4 py-2 text-sm text-gray-900">#{po.id}</td>
                              <td className="px-4 py-2 text-sm text-gray-900">
                                {po.purchaseOrderItems.map((item, index) => {
                                  const product = getProductDetails(item.product_Id);
                                  return (
                                    <span key={index} className="block">
                                      {product.name} x{item.quantity}
                                    </span>
                                  );
                                })}
                              </td>
                              <td className="px-4 py-2 text-sm text-gray-900 text-right">{formatCurrency(poTotal)}</td>
                              <td className="px-4 py-2">
                                <span className={`px-2 py-1 text-xs font-medium rounded-full ${getPOStatusColor(po.status)}`}>
                                  {po.status}
                                </span>
                              </td>
                              <td className="px-4 py-2 text-sm text-gray-900">{po.remarks || 'No remarks'}</td>
                            </tr>
                          );
                        })}
                    </tbody>
                  </table>
                </div>
                
                {(window.allPurchaseOrdersData || [])
                  .filter(po => {
                    if (selectedVendor.id.startsWith('unknown-vendor-')) {
                      return po.id === selectedVendor.poId;
                    }
                    return (po.vendor_Id === selectedVendor.id || po.Vendor_Id === selectedVendor.id);
                  }).length === 0 && (
                    <div className="text-center py-4 text-gray-500">
                      No purchase orders found for this vendor
                    </div>
                  )}
              </div>

              {/* Action Buttons */}
              <div className="flex space-x-3">
                <button className="px-4 py-2 bg-rose-600 text-white rounded-lg hover:bg-rose-700 flex items-center space-x-2">
                  <Plus size={16} />
                  <span>New Purchase Order</span>
                </button>
                <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center space-x-2">
                  <CheckCircle size={16} />
                  <span>Record Payment</span>
                </button>
                <button className="px-4 py-2 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700 flex items-center space-x-2">
                  <Mail size={16} />
                  <span>Send Statement</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Purchase Order Detail Modal */}
{selectedPO && (
  <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
    <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
      
      {/* Header */}
      <div className="px-6 py-4 border-b border-gray-200">
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold text-gray-900">
            Purchase Order Details - #{selectedPO.id}
          </h3>
          <button
            onClick={() => setSelectedPO(null)}
            className="text-gray-400 hover:text-gray-600"
          >
            <XCircle size={20} />
          </button>
        </div>
      </div>
      
      {/* Body */}
      <div className="px-6 py-4">
        
        {/* PO Info */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          <div className="space-y-4">
            <div>
              <label className="text-sm font-medium text-gray-500">Purchase Order ID</label>
              <p className="text-sm text-gray-900">{selectedPO.id}</p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-500">PO ID</label>
              <p className="text-sm text-gray-900">PO-{selectedPO.POID}</p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-500">Vendor</label>
              <p className="text-sm text-gray-900">
                {getVendorName(selectedPO.vendor_Id || selectedPO.Vendor_Id)}
              </p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-500">Status</label>
              <span className={`px-2 py-1 text-xs font-medium rounded-full ${getPOStatusColor(selectedPO.status)}`}>
                {selectedPO.status}
              </span>
            </div>
          </div>
          
          <div className="space-y-4">
            <div>
              <label className="text-sm font-medium text-gray-500">Type</label>
              <p className="text-sm text-gray-900">{selectedPO.type || 'Standard PO'}</p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-500">Total Amount</label>
              <p className="text-lg font-bold text-gray-900">
                {formatCurrency(
                  selectedPO.purchaseOrderItems.reduce(
                    (sum, item) => sum + (item.totalPrice || 0), 
                    0
                  )
                )}
              </p>
            </div>
            <div>
              <label className="text-sm font-medium text-gray-500">Remarks</label>
              <p className="text-sm text-gray-900">{selectedPO.remarks || 'No remarks'}</p>
            </div>
          </div>
        </div>

        {/* Purchase Order Items */}
        <div className="mb-6">
          <h4 className="text-md font-semibold text-gray-900 mb-3">Purchase Order Items</h4>
          <div className="overflow-x-auto">
            <table className="w-full border border-gray-200 rounded">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Product</th>
                  <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Quantity</th>
                  <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Unit Price</th>
                  <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Total Price</th>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Status</th>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Remarks</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {selectedPO.purchaseOrderItems.map((item, index) => {
                  const product = getProductDetails(item.product_Id);
                  return (
                    <tr key={index}>
                      <td className="px-4 py-2 text-sm text-gray-900">
                        <div>
                          <div className="font-medium">{product.name}</div>
                          {product.description && (
                            <div className="text-xs text-gray-500">{product.description}</div>
                          )}
                        </div>
                      </td>
                      <td className="px-4 py-2 text-sm text-gray-900 text-right">{item.quantity}</td>
                      <td className="px-4 py-2 text-sm text-gray-900 text-right">{formatCurrency(item.pricePerUnit)}</td>
                      <td className="px-4 py-2 text-sm font-medium text-gray-900 text-right">{formatCurrency(item.totalPrice)}</td>
                      <td className="px-4 py-2">
                        <span className={`px-2 py-1 text-xs font-medium rounded-full ${getPOStatusColor(item.status)}`}>
                          {item.status}
                        </span>
                      </td>
                      <td className="px-4 py-2 text-sm text-gray-900">{item.remarks || 'No remarks'}</td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>

      </div>
    </div>
  </div>
)}


    {activeTab === 'purchase-orders' && (
        <div>
          <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
            <div className="px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-semibold text-gray-900">Pending Purchase Orders ({purchaseOrders.data.length})</h3>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      PO #
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Vendor
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Products
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Total Amount
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Remarks
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {purchaseOrders.data.map((po) => {
                    const poTotal = po.purchaseOrderItems.reduce((sum, item) => sum + (item.totalPrice || 0), 0);
                    return (
                      <tr key={po.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">#{po.id}</div>
                          <div className="text-xs text-gray-500">PO-{po.POID}</div>
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm font-medium text-gray-900">
                            {getVendorName(po.vendor_Id || po.Vendor_Id)}
                          </div>
                        </td>
                        <td className="px-6 py-4">
                          <div className="text-sm text-gray-900">
                            {po.purchaseOrderItems.map((item, index) => {
                              const product = getProductDetails(item.product_Id);
                              return (
                                <div key={index} className="mb-1">
                                  <span className="font-medium">{product.name}</span>
                                  <span className="text-gray-500 ml-2">x{item.quantity}</span>
                                  {product.description && (
                                    <span className="text-xs text-gray-400 ml-2">({product.description})</span>
                                  )}
                                </div>
                              );
                            })}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {formatCurrency(poTotal)}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${getPOStatusColor(po.status)}`}>
                            {po.status}
                          </span>
                        </td>
                        <td className="px-6 py-4 text-sm text-gray-900">
                          {po.remarks || 'No remarks'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                          <button
                            onClick={() => setSelectedPO(po)}
                            className="text-rose-600 hover:text-rose-900 mr-3"
                            title="View Details"
                          >
                            <Eye size={16} />
                          </button>
                          <button 
                            className="text-blue-600 hover:text-blue-900"
                            title="Edit PO"
                          >
                            <Edit size={16} />
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>

            {purchaseOrders.data.length === 0 && (
              <div className="text-center py-12">
                <div className="text-gray-500 text-lg">No pending purchase orders found</div>
                <p className="text-gray-400 mt-2">All purchase orders are either applied or cancelled</p>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Complete the PO Detail Modal - closing the table body and completing the modal */}
      {selectedPO && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="px-6 py-4 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">
                  Purchase Order Details - #{selectedPO.id}
                </h3>
                <button
                  onClick={() => setSelectedPO(null)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <XCircle size={20} />
                </button>
              </div>
            </div>
            
            <div className="px-6 py-4">
              {/* PO Info */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Purchase Order ID</label>
                    <p className="text-sm text-gray-900">{selectedPO.id}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">PO ID</label>
                    <p className="text-sm text-gray-900">PO-{selectedPO.POID}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Vendor</label>
                    <p className="text-sm text-gray-900">{getVendorName(selectedPO.vendor_Id || selectedPO.Vendor_Id)}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Status</label>
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getPOStatusColor(selectedPO.status)}`}>
                      {selectedPO.status}
                    </span>
                  </div>
                </div>
                
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Type</label>
                    <p className="text-sm text-gray-900">{selectedPO.type || 'Standard PO'}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Total Amount</label>
                    <p className="text-lg font-bold text-gray-900">
                      {formatCurrency(selectedPO.purchaseOrderItems.reduce((sum, item) => sum + (item.totalPrice || 0), 0))}
                    </p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Remarks</label>
                    <p className="text-sm text-gray-900">{selectedPO.remarks || 'No remarks'}</p>
                  </div>
                </div>
              </div>

              {/* Purchase Order Items */}
              <div className="mb-6">
                <h4 className="text-md font-semibold text-gray-900 mb-3">Purchase Order Items</h4>
                <div className="overflow-x-auto">
                  <table className="w-full border border-gray-200 rounded">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Product</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Quantity</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Unit Price</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Total Price</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Status</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Remarks</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200">
                      {selectedPO.purchaseOrderItems.map((item, index) => {
                        const product = getProductDetails(item.product_Id);
                        return (
                          <tr key={index}>
                            <td className="px-4 py-2 text-sm text-gray-900">
                              <div>
                                <div className="font-medium">{product.name}</div>
                                {product.description && (
                                  <div className="text-xs text-gray-500">{product.description}</div>
                                )}
                              </div>
                            </td>
                            <td className="px-4 py-2 text-sm text-gray-900 text-right">{item.quantity}</td>
                            <td className="px-4 py-2 text-sm text-gray-900 text-right">{formatCurrency(item.pricePerUnit)}</td>
                            <td className="px-4 py-2 text-sm font-medium text-gray-900 text-right">{formatCurrency(item.totalPrice)}</td>
                            <td className="px-4 py-2">
                              <span className={`px-2 py-1 text-xs font-medium rounded-full ${getPOStatusColor(item.status)}`}>
                                {item.status}
                              </span>
                            </td>
                            <td className="px-4 py-2 text-sm text-gray-900">{item.remarks || 'No remarks'}</td>
                          </tr>
                        );
                      })}
                    </tbody>
                    <tfoot className="bg-gray-50">
                      <tr>
                        <td colSpan="3" className="px-4 py-2 text-sm font-medium text-gray-900 text-right">
                          Total:
                        </td>
                        <td className="px-4 py-2 text-sm font-bold text-gray-900 text-right">
                          {formatCurrency(selectedPO.purchaseOrderItems.reduce((sum, item) => sum + (item.totalPrice || 0), 0))}
                        </td>
                        <td colSpan="2"></td>
                      </tr>
                    </tfoot>
                  </table>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex space-x-3">
                <button className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 flex items-center space-x-2">
                  <CheckCircle size={16} />
                  <span>Process Payment</span>
                </button>
                <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center space-x-2">
                  <Edit size={16} />
                  <span>Edit PO</span>
                </button>
                <button className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 flex items-center space-x-2">
                  <Download size={16} />
                  <span>Export PDF</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AccountsPayable;
