import React, { useState } from 'react';
import { XCircle, CheckCircle, Plus } from 'lucide-react';

const CreateInvoiceModal = ({ customers, onClose, onCreate }) => {
  const [form, setForm] = useState({
    customerId: '',
    dueDate: '',
    items: [{ description: '', quantity: 1, price: 0, total: 0 }],
    notes: ''
  });

  const [errors, setErrors] = useState({});

  const addItem = () => {
    setForm({ 
      ...form, 
      items: [...form.items, { description: '', quantity: 1, price: 0, total: 0 }] 
    });
  };

  const removeItem = (index) => {
    if (form.items.length > 1) {
      const updatedItems = form.items.filter((_, i) => i !== index);
      setForm({ ...form, items: updatedItems });
    }
  };

  const updateItem = (index, field, value) => {
    const updatedItems = [...form.items];
    updatedItems[index] = { ...updatedItems[index], [field]: value };
    
    // Auto-calculate total when quantity or price changes
    if (field === 'quantity' || field === 'price') {
      const quantity = field === 'quantity' ? parseFloat(value) || 0 : updatedItems[index].quantity;
      const price = field === 'price' ? parseFloat(value) || 0 : updatedItems[index].price;
      updatedItems[index].total = quantity * price;
    }
    
    setForm({ ...form, items: updatedItems });
  };

  const calculateTotal = () => {
    return form.items.reduce((sum, item) => sum + (item.total || 0), 0);
  };

  const validateForm = () => {
    const newErrors = {};

    if (!form.customerId) {
      newErrors.customerId = 'Customer is required';
    }

    if (!form.dueDate) {
      newErrors.dueDate = 'Due date is required';
    }

    // Validate that at least one item has description and amount
    const validItems = form.items.filter(item => 
      item.description && item.quantity > 0 && item.price > 0
    );

    if (validItems.length === 0) {
      newErrors.items = 'At least one valid item is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (!validateForm()) {
      return;
    }

    const customer = customers.find(c => c.id === form.customerId);
    const total = calculateTotal();

    // Check credit limit
    if (customer && customer.creditLimit > 0) {
      const availableCredit = customer.creditLimit - customer.totalOwed;
      if (total > availableCredit) {
        alert(`Invoice amount exceeds available credit limit. Available: $${availableCredit.toFixed(2)}`);
        return;
      }
    }

    const invoice = {
      ...form,
      amount: total,
      paid: 0,
      balance: total,
      status: 'outstanding',
      date: new Date().toISOString().split('T')[0],
      customerName: customer?.name || '',
      items: form.items.filter(item => item.description && item.quantity > 0),
      source: 'manual',
      daysOverdue: 0
    };

    onCreate(invoice);
    onClose();
  };

  const selectedCustomer = customers.find(c => c.id === form.customerId);

  return (
    <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        <div className="px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h3 className="text-lg font-semibold text-gray-900">Create New Invoice</h3>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600"
            >
              <XCircle size={20} />
            </button>
          </div>
        </div>
        
        <div className="px-6 py-4">
          {/* Invoice Header */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Customer *
              </label>
              <select
                value={form.customerId}
                onChange={(e) => setForm({ ...form, customerId: e.target.value })}
                className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent ${
                  errors.customerId ? 'border-red-300' : 'border-gray-300'
                }`}
              >
                <option value="">Select Customer</option>
                {customers.filter(c => c.customerType !== 'cash').map(customer => (
                  <option key={customer.id} value={customer.id}>
                    {customer.name} ({customer.id})
                  </option>
                ))}
              </select>
              {errors.customerId && (
                <p className="text-red-500 text-xs mt-1">{errors.customerId}</p>
              )}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Due Date *
              </label>
              <input
                type="date"
                value={form.dueDate}
                onChange={(e) => setForm({ ...form, dueDate: e.target.value })}
                className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent ${
                  errors.dueDate ? 'border-red-300' : 'border-gray-300'
                }`}
              />
              {errors.dueDate && (
                <p className="text-red-500 text-xs mt-1">{errors.dueDate}</p>
              )}
            </div>
          </div>

          {/* Customer Info Display */}
          {selectedCustomer && (
            <div className="mb-6 p-4 bg-blue-50 rounded-lg">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                <div>
                  <span className="font-medium text-blue-800">Payment Terms:</span>
                  <span className="ml-2 text-blue-700">{selectedCustomer.paymentTerms} days</span>
                </div>
                <div>
                  <span className="font-medium text-blue-800">Credit Limit:</span>
                  <span className="ml-2 text-blue-700">${selectedCustomer.creditLimit.toLocaleString()}</span>
                </div>
                <div>
                  <span className="font-medium text-blue-800">Available Credit:</span>
                  <span className="ml-2 text-blue-700">
                    ${(selectedCustomer.creditLimit - selectedCustomer.totalOwed).toFixed(2)}
                  </span>
                </div>
              </div>
            </div>
          )}

          {/* Invoice Items */}
          <div className="mb-6">
            <label className="block text-sm font-medium text-gray-700 mb-2">Invoice Items</label>
            <div className="space-y-3">
              {form.items.map((item, index) => (
                <div key={index} className="grid grid-cols-12 gap-2 items-center bg-gray-50 p-3 rounded">
                  <div className="col-span-5">
                    <input
                      type="text"
                      value={item.description}
                      onChange={(e) => updateItem(index, 'description', e.target.value)}
                      placeholder="Item description"
                      className="w-full px-2 py-1 border border-gray-300 rounded text-sm focus:ring-2 focus:ring-emerald-500"
                    />
                  </div>
                  <div className="col-span-2">
                    <input
                      type="number"
                      value={item.quantity}
                      onChange={(e) => updateItem(index, 'quantity', e.target.value)}
                      placeholder="Qty"
                      min="0"
                      step="1"
                      className="w-full px-2 py-1 border border-gray-300 rounded text-sm focus:ring-2 focus:ring-emerald-500"
                    />
                  </div>
                  <div className="col-span-2">
                    <input
                      type="number"
                      value={item.price}
                      onChange={(e) => updateItem(index, 'price', e.target.value)}
                      placeholder="Price"
                      min="0"
                      step="0.01"
                      className="w-full px-2 py-1 border border-gray-300 rounded text-sm focus:ring-2 focus:ring-emerald-500"
                    />
                  </div>
                  <div className="col-span-2">
                    <input
                      type="number"
                      value={item.total.toFixed(2)}
                      readOnly
                      className="w-full px-2 py-1 border border-gray-300 rounded text-sm bg-gray-100"
                    />
                  </div>
                  <div className="col-span-1 flex justify-center">
                    {form.items.length > 1 && (
                      <button
                        onClick={() => removeItem(index)}
                        className="p-1 text-red-600 hover:bg-red-100 rounded"
                        type="button"
                      >
                        <XCircle size={14} />
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>

            {errors.items && (
              <p className="text-red-500 text-xs mt-1">{errors.items}</p>
            )}

            <div className="flex justify-between items-center mt-3">
              <button
                onClick={addItem}
                type="button"
                className="px-3 py-1 text-sm bg-emerald-100 text-emerald-700 rounded hover:bg-emerald-200 flex items-center space-x-1"
              >
                <Plus size={14} />
                <span>Add Item</span>
              </button>

              <div className="text-lg font-semibold">
                Total: ${calculateTotal().toFixed(2)}
              </div>
            </div>
          </div>

          {/* Notes */}
          <div className="mb-6">
            <label className="block text-sm font-medium text-gray-700 mb-1">Notes</label>
            <textarea
              value={form.notes}
              onChange={(e) => setForm({ ...form, notes: e.target.value })}
              rows={3}
              placeholder="Additional notes..."
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            />
          </div>

          {/* Action Buttons */}
          <div className="flex space-x-3">
            <button
              onClick={handleSubmit}
              className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2"
            >
              <CheckCircle size={16} />
              <span>Create Invoice</span>
            </button>
            <button
              onClick={onClose}
              className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 flex items-center space-x-2"
            >
              <XCircle size={16} />
              <span>Cancel</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CreateInvoiceModal;
