    let invalidVendorCount = 0;
    
    vendors.data.forEach((vendor, index) => {
      console.log(`Checking vendor ${index}:`, vendor);
      
      // Only include vendors with valid names (not null, undefined, empty, or "null")
      if (vendor.name && vendor.name.trim() !== '' && vendor.name !== 'null') {
        console.log(`Vendor ${index} has valid name: "${vendor.name}"`);
        vendorMap.set(vendor.id, {
          ...vendor,
          purchaseOrders: []
        });
        validVendorCount++;
        console.log(`Added vendor to map with id: ${vendor.id}`);
      } else {
        console.log(`Vendor ${index} SKIPPED - invalid name: "${vendor.name}"`);
        invalidVendorCount++;
      }
    });
    
    console.log(`Added ${validVendorCount} vendors with valid names to map`);
    console.log(`Skipped ${invalidVendorCount} vendors with invalid names`);
  } else {
    console.log("No valid vendors data found");
  }

  // Process all valid purchase orders
  if (purchaseOrders && Array.isArray(purchaseOrders)) {
    console.log(`Processing ${purchaseOrders.length} purchase orders`);
    
    let assignedPOCount = 0;
    let skippedPOCount = 0;
    
    purchaseOrders.forEach((po, index) => {
      console.log(`Checking PO ${index}:`, po);
      
      // Handle both string "null" and actual null values
      const vendorId = po.Vendor_Id || po.vendor_Id;
      console.log(`PO ${index} vendorId: ${vendorId}`);
      
      // Only process POs with valid vendor_Id (not null, undefined, empty, or "null")
      if (vendorId && vendorId !== 'null') {
        console.log(`PO ${index} has valid vendorId: ${vendorId}`);
        
        if (vendorMap.has(vendorId)) {
          console.log(`Found existing vendor with id ${vendorId} for PO ${index}`);
          // Add PO to existing vendor
          vendorMap.get(vendorId).purchaseOrders.push(po);
          assignedPOCount++;
          console.log(`Added PO ${po.id || po.POID} to existing vendor ${vendorId}`);
        } else {
          console.log(`No existing vendor found with id ${vendorId} for PO ${index}`);
          // If vendor with valid name doesn't exist, create a placeholder
          const uniqueKey = `${vendorId}_${po.POID || po.id}`;
