            creditLimit: 0,
            paymentTerms: 0,
            lastPayment: 'No payments',
            creditStatus: 'unknown',
            billCount: 1,
            avgPaymentDays: 0,
            status: 'pending',
            type: 'Unknown',
            poId: po.id,
            purchaseOrders: [po]
          });
          assignedPOCount++;
          console.log(`Created new placeholder vendor and added PO ${po.id || po.POID}`);
        }
      } else {
        console.log(`PO ${index} SKIPPED - invalid vendorId: ${vendorId}`);
        skippedPOCount++;
      }
    });
    
    console.log(`Assigned ${assignedPOCount} POs to vendors`);
    console.log(`Skipped ${skippedPOCount} POs with invalid vendor IDs`);
  } else {
    console.log("No valid purchase orders data found");
  }

  // Log vendor map stats
  console.log(`Vendor map contains ${vendorMap.size} entries`);
  
  // Log each vendor and their PO count
  console.log("Vendor map contents:");
  for (const [key, value] of vendorMap.entries()) {
    console.log(`Vendor ${key}: "${value.name}" has ${value.purchaseOrders.length} POs`);
  }

  // Convert the map back to an array and filter to only include vendors with names
  const allVendors = Array.from(vendorMap.values());
  console.log(`Converted map to array with ${allVendors.length} vendors`);
  
  const vendorsWithNames = allVendors.filter(vendor => 
    vendor.name && vendor.name.trim() !== '' && vendor.name !== 'null'
  );
  console.log(`After final name filtering: ${vendorsWithNames.length} vendors remain`);
  
  // Check if any vendors have POs
  const vendorsWithPOs = vendorsWithNames.filter(vendor => 
    vendor.purchaseOrders && vendor.purchaseOrders.length > 0
