// src/services/apApiService.js

// API Configuration for Accounts Payable
const API_PURCHASE_ORDERS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/PurchaseOrderManagement/purchaseOrdersWithoutDetails';
const API_VENDORS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/VendorManagement/vendors';
const API_PRODUCTS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/products?status=All&start=0&size=200';

// Replace with your actual Bearer token
const BEARER_TOKEN = 'your-bearer-token-here';
const apiHeaders = {
  'Authorization': `Bearer ${BEARER_TOKEN}`,
  'Content-Type': 'application/json'
};
export const apApiService = {
  async fetchProducts() {
    const response = await fetch(API_PRODUCTS, {
      method: 'GET',
      headers: apiHeaders
    });
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    return await response.json();
  },
  async fetchPurchaseOrders() {
    const response = await fetch(API_PURCHASE_ORDERS, {
      method: 'GET',
      headers: apiHeaders
    });
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    return await response.json();
  },
  async fetchVendors() {
    const response = await fetch(API_VENDORS, {
      method: 'GET',
      headers: apiHeaders
    });
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    return await response.json();
  },
  transformVendors(apiVendors, allPOData) {
    return apiVendors.map(vendor => {
      // Pick correct details block depending on type
      let vendorDetails = null;
      if (vendor.type === 'Individual') {
        vendorDetails = vendor.individuals && vendor.individuals.length > 0 ? vendor.individuals[0] : null;
      } else if (vendor.type === 'Organization') {
        vendorDetails = vendor.organizations && vendor.organizations.length > 0 ? vendor.organizations[0] : null;
      }
      if (!vendorDetails) {
        console.warn(`No details found for vendor ${vendor.id}`);
        return null;
      }

      // Contact info (first available)
      const contactInfo = vendorDetails.contactMediums && vendorDetails.contactMediums.length > 0 ? vendorDetails.contactMediums[0].mediumCharacteristics[0] : {};

      // All purchase orders for this vendor
      const vendorPOs = allPOData.filter(po => po.vendor_Id === vendor.id || po.Vendor_Id === vendor.id);

      // Calculate payable only for non-fully-applied POs
      const totalPayable = vendorPOs.filter(po => !po.purchaseOrderItems.every(item => item.status === 'applied')).reduce((sum, po) => {
        const poTotal = po.purchaseOrderItems.reduce((itemSum, item) => itemSum + (item.totalPrice || 0), 0);
        return sum + poTotal;
      }, 0);
      const overdueAmount = totalPayable;
      const billCount = vendorPOs.length;

      // ðŸ”‘ Correct naming logic
      let vendorName = `Vendor ${vendor.id}`;
      if (vendor.type === 'Individual') {
        vendorName = vendorDetails.fullName || vendorName;
      } else if (vendor.type === 'Organization') {
        vendorName = vendorDetails.tradingName || vendorDetails.legalName || vendorName;
      }
      return {
        id: vendor.id,
        name: vendorName,
        email: contactInfo.emailAddress || 'No email provided',
        phone: contactInfo.number || 'No phone provided',
        address: `${contactInfo.street1 || ''} ${contactInfo.city || ''} ${contactInfo.country || ''}`.trim() || 'No address provided',
        vendorType: vendor.type,
        paymentTerms: vendor.type === 'Organization' ? 45 : 30,
        totalPayable,
        overdueAmount,
        lastPayment: 'No payments',
        accountBalance: totalPayable,
        billCount,
        avgPaymentDays: 30,
        status: vendor.status || 'active',
        type: vendor.type
      };
    }).filter(v => v !== null); // skip bad vendors
  }
};

