import { useState, useEffect } from 'react';
import { Modal, DatePicker, Input, Select, Button } from 'antd';
import { formatCurrency } from '../../Utility/formatters';

const { Option } = Select;

const RecordPaymentModal = ({ invoiceId, amount, onClose, onSubmit }) => {
  const [date, setDate] = useState(null);  
  const [enteredAmount, setEnteredAmount] = useState(amount.toString());
  const [method, setMethod] = useState('');
  const [account, setAccount] = useState('');
  const [memo, setMemo] = useState('');
  const [accounts, setAccounts] = useState([]);

  useEffect(() => {
    const fetchAccounts = async () => {
      try {
        const response = await fetch('http://localhost:8080/api/accounts');
        const data = await response.json();

        // Filter for asset and bank/cash accounts
        const filteredAccounts = data.filter(acct => 
          acct.type === 'assets' && 
          (acct.name.toLowerCase().includes('bank') || acct.name.toLowerCase().includes('cash'))
        );

        setAccounts(filteredAccounts);
      } catch (err) {
        console.error('Failed to fetch accounts:', err);
      }
    };

    fetchAccounts();
  }, []);

  const handleSubmit = () => {
    if (!date || !enteredAmount || !method || !account) return;
    onSubmit(date, parseFloat(enteredAmount), method, account, memo);
    onClose();
  };

  return (
    <Modal
      title={`Record Payment for Invoice ${invoiceId}`}
      open={true}
      onCancel={onClose}
      footer={[
        <Button key="cancel" onClick={onClose}>
          Close
        </Button>
      ]}
    >
      <div className="space-y-4">
        <div>
          <label className="text-gray-700">Date</label>
          <DatePicker 
            className="w-full"
            value={date}
            onChange={(val) => setDate(val)}
          />
        </div>
        <div>
          <label className="text-gray-700">Amount</label>
          <Input
            value={enteredAmount} 
            onChange={(e) => setEnteredAmount(e.target.value)}
            prefix={<span className="text-gray-500">Rs</span>}
          />
          <div className="text-sm text-gray-500">Invoice amount: {formatCurrency(amount)}</div>
        </div>
        <div>
          <label className="text-gray-700">Payment Method</label>
          <Select value={method} onChange={setMethod} className="w-full">
            <Option value="cash">Cash</Option>
            <Option value="check">Check</Option>
            <Option value="eft">Bank/Electronic Funds</Option>
            <Option value="credit">Credit Card</Option> 
          </Select>
        </div>
        <div>
          <label className="text-gray-700">Deposit Account</label>
         <Select 
  value={account} 
  onChange={setAccount}
  showSearch
  filterOption={(input, option) =>
    (option?.children ?? '').toLowerCase().includes(input.toLowerCase())
  }
  className="w-full"
>
  {accounts.map(parent => (
    parent.children && parent.children.length > 0 ? (
      <Select.OptGroup key={parent.id} label={`${parent.code} - ${parent.name}`}>
        {parent.children.map(child => (
          <Option key={child.id} value={child.id}>
            {child.code} - {child.name}
          </Option>
        ))}
      </Select.OptGroup>
    ) : (
      <Option key={parent.id} value={parent.id}>
        {parent.code} - {parent.name}
      </Option>
    )
  ))}
</Select>

        </div>
        <div>
          <label className="text-gray-700">Memo</label>
          <Input.TextArea value={memo} onChange={(e) => setMemo(e.target.value)} />
        </div>
        
        <Button 
          type="primary"
          className="mt-6"
          disabled={!date || !enteredAmount || !method || !account}
          onClick={handleSubmit}
        >
          Submit Payment
        </Button>
      </div>
    </Modal>
  );
};

export default RecordPaymentModal;
