const handlePaymentComplete = (paymentData, isFullPayment) => {
  const updatedInvoice = { ...localInvoice };

  if (isFullPayment || paymentData.amount >= localInvoice.remainingAmount) {
  updatedInvoice.paid = true;
  updatedInvoice.paymentStatus = 'paid';
  updatedInvoice.remainingAmount = 0;
} else {
  updatedInvoice.paid = false;
  updatedInvoice.paymentStatus = 'partial';
  updatedInvoice.remainingAmount = paymentData.remainingAmount;
}


  // Save to localStorage (simulate API update)
  const existingOrders = JSON.parse(localStorage.getItem('orders') || '[]');
  const orderIndex = existingOrders.findIndex(o => o.orderId === updatedInvoice.orderId);
  if (orderIndex !== -1) {
    existingOrders[orderIndex] = updatedInvoice;
    localStorage.setItem('orders', JSON.stringify(existingOrders));
  }

  setLocalInvoice(updatedInvoice);

  if (onRefresh) {
    onRefresh();   // 🔥 tell parent to reload sales
  }

  setPaymentModalOpen(false);
};


