import React, { useState } from "react";
import {
  FileText,
  TrendingUp,
  BarChart2,
  PieChart,
  DollarSign,
  Calendar,
  Eye,
  XCircle,
  Download,
  Search,
  Filter,
} from "lucide-react";
import { DatePicker } from "@mui/x-date-pickers";
import { jsPDF } from "jspdf";
import autoTable from "jspdf-autotable";

// Currency Formatter (PKR for POS in Pakistan)
function formatCurrency(amount) {
  const abs = Math.abs(amount);
  return (amount < 0 ? "-₨" : "₨") + abs.toLocaleString("en-PK");
}

// Modal Component
const StatementViewModal = ({ statement, type, onClose }) => {
  const [searchTerm, setSearchTerm] = useState("");

  const filteredDetails = statement.details.filter(row =>
    row.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const downloadPDF = () => {
    const doc = new jsPDF();
    doc.text(statement.title, 14, 10);
    autoTable(doc, {
      head: [["Account", "Amount"]],
      body: filteredDetails.map(row => [
        row.name,
        formatCurrency(row.amount),
      ]),
    });
    doc.save(`${statement.id}.pdf`);
  };

  if (!statement) return null;
  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-3xl w-full shadow-lg overflow-y-auto">
        {/* Modal Header */}
        <div className="px-6 py-4 border-b flex items-center justify-between">
          <h3 className="text-lg font-semibold flex items-center gap-2">
            {statement.title} ({statement.period})
          </h3>
          <button onClick={onClose}>
            <XCircle className="text-gray-500 hover:text-gray-700" size={22} />
          </button>
        </div>

        {/* Date & Search */}
        <div className="px-6 py-3 flex flex-wrap items-center justify-between gap-3 border-b">
          <div className="flex items-center gap-2">
            <Calendar size={16} className="text-gray-500" />
            <span className="text-sm">{statement.date}</span>
          </div>
          <div className="flex items-center gap-2">
            <Search size={16} className="text-gray-500" />
            <input
              type="text"
              placeholder="Search account..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="border rounded px-2 py-1 text-sm"
            />
          </div>
        </div>

        {/* Table */}
        <div className="p-6">
          <table className="w-full border-collapse">
            <tbody>
              {filteredDetails.map((row, i) => (
                <tr
                  key={i}
                  className={`border-b hover:bg-gray-50 ${
                    row.bold ? "font-bold" : ""
                  }`}
                >
                  <td className="px-4 py-2">{row.name}</td>
                  <td className="px-4 py-2 text-right">
                    {formatCurrency(row.amount)}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {statement.notes && (
            <div className="mt-4 p-3 bg-gray-50 rounded">
              <h4 className="text-sm font-semibold text-gray-600">Notes</h4>
              <p className="text-sm">{statement.notes}</p>
            </div>
          )}

          {/* Actions */}
          <div className="flex justify-end gap-3 mt-6">
            <button
              onClick={downloadPDF}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded flex items-center gap-2"
            >
              <Download size={16} /> PDF
            </button>
            <button
              onClick={onClose}
              className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded flex items-center gap-2"
            >
              <XCircle size={16} /> Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// Main Financial Statements Component
const FinancialStatements = () => {
  const [viewStatement, setViewStatement] = useState(null);
  const [viewType, setViewType] = useState(null);

  const sampleStatements = {
    // ... your same sample data here
  };

  const statementCards = [
    {
      key: "profit_loss",
      icon: <TrendingUp className="text-green-600" size={28} />,
      bg: "bg-green-50",
      border: "border-green-200",
      label: "Net Income",
      value: formatCurrency(sampleStatements.profit_loss.netIncome),
    },
    {
      key: "balance_sheet",
      icon: <BarChart2 className="text-blue-600" size={28} />,
      bg: "bg-blue-50",
      border: "border-blue-200",
      label: "Total Assets",
      value: formatCurrency(sampleStatements.balance_sheet.assets),
    },
    {
      key: "cash_flow",
      icon: <PieChart className="text-emerald-600" size={28} />,
      bg: "bg-emerald-50",
      border: "border-emerald-200",
      label: "Net Change in Cash",
      value: formatCurrency(sampleStatements.cash_flow.netChangeInCash),
    },
    {
      key: "retained_earnings",
      icon: <DollarSign className="text-purple-600" size={28} />,
      bg: "bg-purple-50",
      border: "border-purple-200",
      label: "Ending Retained Earnings",
      value: formatCurrency(sampleStatements.retained_earnings.endingRetainedEarnings),
    },
  ];

  return (
    <div className="max-w-6xl mx-auto p-6 bg-white shadow rounded">
      {/* Header */}
      <div className="mb-8 flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 flex items-center gap-2">
            <FileText className="text-emerald-600" /> Financial Statements
          </h1>
          <p className="text-gray-600">
            View and analyze financial performance, tailored for POS reports.
          </p>
        </div>
        <div>
          <DatePicker label="Select Period" />
        </div>
      </div>

      {/* Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {statementCards.map((card) => (
          <div
            key={card.key}
            className={`${card.bg} p-6 rounded-lg border ${card.border} flex flex-col`}
          >
            <div className="flex items-center gap-3">
              {card.icon}
              <div className="flex-1">
                <div className="text-lg font-semibold">
                  {sampleStatements[card.key].title}
                </div>
                <div className="text-sm text-gray-600">
                  {sampleStatements[card.key].period}
                </div>
              </div>
            </div>
            <div className="mt-4 flex justify-between items-end">
              <div>
                <div className="text-xs text-gray-500">{card.label}</div>
                <div className="text-2xl font-bold">{card.value}</div>
              </div>
              <button
                onClick={() => {
                  setViewStatement(sampleStatements[card.key]);
                  setViewType(card.key);
                }}
                className="px-4 py-2 bg-gray-800 hover:bg-gray-900 text-white rounded flex items-center gap-2"
              >
                <Eye size={16} /> View
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal */}
      {viewStatement && (
        <StatementViewModal
          statement={viewStatement}
          type={viewType}
          onClose={() => setViewStatement(null)}
        />
      )}
    </div>
  );
};

export default FinancialStatements;
