const GeneralLedger = () => {
  const [journalEntries, setJournalEntries] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const [filteredEntries, setFilteredEntries] = useState([]);
  const [selectedEntry, setSelectedEntry] = useState(null);
  const [isAddingEntry, setIsAddingEntry] = useState(false);
  const [loading, setLoading] = useState({
    entries: false,
    accounts: false,
    purchase: false
  });
  const [errors, setErrors] = useState({
    entries: null,
    accounts: null,
    purchase: null
  });
  const [filters, setFilters] = useState({
    dateFrom: '',
    dateTo: '',
    accountId: '',
    transactionType: 'all',
    searchTerm: ''
  });
  const [trialBalance, setTrialBalance] = useState([]);
  const [showTrialBalance, setShowTrialBalance] = useState(false);
  const [newEntry, setNewEntry] = useState({
    date: new Date().toISOString().split('T')[0],
    description: '',
    reference: '',
    entries: [
      { accountId: '', debit: 0, credit: 0, description: '' },
      { accountId: '', debit: 0, credit: 0, description: '' }
    ]
  });

  // API Configuration
  const API_BASE_URL_SALES_ORDERS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/orders?start=0&size=200';
  const API_BASE_URL_CHARTS_OF_ACCOUNTS = 'http://localhost:8080/api/accounts';
  const API_BASE_URL_PURCHASE_ORDERS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/PurchaseOrderManagement/purchaseOrdersWithoutDetails';

  // Fetch sales order data from API
  const fetchSalesOrderData = async () => {
    try {
      const response = await fetch(API_BASE_URL_SALES_ORDERS, {
        method: 'GET',
        headers: {
          'Authorization': 'Bearer Hamid',
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`Sales API request failed with status ${response.status}`);
      }
      
      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error fetching sales order data:', error);
      return null;
    }
  };

  // Fetch purchase order data from API
  const fetchPurchaseOrderData = async () => {
    try {
      console.log('Making purchase orders API call to:', API_BASE_URL_PURCHASE_ORDERS);
      
      const response = await fetch(API_BASE_URL_PURCHASE_ORDERS, {
        method: 'GET',
        headers: {
          'Authorization': 'Bearer Hamid',
          'Content-Type': 'application/json',
        },
      });
      
      console.log('Purchase orders API response status:', response.status);
      
      if (!response.ok) {
        throw new Error(`Purchase API request failed with status ${response.status}`);
      }
      
      const data = await response.json();
      console.log('Purchase orders API data received:', data);
      return data;
    } catch (error) {
      console.error('Error fetching purchase order data:', error);
      return null;
    }
  };

  // Format date from API format
  const formatDateFromCreatedAt = (createdAt) => {
    try {
      const parts = createdAt.split('.');
      if (parts.length >= 3) {
        return `${parts[0]}-${parts[1].padStart(2, '0')}-${parts[2].padStart(2, '0')}`;
      }
      return new Date().toISOString().split('T')[0];
    } catch (error) {
      return new Date().toISOString().split('T')[0];
    }
  };

  // Helper function to flatten hierarchical account structure
  const flattenAccounts = (accounts) => {
    const result = [];
    
    const processAccount = (account) => {
      result.push(account);
      
      if (account.children && Array.isArray(account.children)) {
        account.children.forEach(child => processAccount(child));
      }
    };
    
    accounts.forEach(account => processAccount(account));
    return result;
  };

  // Fetch Charts of Accounts from API
  const loadChartsOfAccounts = useCallback(async () => {
    setLoading(prev => ({ ...prev, accounts: true }));
    setErrors(prev => ({ ...prev, accounts: null }));
    
    try {
      const response = await fetch(API_BASE_URL_CHARTS_OF_ACCOUNTS, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`Failed to fetch accounts: ${response.status}`);
      }
      
      const data = await response.json();
      console.log('Raw accounts data from API:', data);
      
      if (!Array.isArray(data)) {
        throw new Error('API response is not an array');
      }
      
      const allAccounts = flattenAccounts(data);
      console.log('All flattened accounts:', allAccounts.length);
      
      const usableAccounts = allAccounts.filter(account => 
        account.category === 'account' && 
        account.id && 
        account.code && 
        account.name && 
        account.type
      );
      
      console.log('Usable accounts (category=account):', usableAccounts.length);
      
      if (usableAccounts.length === 0) {
        console.log('No usable accounts found, using fallback');
        throw new Error('No usable accounts returned from API');
      }
      
      usableAccounts.sort((a, b) => a.code.localeCompare(b.code));
      
      setAccounts(usableAccounts);
      setLoading(prev => ({ ...prev, accounts: false }));
      
      console.log(`Successfully loaded ${usableAccounts.length} accounts from API`);
      return usableAccounts;
      
    } catch (error) {
      console.error('Error loading charts of accounts:', error);
      setErrors(prev => ({ ...prev, accounts: error.message }));
      setLoading(prev => ({ ...prev, accounts: false }));
      
      console.log('Using fallback chart of accounts');
      // const fallbackAccounts = [
      //   // Current Assets
      //   { id: '12', code: '1111', name: 'Petty Cash', type: 'assets', category: 'account' },
      //   { id: '13', code: '1112', name: 'Bank - Main Account', type: 'assets', category: 'account' },
      //   { id: '14', code: '1120', name: 'Accounts Receivable', type: 'assets', category: 'account' },
      //   { id: '15', code: '1125', name: 'Insurance Receivables', type: 'assets', category: 'account' },
      //   { id: '16', code: '1130', name: 'Inventory - FMCG Products', type: 'assets', category: 'account' },
      //   { id: '19', code: '1131', name: 'Inventory - Telecom Equipment', type: 'assets', category: 'account' },
      //   { id: '22', code: '1132', name: 'Medical Supplies Inventory', type: 'assets', category: 'account' },
      //   { id: '17', code: '1140', name: 'Packaging Materials', type: 'assets', category: 'account' },
