      //   { id: '18', code: '1150', name: 'Promotional Materials', type: 'assets', category: 'account' },
        
      //   // Fixed Assets
      //   { id: '24', code: '1210', name: 'Network Equipment', type: 'assets', category: 'account' },
      //   { id: '26', code: '1211', name: 'Medical Equipment', type: 'assets', category: 'account' },
