import React, { useState, useEffect } from 'react';
import { 
  Users, 
  DollarSign, 
  CreditCard, 
  AlertTriangle, 
  Clock, 
  Plus, 
  Search, 
  Filter, 
  Eye, 
  Edit, 
  Download,
  CheckCircle,
  XCircle,
  Calendar,
  FileText,
  TrendingUp,
  Mail,
  Phone,
  Printer,
  Send
} from 'lucide-react';

const AccountsReceivable = () => {
  const [customers, setCustomers] = useState([]);
  const [invoices, setInvoices] = useState([]);
  const [payments, setPayments] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [viewInvoice, setViewInvoice] = useState(null);
  const [loading, setLoading] = useState(false);
  const [salesOrders, setSalesOrders] = useState([]);
  
  const [filters, setFilters] = useState({
    customerType: 'all',
    status: 'all',
    dateRange: '30',
    searchTerm: ''
  });

  const [showInvoiceForm, setShowInvoiceForm] = useState(false);
  const [newInvoice, setNewInvoice] = useState({
    customerId: '',
    dueDate: '',
    items: [{ description: '', quantity: 1, price: 0, total: 0 }],
    notes: ''
  });

  // API Configuration
  const API_BASE_URL_SALES = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/orders?start=0&size=200';
  const API_CUSTOMERS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/customers?status=Active';

  // Fetch both sales orders and customers from API
  const fetchData = async () => {
    setLoading(true);
    try {
      // Fetch both data sources in parallel
      const [ordersResponse, customersResponse] = await Promise.all([
        fetch(API_BASE_URL_SALES, {
          method: 'GET',
          headers: {
            'Authorization': 'Bearer Hamid',
            'Content-Type': 'application/json',
          },
        }),
        fetch(API_CUSTOMERS, {
          method: 'GET',
          headers: {
            'Authorization': 'Bearer Hamid',
            'Content-Type': 'application/json',
          },
        })
      ]);
      
      if (!ordersResponse.ok || !customersResponse.ok) {
        throw new Error(`API request failed`);
      }
      
      const ordersData = await ordersResponse.json();
      const customersData = await customersResponse.json();
      
      setSalesOrders(ordersData);
      processARData(ordersData, customersData);
    } catch (error) {
      console.error('Error fetching data:', error);
      loadSampleData();
    } finally {
      setLoading(false);
    }
  };

  // Process API data to create AR entries - ONLY UNPAID & UNFULFILLED
  const processARData = (orders, apiCustomers) => {
    // Create customer map from API customers
    const customerMap = new Map();
    
    // First, add all customers from the API
    apiCustomers.forEach(customer => {
      customerMap.set(customer.id, {
        id: customer.id,
        name: customer.name,
        email: customer.email,
        phone: customer.phoneNumber,
        address: customer.address?.join(', ') || 'Not specified',
        customerType: 'credit',
        creditLimit: 100000, // Default credit limit
        paymentTerms: 30,
        totalOwed: 0,
        overdueAmount: 0,
        lastPayment: null,
        creditStatus: 'good',
        accountBalance: 0,
        invoiceCount: 0,
        avgPaymentDays: 0,
        totalSpend: customer.totalSpendOnOrder || 0,
        numberOfOrders: customer.numberOfOrders || 0
      });
    });

    const invoiceList = [];
    const paymentList = [];

    // Process only UNPAID and UNFULFILLED orders
    orders.forEach(order => {
      // Skip draft, paid, or fulfilled orders
      if (order.draft || order.paid || order.fulfilled) return;

      // Get customer info
      let customer;
      if (order.customerId && order.customerId !== 'null') {
        customer = customerMap.get(order.customerId);
        if (!customer) {
          // Create a placeholder for unknown customer
          customer = {
            id: order.customerId,
            name: `Customer ${order.customerId}`,
            email: 'unknown@example.com',
            phone: 'Not specified',
            address: 'Not specified',
            customerType: 'credit',
            creditLimit: 50000,
            paymentTerms: 30,
            totalOwed: 0,
            overdueAmount: 0,
            lastPayment: null,
            creditStatus: 'good',
            accountBalance: 0,
            invoiceCount: 0,
            avgPaymentDays: 0
          };
          customerMap.set(order.customerId, customer);
        }
      } else {
        // Handle orders without customer ID
        const walkInId = `WALKIN-${order.orderId}`;
        customer = {
          id: walkInId,
          name: `Walk-in Customer (Order ${order.orderId})`,
          email: 'walkin@pos.com',
          phone: 'Cash Sale',
          address: 'Point of Sale',
          customerType: 'cash',
          creditLimit: 0,
          paymentTerms: 0,
          totalOwed: 0,
          overdueAmount: 0,
          lastPayment: null,
          creditStatus: 'cash_only',
          accountBalance: 0,
          invoiceCount: 0,
          avgPaymentDays: 0
        };
        customerMap.set(walkInId, customer);
      }

      // Create invoice from unpaid order
      const invoice = {
        id: `INV-${order.orderId}`,
        customerId: customer.id,
        customerName: customer.name,
        date: formatDate(order.createdAt),
        dueDate: calculateDueDate(order.createdAt, 30),
        amount: parseFloat(order.total) || 0,
        paid: 0,
        balance: parseFloat(order.total) || 0,
        status: isOverdue(order.createdAt, 30) ? 'overdue' : 'outstanding',
        daysOverdue: calculateDaysOverdue(order.createdAt, 30),
        paymentTerms: 30,
        items: order.lineItems?.map(item => ({
          description: item.productName,
          quantity: item.quantity,
          price: item.price,
          total: item.total
        })) || [],
        notes: `Unpaid & Unfulfilled - Status: ${order.status}`,
        source: 'pos',
        fulfillmentStatus: order.fulfilled ? 'Fulfilled' : 'Unfulfilled'
      };

      invoiceList.push(invoice);

      // Update customer totals
      customer.totalOwed += invoice.balance;
      if (invoice.status === 'overdue') {
        customer.overdueAmount += invoice.balance;
      }
      customer.invoiceCount++;
      customer.accountBalance = -customer.totalOwed;
    });

    // Update credit status based on outstanding amounts
    customerMap.forEach(customer => {
      if (customer.totalOwed === 0) {
        customer.creditStatus = 'excellent';
      } else {
        const utilizationRate = customer.totalOwed / (customer.creditLimit || 1);
        if (customer.overdueAmount > 0) {
          customer.creditStatus = 'watch';
        } else if (utilizationRate > 0.8) {
          customer.creditStatus = 'watch';
        } else if (utilizationRate > 0.5) {
          customer.creditStatus = 'good';
        } else {
          customer.creditStatus = 'excellent';
        }
      }
    });

    // Filter out customers with no outstanding balance
    const customersWithBalance = Array.from(customerMap.values()).filter(c => c.totalOwed > 0);

    setCustomers(customersWithBalance);
    setInvoices(invoiceList);
    setPayments(paymentList);
  };

  // Helper functions
  const formatDate = (dateString) => {
    if (!dateString) return new Date().toISOString().split('T')[0];
    
    const parts = dateString.split('.');
    if (parts.length >= 3) {
      return `${parts[0]}-${parts[1].padStart(2, '0')}-${parts[2].padStart(2, '0')}`;
    }
    return new Date().toISOString().split('T')[0];
  };

  const calculateDueDate = (createdAt, terms) => {
    const date = new Date(formatDate(createdAt));
    date.setDate(date.getDate() + terms);
    return date.toISOString().split('T')[0];
  };

  const isOverdue = (createdAt, terms) => {
    const dueDate = new Date(calculateDueDate(createdAt, terms));
    return dueDate < new Date();
  };

  const calculateDaysOverdue = (createdAt, terms) => {
    const dueDate = new Date(calculateDueDate(createdAt, terms));
    const today = new Date();
    if (dueDate >= today) return 0;
    
    const diffTime = Math.abs(today - dueDate);
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24));
  };

  useEffect(() => {
    fetchData();
  }, []);

  const loadSampleData = () => {
    // Fallback sample data
    const sampleCustomers = [
      {
        id: 'CUST001',
        name: 'Sample Customer 1',
        email: 'customer1@example.com',
        phone: '+966-11-123-4567',
        address: 'Riyadh',
        customerType: 'credit',
        creditLimit: 50000,
        paymentTerms: 30,
        totalOwed: 15750.00,
        overdueAmount: 5250.00,
        lastPayment: '2025-01-10',
        creditStatus: 'good',
        accountBalance: -15750.00,
        invoiceCount: 2,
        avgPaymentDays: 25
      }
    ];

    const sampleInvoices = [
      {
        id: 'INV-001',
        customerId: 'CUST001',
        customerName: 'Sample Customer 1',
        date: '2025-01-15',
        dueDate: '2025-02-14',
        amount: 5250.00,
        paid: 0,
        balance: 5250.00,
        status: 'outstanding',
        daysOverdue: 0,
        paymentTerms: 30,
        items: [
          { description: 'Sample Product', quantity: 1, price: 5250.00, total: 5250.00 }
        ],
        notes: 'Unpaid order',
        source: 'manual'
      }
    ];

    setCustomers(sampleCustomers);
    setInvoices(sampleInvoices);
    setPayments([]);
  };

  const calculateARSummary = () => {
    const totalAR = customers.reduce((sum, customer) => sum + customer.totalOwed, 0);
    const totalOverdue = customers.reduce((sum, customer) => sum + customer.overdueAmount, 0);
    const customersWithBalance = customers.filter(customer => customer.totalOwed > 0).length;
    const avgDaysOutstanding = customers.length > 0 
      ? customers.reduce((sum, customer) => sum + customer.avgPaymentDays, 0) / customers.length 
      : 0;

    return {
      totalAR,
      totalOverdue,
      customersWithBalance,
      avgDaysOutstanding: Math.round(avgDaysOutstanding)
    };
  };

  const getAgingData = () => {
    const aging = {
      current: 0,
      days30: 0,
      days60: 0,
      days90: 0,
      over90: 0
    };

    invoices.forEach(invoice => {
      if (invoice.status === 'paid') return;
      
      const daysPastDue = invoice.daysOverdue || 0;
      
      if (daysPastDue <= 0) {
        aging.current += invoice.balance;
      } else if (daysPastDue <= 30) {
        aging.days30 += invoice.balance;
      } else if (daysPastDue <= 60) {
        aging.days60 += invoice.balance;
      } else if (daysPastDue <= 90) {
        aging.days90 += invoice.balance;
      } else {
        aging.over90 += invoice.balance;
      }
    });

    return aging;
  };

  const getFilteredCustomers = () => {
    let filtered = [...customers];

    if (filters.customerType !== 'all') {
      filtered = filtered.filter(customer => customer.customerType === filters.customerType);
    }

    if (filters.status !== 'all') {
      filtered = filtered.filter(customer => {
        if (filters.status === 'overdue') return customer.overdueAmount > 0;
        if (filters.status === 'current') return customer.totalOwed > 0 && customer.overdueAmount === 0;
        if (filters.status === 'paid_up') return customer.totalOwed === 0;
        return true;
      });
    }

    if (filters.searchTerm) {
      const term = filters.searchTerm.toLowerCase();
      filtered = filtered.filter(customer =>
        customer.name.toLowerCase().includes(term) ||
        customer.email.toLowerCase().includes(term) ||
        customer.id.toLowerCase().includes(term)
      );
    }

    return filtered;
  };

  const getCreditStatusColor = (status) => {
    switch (status) {
      case 'excellent': return 'bg-green-100 text-green-800';
      case 'good': return 'bg-blue-100 text-blue-800';
      case 'watch': return 'bg-yellow-100 text-yellow-800';
      case 'hold': return 'bg-red-100 text-red-800';
      case 'cash_only': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getInvoiceStatusColor = (status) => {
    switch (status) {
      case 'paid': return 'bg-green-100 text-green-800';
      case 'outstanding': return 'bg-blue-100 text-blue-800';
      case 'overdue': return 'bg-red-100 text-red-800';
      case 'partial': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const summary = calculateARSummary();
  const aging = getAgingData();
  const filteredCustomers = getFilteredCustomers();

  // Render Invoices Tab
  const renderInvoicesTab = () => (
    <div>
      <div className="mb-6 flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
            <input
              type="text"
              placeholder="Search invoices..."
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            />
          </div>
          <select className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent">
            <option value="all">All Statuses</option>
            <option value="outstanding">Outstanding</option>
            <option value="overdue">Overdue</option>
            <option value="paid">Paid</option>
          </select>
        </div>
        <button
          onClick={() => setShowInvoiceForm(true)}
          className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2"
        >
          <Plus size={16} />
          <span>New Invoice</span>
        </button>
      </div>

      <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Invoice</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Customer</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Due Date</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Amount</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Balance</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {invoices.map((invoice) => (
              <tr key={invoice.id} className="hover:bg-gray-50">
                <td className="px-6 py-4">
                  <div className="text-sm font-medium text-gray-900">{invoice.id}</div>
                  <div className="text-xs text-gray-500">{invoice.source}</div>
                </td>
                <td className="px-6 py-4 text-sm text-gray-900">{invoice.customerName}</td>
                <td className="px-6 py-4 text-sm text-gray-900">{invoice.date}</td>
                <td className="px-6 py-4 text-sm text-gray-900">
                  {invoice.dueDate}
                  {invoice.daysOverdue > 0 && (
                    <div className="text-xs text-red-600">
                      {invoice.daysOverdue} days overdue
                    </div>
                  )}
                </td>
                <td className="px-6 py-4 text-sm text-gray-900">${invoice.amount.toFixed(2)}</td>
                <td className="px-6 py-4 text-sm text-gray-900">${invoice.balance.toFixed(2)}</td>
                <td className="px-6 py-4">
                  <span className={`px-2 py-1 text-xs font-medium rounded-full ${getInvoiceStatusColor(invoice.status)}`}>
                    {invoice.status}
                  </span>
                </td>
                <td className="px-6 py-4 text-sm font-medium">
                  <button
                    onClick={() => setViewInvoice(invoice)}
                    className="text-emerald-600 hover:text-emerald-900 mr-3"
                  >
                    <Eye size={16} />
                  </button>
                  <button className="text-blue-600 hover:text-blue-900">
                    <Edit size={16} />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  // Render Aging Report Tab
  const renderAgingTab = () => (
    <div>
      <div className="grid grid-cols-1 md:grid-cols-5 gap-4 mb-8">
        <div className="bg-green-50 p-4 rounded-lg border border-green-200">
          <div className="text-sm font-medium text-green-600">Current (0-30)</div>
          <div className="text-xl font-bold text-green-900">${aging.current.toFixed(2)}</div>
        </div>
        <div className="bg-yellow-50 p-4 rounded-lg border border-yellow-200">
          <div className="text-sm font-medium text-yellow-600">31-60 Days</div>
          <div className="text-xl font-bold text-yellow-900">${aging.days30.toFixed(2)}</div>
        </div>
        <div className="bg-orange-50 p-4 rounded-lg border border-orange-200">
          <div className="text-sm font-medium text-orange-600">61-90 Days</div>
          <div className="text-xl font-bold text-orange-900">${aging.days60.toFixed(2)}</div>
        </div>
        <div className="bg-red-50 p-4 rounded-lg border border-red-200">
          <div className="text-sm font-medium text-red-600">91-120 Days</div>
          <div className="text-xl font-bold text-red-900">${aging.days90.toFixed(2)}</div>
        </div>
        <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
          <div className="text-sm font-medium text-gray-600">Over 120 Days</div>
          <div className="text-xl font-bold text-gray-900">${aging.over90.toFixed(2)}</div>
        </div>
      </div>

      <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Customer</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Current</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">31-60</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">61-90</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">91-120</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">120+</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Total</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {customers.filter(c => c.totalOwed > 0).map((customer) => {
              const customerInvoices = invoices.filter(inv => inv.customerId === customer.id && inv.status !== 'paid');
              const customerAging = {current: 0, days30: 0, days60: 0, days90: 0, over90: 0};
              
              customerInvoices.forEach(invoice => {
                const days = invoice.daysOverdue || 0;
                if (days <= 0) customerAging.current += invoice.balance;
                else if (days <= 30) customerAging.days30 += invoice.balance;
                else if (days <= 60) customerAging.days60 += invoice.balance;
                else if (days <= 90) customerAging.days90 += invoice.balance;
                else customerAging.over90 += invoice.balance;
              });

              return (
                <tr key={customer.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4">
                    <div className="text-sm font-medium text-gray-900">{customer.name}</div>
                    <div className="text-xs text-gray-500">{customer.id}</div>
                  </td>
                  <td className="px-6 py-4 text-sm text-gray-900">${customerAging.current.toFixed(2)}</td>
                  <td className="px-6 py-4 text-sm text-gray-900">${customerAging.days30.toFixed(2)}</td>
                  <td className="px-6 py-4 text-sm text-gray-900">${customerAging.days60.toFixed(2)}</td>
                  <td className="px-6 py-4 text-sm text-gray-900">${customerAging.days90.toFixed(2)}</td>
                  <td className="px-6 py-4 text-sm text-gray-900">${customerAging.over90.toFixed(2)}</td>
                  <td className="px-6 py-4 text-sm font-medium text-gray-900">${customer.totalOwed.toFixed(2)}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );

  // Render Payments Tab
  const renderPaymentsTab = () => (
    <div>
      <div className="mb-6 flex justify-between items-center">
        <div className="flex items-center space-x-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
            <input
              type="text"
              placeholder="Search payments..."
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            />
          </div>
          <select className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent">
            <option value="all">All Methods</option>
            <option value="cash">Cash</option>
            <option value="check">Check</option>
            <option value="bank_transfer">Bank Transfer</option>
            <option value="credit_card">Credit Card</option>
          </select>
        </div>
        <button className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2">
          <Plus size={16} />
          <span>Record Payment</span>
        </button>
      </div>

      <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
        <table className="w-full">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Payment ID</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Customer</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Invoice</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Amount</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Method</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Reference</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-200">
            {payments.map((payment) => (
              <tr key={payment.id} className="hover:bg-gray-50">
                <td className="px-6 py-4 text-sm font-medium text-gray-900">{payment.id}</td>
                <td className="px-6 py-4 text-sm text-gray-900">{payment.customerName}</td>
                <td className="px-6 py-4 text-sm text-gray-900">{payment.invoiceId}</td>
                <td className="px-6 py-4 text-sm text-gray-900">{payment.date}</td>
                <td className="px-6 py-4 text-sm text-gray-900">${payment.amount.toFixed(2)}</td>
                <td className="px-6 py-4 text-sm text-gray-900">{payment.method.replace('_', ' ')}</td>
                <td className="px-6 py-4 text-sm text-gray-900">{payment.reference}</td>
                <td className="px-6 py-4">
                  <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                    payment.status === 'cleared' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                  }`}>
                    {payment.status}
                  </span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  return (
    <div className="max-w-7xl mx-auto p-6 bg-white">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2 flex items-center">
          <CreditCard className="mr-3 text-emerald-600" />
          Accounts Receivable
        </h1>
        <p className="text-gray-600">Manage customer invoicing, payments, and credit limits</p>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="bg-blue-50 p-6 rounded-lg border border-blue-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-blue-600 text-sm font-medium">Total A/R</p>
              <p className="text-2xl font-bold text-blue-900">${summary.totalAR.toFixed(2)}</p>
            </div>
            <DollarSign className="text-blue-600" size={24} />
          </div>
          <p className="text-sm text-blue-700 mt-2">{summary.customersWithBalance} customers with balances</p>
        </div>

        <div className="bg-red-50 p-6 rounded-lg border border-red-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-red-600 text-sm font-medium">Overdue Amount</p>
              <p className="text-2xl font-bold text-red-900">${summary.totalOverdue.toFixed(2)}</p>
            </div>
            <AlertTriangle className="text-red-600" size={24} />
          </div>
          <p className="text-sm text-red-700 mt-2">
            {summary.totalAR > 0 ? ((summary.totalOverdue / summary.totalAR) * 100).toFixed(1) : 0}% of total A/R
          </p>
        </div>

        <div className="bg-green-50 p-6 rounded-lg border border-green-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-green-600 text-sm font-medium">Avg. Days Outstanding</p>
              <p className="text-2xl font-bold text-green-900">{summary.avgDaysOutstanding}</p>
            </div>
            <Clock className="text-green-600" size={24} />
          </div>
          <p className="text-sm text-green-700 mt-2">Collection performance</p>
        </div>

        <div className="bg-purple-50 p-6 rounded-lg border border-purple-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-purple-600 text-sm font-medium">Active Customers</p>
              <p className="text-2xl font-bold text-purple-900">{customers.length}</p>
            </div>
            <Users className="text-purple-600" size={24} />
          </div>
          <p className="text-sm text-purple-700 mt-2">Total customer accounts</p>
        </div>
      </div>

      {/* Tabs */}
      <div className="mb-6">
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            {[
              { id: 'overview', name: 'Customer Overview', icon: Users },
              { id: 'invoices', name: 'Invoices', icon: FileText },
              { id: 'aging', name: 'Aging Report', icon: Clock },
              { id: 'payments', name: 'Payments', icon: CheckCircle }
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center py-2 px-1 border-b-2 font-medium text-sm ${
                  activeTab === tab.id
                    ? 'border-emerald-500 text-emerald-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <tab.icon className="mr-2" size={16} />
                {tab.name}
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Tab Content */}
      {activeTab === 'overview' && (
        <div>
          {/* Filters */}
          <div className="flex flex-wrap items-center gap-4 mb-6">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
              <input
                type="text"
                placeholder="Search customers..."
                value={filters.searchTerm}
                onChange={(e) => setFilters({ ...filters, searchTerm: e.target.value })}
                className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
              />
            </div>

            <select
              value={filters.customerType}
              onChange={(e) => setFilters({ ...filters, customerType: e.target.value })}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            >
              <option value="all">All Customer Types</option>
              <option value="corporate">Corporate</option>
              <option value="small_business">Small Business</option>
              <option value="credit">Credit</option>
              <option value="cash">Cash Only</option>
            </select>

            <select
              value={filters.status}
              onChange={(e) => setFilters({ ...filters, status: e.target.value })}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            >
              <option value="all">All Statuses</option>
              <option value="current">Current</option>
              <option value="overdue">Overdue</option>
              <option value="paid_up">Paid Up</option>
            </select>

            <button
              onClick={() => setShowInvoiceForm(true)}
              className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2"
            >
              <Plus size={16} />
              <span>New Invoice</span>
            </button>
          </div>

          {/* Customer List */}
          <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Customer
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Type
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Credit Limit
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Balance
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Overdue
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Credit Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Last Payment
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {loading ? (
                    <tr>
                      <td colSpan="8" className="px-6 py-4 text-center text-gray-500">
                        Loading data from API...
                      </td>
                    </tr>
                  ) : (
                    filteredCustomers.map((customer) => (
                      <tr key={customer.id} className="hover:bg-gray-50">
                        <td className="px-6 py-4">
                          <div>
                            <div className="text-sm font-medium text-gray-900">{customer.name}</div>
                            <div className="text-sm text-gray-500">{customer.id}</div>
                            <div className="text-xs text-gray-400 flex items-center mt-1">
                              <Mail className="mr-1" size={12} />
                              {customer.email}
                            </div>
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className="px-2 py-1 text-xs font-medium rounded-full bg-gray-100 text-gray-800">
                            {customer.customerType.replace('_', ' ')}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          ${customer.creditLimit.toLocaleString()}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">
                            ${customer.totalOwed.toFixed(2)}
                          </div>
                          <div className="text-xs text-gray-500">
                            {customer.invoiceCount} invoices
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          {customer.overdueAmount > 0 ? (
                            <span className="text-red-600 font-medium">
                              ${customer.overdueAmount.toFixed(2)}
                            </span>
                          ) : (
                            <span className="text-green-600">$0.00</span>
                          )}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${getCreditStatusColor(customer.creditStatus)}`}>
                            {customer.creditStatus.replace('_', ' ')}
                          </span>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                          {customer.lastPayment || 'N/A'}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                          <button
                            onClick={() => setSelectedCustomer(customer)}
                            className="text-emerald-600 hover:text-emerald-900 mr-3"
                          >
                            <Eye size={16} />
                          </button>
                          <button className="text-blue-600 hover:text-blue-900">
                            <Edit size={16} />
                          </button>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'invoices' && renderInvoicesTab()}
      {activeTab === 'aging' && renderAgingTab()}
      {activeTab === 'payments' && renderPaymentsTab()}

      {/* Customer Detail Modal */}
      {selectedCustomer && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="px-6 py-4 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">
                  Customer Details - {selectedCustomer.name}
                </h3>
                <button
                  onClick={() => setSelectedCustomer(null)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <XCircle size={20} />
                </button>
              </div>
            </div>
            
            <div className="px-6 py-4">
              {/* Customer Info Grid */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Customer ID</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.id}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Email</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.email}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Phone</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.phone}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Address</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.address}</p>
                  </div>
                </div>
                
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Customer Type</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.customerType.replace('_', ' ')}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Credit Limit</label>
                    <p className="text-sm text-gray-900">${selectedCustomer.creditLimit.toLocaleString()}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Payment Terms</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.paymentTerms} days</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Credit Status</label>
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getCreditStatusColor(selectedCustomer.creditStatus)}`}>
                      {selectedCustomer.creditStatus.replace('_', ' ')}
                    </span>
                  </div>
                </div>
              </div>

              {/* Financial Summary */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-blue-600">Current Balance</div>
                  <div className="text-xl font-bold text-blue-900">
                    ${selectedCustomer.totalOwed.toFixed(2)}
                  </div>
                </div>
                <div className="bg-red-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-red-600">Overdue Amount</div>
                  <div className="text-xl font-bold text-red-900">
                    ${selectedCustomer.overdueAmount.toFixed(2)}
                  </div>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-green-600">Credit Available</div>
                  <div className="text-xl font-bold text-green-900">
                    ${(selectedCustomer.creditLimit - selectedCustomer.totalOwed).toFixed(2)}
                  </div>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-purple-600">Avg Payment Days</div>
                  <div className="text-xl font-bold text-purple-900">
                    {selectedCustomer.avgPaymentDays}
                  </div>
                </div>
              </div>

              {/* Customer Invoices */}
              <div className="mb-6">
                <h4 className="text-md font-semibold text-gray-900 mb-3">Customer Invoices</h4>
                <div className="overflow-x-auto">
                  <table className="w-full border border-gray-200 rounded">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Invoice</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Date</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Due Date</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Amount</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Balance</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Status</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200">
                      {invoices
                        .filter(inv => inv.customerId === selectedCustomer.id)
                        .map((invoice) => (
                          <tr key={invoice.id}>
                            <td className="px-4 py-2 text-sm text-gray-900">{invoice.id}</td>
                            <td className="px-4 py-2 text-sm text-gray-900">{invoice.date}</td>
                            <td className="px-4 py-2 text-sm text-gray-900">{invoice.dueDate}</td>
                            <td className="px-4 py-2 text-sm text-gray-900 text-right">${invoice.amount.toFixed(2)}</td>
                            <td className="px-4 py-2 text-sm text-gray-900 text-right">${invoice.balance.toFixed(2)}</td>
                            <td className="px-4 py-2">
                              <span className={`px-2 py-1 text-xs font-medium rounded-full ${getInvoiceStatusColor(invoice.status)}`}>
                                {invoice.status}
                              </span>
                            </td>
                          </tr>
                        ))}
                    </tbody>
                  </table>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex space-x-3">
                <button className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2">
                  <Plus size={16} />
                  <span>New Invoice</span>
                </button>
                <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center space-x-2">
                  <CheckCircle size={16} />
                  <span>Record Payment</span>
                </button>
                <button className="px-4 py-2 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700 flex items-center space-x-2">
                  <Mail size={16} />
                  <span>Send Statement</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Invoice Detail Modal */}
      {viewInvoice && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-3xl w-full max-h-[90vh] overflow-y-auto">
            <div className="px-6 py-4 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">
                  Invoice Details - {viewInvoice.id}
                </h3>
                <button
                  onClick={() => setViewInvoice(null)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <XCircle size={20} />
                </button>
              </div>
            </div>
            
            <div className="px-6 py-4">
              {/* Invoice Header */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                <div>
                  <h4 className="font-semibold text-gray-900 mb-3">Invoice Information</h4>
                  <div className="space-y-2">
                    <div><span className="text-gray-500">Invoice ID:</span> {viewInvoice.id}</div>
                    <div><span className="text-gray-500">Date:</span> {viewInvoice.date}</div>
                    <div><span className="text-gray-500">Due Date:</span> {viewInvoice.dueDate}</div>
                    <div><span className="text-gray-500">Payment Terms:</span> {viewInvoice.paymentTerms} days</div>
                    <div><span className="text-gray-500">Source:</span> {viewInvoice.source}</div>
                  </div>
                </div>
                <div>
                  <h4 className="font-semibold text-gray-900 mb-3">Customer Information</h4>
                  <div className="space-y-2">
                    <div><span className="text-gray-500">Customer:</span> {viewInvoice.customerName}</div>
                    <div><span className="text-gray-500">Customer ID:</span> {viewInvoice.customerId}</div>
                  </div>
                </div>
              </div>

              {/* Invoice Items */}
              <div className="mb-6">
                <h4 className="font-semibold text-gray-900 mb-3">Invoice Items</h4>
                <div className="overflow-x-auto">
                  <table className="w-full border border-gray-200 rounded">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Description</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Quantity</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Price</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Total</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200">
                      {viewInvoice.items.map((item, index) => (
                        <tr key={index}>
                          <td className="px-4 py-2 text-sm text-gray-900">{item.description}</td>
                          <td className="px-4 py-2 text-sm text-gray-900 text-right">{item.quantity}</td>
                          <td className="px-4 py-2 text-sm text-gray-900 text-right">${item.price.toFixed(2)}</td>
                          <td className="px-4 py-2 text-sm text-gray-900 text-right">${item.total.toFixed(2)}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>

          {/* Invoice Summary */}
<div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
  <div className="bg-blue-50 p-4 rounded-lg">
    <div className="text-sm font-medium text-blue-600">Invoice Amount</div>
    <div className="text-xl font-bold text-blue-900">${viewInvoice.amount.toFixed(2)}</div>
  </div>
  <div className="bg-green-50 p-4 rounded-lg">
    <div className="text-sm font-medium text-green-600">Amount Paid</div>
    <div className="text-xl font-bold text-green-900">${viewInvoice.paid.toFixed(2)}</div>
  </div>
  <div className="bg-red-50 p-4 rounded-lg">
    <div className="text-sm font-medium text-red-600">Balance Due</div>
    <div className="text-xl font-bold text-red-900">${viewInvoice.balance.toFixed(2)}</div>
  </div>
</div>

{/* Invoice Status and Days Information */}
<div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
  <div className="bg-gray-50 p-4 rounded-lg">
    <div className="text-sm font-medium text-gray-600">Invoice Status</div>
    <span className={`inline-block mt-1 px-3 py-1 text-sm font-medium rounded-full ${getInvoiceStatusColor(viewInvoice.status)}`}>
      {viewInvoice.status.charAt(0).toUpperCase() + viewInvoice.status.slice(1)}
    </span>
  </div>
  <div className="bg-gray-50 p-4 rounded-lg">
    <div className="text-sm font-medium text-gray-600">
      {viewInvoice.status === 'overdue' ? 'Days Overdue' : 'Days Until Due'}
    </div>
    <div className={`text-xl font-bold ${viewInvoice.status === 'overdue' ? 'text-red-900' : 'text-gray-900'}`}>
      {viewInvoice.status === 'overdue' ? viewInvoice.daysOverdue : 
       Math.max(0, Math.ceil((new Date(viewInvoice.dueDate) - new Date()) / (1000 * 60 * 60 * 24)))}
    </div>
  </div>
</div>

{/* Notes */}
{viewInvoice.notes && (
  <div className="mb-6">
    <h4 className="font-semibold text-gray-900 mb-2">Notes</h4>
    <p className="text-sm text-gray-700 bg-gray-50 p-3 rounded border">{viewInvoice.notes}</p>
  </div>
)}

{/* Payment History for this Invoice */}
<div className="mb-6">
  <h4 className="font-semibold text-gray-900 mb-3">Payment History</h4>
  {payments.filter(payment => payment.invoiceId === viewInvoice.id).length > 0 ? (
    <div className="overflow-x-auto">
      <table className="w-full border border-gray-200 rounded">
        <thead className="bg-gray-50">
          <tr>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Date</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Amount</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Method</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Reference</th>
            <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Status</th>
          </tr>
        </thead>
        <tbody className="divide-y divide-gray-200">
          {payments
            .filter(payment => payment.invoiceId === viewInvoice.id)
            .map((payment) => (
              <tr key={payment.id}>
                <td className="px-4 py-2 text-sm text-gray-900">{payment.date}</td>
                <td className="px-4 py-2 text-sm text-gray-900">${payment.amount.toFixed(2)}</td>
                <td className="px-4 py-2 text-sm text-gray-900">{payment.method.replace('_', ' ')}</td>
                <td className="px-4 py-2 text-sm text-gray-900">{payment.reference}</td>
                <td className="px-4 py-2">
                  <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                    payment.status === 'cleared' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                  }`}>
                    {payment.status}
                  </span>
                </td>
              </tr>
            ))}
        </tbody>
      </table>
    </div>
  ) : (
    <div className="text-sm text-gray-500 bg-gray-50 p-4 rounded border text-center">
      No payments recorded for this invoice
    </div>
  )}
</div>

{/* Customer Credit Information */}
<div className="mb-6 bg-blue-50 p-4 rounded-lg border border-blue-200">
  <h4 className="font-semibold text-blue-900 mb-3">Customer Credit Information</h4>
  <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
    <div>
      <span className="text-blue-700 font-medium">Credit Limit:</span>
      <div className="text-blue-900 font-bold">
        ${customers.find(c => c.id === viewInvoice.customerId)?.creditLimit.toLocaleString() || 'N/A'}
      </div>
    </div>
    <div>
      <span className="text-blue-700 font-medium">Total Outstanding:</span>
      <div className="text-blue-900 font-bold">
        ${customers.find(c => c.id === viewInvoice.customerId)?.totalOwed.toFixed(2) || '0.00'}
      </div>
    </div>
    <div>
      <span className="text-blue-700 font-medium">Credit Available:</span>
      <div className="text-blue-900 font-bold">
        ${((customers.find(c => c.id === viewInvoice.customerId)?.creditLimit || 0) - 
            (customers.find(c => c.id === viewInvoice.customerId)?.totalOwed || 0)).toFixed(2)}
      </div>
    </div>
  </div>
</div>
{/* Action Buttons */}
<div className="flex flex-wrap gap-3">
  <button className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2 transition-colors">
    <CheckCircle size={16} />
    <span>Record Payment</span>
  </button>
  <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center space-x-2 transition-colors">
    <Mail size={16} />
    <span>Email Invoice</span>
  </button>
  <button className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 flex items-center space-x-2 transition-colors">
    <Printer size={16} />
    <span>Print Invoice</span>
  </button>
  <button className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 flex items-center space-x-2 transition-colors">
    <Download size={16} />
    <span>Download PDF</span>
  </button>
  <button className="px-4 py-2 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700 flex items-center space-x-2 transition-colors">
    <Send size={16} />
    <span>Send Reminder</span>
  </button>
  {viewInvoice.balance > 0 && (
    <button className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 flex items-center space-x-2 transition-colors">
      <AlertTriangle size={16} />
      <span>Mark Overdue</span>
    </button>
  )}
</div>
            </div>
          </div>
        </div>
      )}

      {/* New Invoice Form */}
      {showInvoiceForm && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="px-6 py-4 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">Create New Invoice</h3>
                <button
                  onClick={() => setShowInvoiceForm(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <XCircle size={20} />
                </button>
              </div>
            </div>
            
            <div className="px-6 py-4">
              {/* Invoice Header */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Customer *</label>
                  <select
                    value={newInvoice.customerId}
                    onChange={(e) => setNewInvoice({ ...newInvoice, customerId: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
                  >
                    <option value="">Select Customer</option>
                    {customers.filter(c => c.customerType !== 'cash').map(customer => (
                      <option key={customer.id} value={customer.id}>
                        {customer.name} ({customer.id})
                      </option>
                    ))}
                  </select>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Due Date *</label>
                  <input
                    type="date"
                    value={newInvoice.dueDate}
                    onChange={(e) => setNewInvoice({ ...newInvoice, dueDate: e.target.value })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
                  />
                </div>
              </div>

              {/* Invoice Items */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Invoice Items</label>
                <div className="space-y-3">
                  {newInvoice.items.map((item, index) => (
                    <div key={index} className="grid grid-cols-12 gap-2 items-center bg-gray-50 p-3 rounded">
                      <div className="col-span-5">
                        <input
                          type="text"
                          value={item.description}
                          onChange={(e) => updateInvoiceItem(index, 'description', e.target.value)}
                          placeholder="Item description"
                          className="w-full px-2 py-1 border border-gray-300 rounded text-sm focus:ring-2 focus:ring-emerald-500"
                        />
                      </div>
                      <div className="col-span-2">
                        <input
                          type="number"
                          value={item.quantity}
                          onChange={(e) => updateInvoiceItem(index, 'quantity', parseFloat(e.target.value) || 0)}
                          placeholder="Qty"
                          className="w-full px-2 py-1 border border-gray-300 rounded text-sm focus:ring-2 focus:ring-emerald-500"
                        />
                      </div>
                      <div className="col-span-2">
                        <input
                          type="number"
                          step="0.01"
                          value={item.price}
                          onChange={(e) => updateInvoiceItem(index, 'price', parseFloat(e.target.value) || 0)}
                          placeholder="Price"
                          className="w-full px-2 py-1 border border-gray-300 rounded text-sm focus:ring-2 focus:ring-emerald-500"
                        />
                      </div>
                      <div className="col-span-2">
                        <input
                          type="number"
                          step="0.01"
                          value={item.total}
                          readOnly
                          className="w-full px-2 py-1 border border-gray-300 rounded text-sm bg-gray-100"
                        />
                      </div>
                      <div className="col-span-1">
                        {newInvoice.items.length > 1 && (
                          <button
                            onClick={() => {
                              const updatedItems = newInvoice.items.filter((_, i) => i !== index);
                              setNewInvoice({ ...newInvoice, items: updatedItems });
                            }}
                            className="p-1 text-red-600 hover:bg-red-100 rounded"
                          >
                            <XCircle size={14} />
                          </button>
                        )}
                      </div>
                    </div>
                  ))}
                </div>

                <div className="flex justify-between items-center mt-3">
                  <button
                    onClick={addInvoiceItem}
                    className="px-3 py-1 text-sm bg-emerald-100 text-emerald-700 rounded hover:bg-emerald-200 flex items-center space-x-1"
                  >
                    <Plus size={14} />
                    <span>Add Item</span>
                  </button>

                  <div className="text-lg font-semibold">
                    Total: ${calculateInvoiceTotal().toFixed(2)}
                  </div>
                </div>
              </div>

              {/* Notes */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-1">Notes</label>
                <textarea
                  value={newInvoice.notes}
                  onChange={(e) => setNewInvoice({ ...newInvoice, notes: e.target.value })}
                  rows={3}
                  placeholder="Additional notes..."
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
                />
              </div>

              {/* Action Buttons */}
              <div className="flex space-x-3">
                <button
                  onClick={handleCreateInvoice}
                  className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2"
                >
                  <CheckCircle size={16} />
                  <span>Create Invoice</span>
                </button>
                <button
                  onClick={() => setShowInvoiceForm(false)}
                  className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 flex items-center space-x-2"
                >
                  <XCircle size={16} />
                  <span>Cancel</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Integration Status */}
      <div className="mt-8 p-6 bg-green-50 border border-green-200 rounded-lg">
        <div className="flex items-center mb-4">
          <CheckCircle className="text-green-600 mr-2" size={20} />
          <h3 className="font-medium text-green-800">POS Integration Active</h3>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm text-green-700">
          <div>
            <strong>Credit Sales:</strong> Automatically create AR entries when customers buy on credit in POS
          </div>
          <div>
            <strong>Customer Limits:</strong> POS checks credit limits before allowing credit sales
          </div>
          <div>
            <strong>Real-time Updates:</strong> Customer balances update instantly with each transaction
          </div>
        </div>
      </div>
    </div>
  );
};

export default AccountsReceivable;
