import React, { useState, useEffect } from 'react';
import { 
  Users, 
  DollarSign, 
  CreditCard, 
  AlertTriangle, 
  Clock, 
  Plus, 
  Search, 
  Filter, 
  Eye, 
  Edit, 
  Download,
  CheckCircle,
  XCircle,
  Calendar,
  FileText,
  TrendingDown,
  Mail,
  Phone,
  Loader,
  RefreshCw,
  Package
} from 'lucide-react';

// API Configuration for Accounts Payable
const API_PURCHASE_ORDERS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/PurchaseOrderManagement/purchaseOrdersWithoutDetails';
const API_VENDORS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/VendorManagement/vendors';
const API_PRODUCTS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/products?status=All&start=0&size=200';

// Replace with your actual Bearer token
const BEARER_TOKEN = 'your-bearer-token-here';

const AccountsPayable = () => {
  // State management
  const [purchaseOrders, setPurchaseOrders] = useState({ loading: false, data: [], error: null });
  const [vendors, setVendors] = useState({ loading: false, data: [], error: null });
  const [products, setProducts] = useState({ loading: false, data: [], error: null });
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedVendor, setSelectedVendor] = useState(null);
  const [selectedPO, setSelectedPO] = useState(null);
  const [viewBill, setViewBill] = useState(null);
  const [filters, setFilters] = useState({
    vendorType: 'all',
    status: 'all',
    dateRange: '30',
    searchTerm: ''
  });

const createVendorPOMapping = (purchaseOrders, vendors) => {
  console.log('Starting createVendorPOMapping function');
  console.log('Input vendors:', vendors);
  console.log('Input purchaseOrders:', purchaseOrders);
  
  const vendorPOMap = new Map();
  
  // Only initialize vendors with valid names
  if (vendors && vendors.data && Array.isArray(vendors.data)) {
    console.log(`Processing ${vendors.data.length} vendors`);
    
    let validVendorCount = 0;
    let invalidVendorCount = 0;
    
    vendors.data.forEach(vendor => {
      // Check if vendor has a valid name (not null, undefined, or empty)
      if (vendor.name && vendor.name.trim() !== '') {
        vendorPOMap.set(vendor.id, {
          vendor,
          purchaseOrders: []
        });
        validVendorCount++;
        console.log(`Added vendor: ID=${vendor.id}, Name=${vendor.name}`);
      } else {
        invalidVendorCount++;
        console.log(`Skipped vendor with invalid name: ID=${vendor.id}, Name=${vendor.name}`);
      }
    });
    
    console.log(`Valid vendors added: ${validVendorCount}`);
    console.log(`Invalid vendors skipped: ${invalidVendorCount}`);
  } else {
    console.warn('No valid vendors data found');
  }
  
  // Process purchase orders and assign them to vendors with valid names
  if (purchaseOrders && purchaseOrders.data && Array.isArray(purchaseOrders.data)) {
    console.log(`Processing ${purchaseOrders.data.length} purchase orders`);
    
    let assignedPOCount = 0;
    let skippedPOCount = 0;
    
    purchaseOrders.data.forEach(po => {
      // Only process POs with a valid vendor_Id that maps to a vendor with a name
      if (po.vendor_Id && vendorPOMap.has(po.vendor_Id)) {
        vendorPOMap.get(po.vendor_Id).purchaseOrders.push(po);
        assignedPOCount++;
        console.log(`Assigned PO ${po.id || po.POID} to vendor ${po.vendor_Id}`);
      } else {
        skippedPOCount++;
        console.log(`Skipped PO ${po.id || po.POID}, vendor_Id=${po.vendor_Id} (not found in valid vendors)`);
      }
    });
    
    console.log(`POs assigned to vendors: ${assignedPOCount}`);
    console.log(`POs skipped: ${skippedPOCount}`);
  } else {
    console.warn('No valid purchase orders data found');
  }
  
  // Log vendor stats after assignment
  console.log('\nVendor statistics after PO assignment:');
  let vendorsWithPOs = 0;
  let vendorsWithoutPOs = 0;
  let totalPOsAssigned = 0;
  
  for (const [key, value] of vendorPOMap.entries()) {
    const poCount = value.purchaseOrders.length;
    totalPOsAssigned += poCount;
    
    if (poCount > 0) {
      vendorsWithPOs++;
      console.log(`Vendor ${key} (${value.vendor.name}) has ${poCount} purchase orders`);
    } else {
      vendorsWithoutPOs++;
    }
  }
  
  console.log(`Vendors with POs: ${vendorsWithPOs}`);
  console.log(`Vendors without POs: ${vendorsWithoutPOs}`);
  console.log(`Total POs assigned: ${totalPOsAssigned}`);
  
  // Convert the map to an array of vendor-PO pairs
  const result = Array.from(vendorPOMap.values());
  console.log(`\nFinal result: ${result.length} vendor entries with their purchase orders`);
  
  return result;
};

  // API Functions
  const fetchProducts = async () => {
    setProducts(prev => ({ ...prev, loading: true, error: null }));
    try {
      const response = await fetch(API_PRODUCTS, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${BEARER_TOKEN}`,
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      
      // Store products data for reference
      window.productsData = data;
      
      setProducts({ loading: false, data: data, error: null });
    } catch (error) {
      console.error('Error fetching products:', error);
      // Use sample product data if API fails
      const sampleProducts = [
        {
          id: "1",
          name: "IPHONE 15 PRO MAX",
          description: "6.1 INCH",
          costPrice: 180000,
          price: 210000,
          quantity: 49,
          status: "Active"
        },
        {
          id: "2",
          name: "LAPTOP",
          description: "14 INCH",
          costPrice: 45000,
          price: 50000,
          quantity: 55,
          status: "Active"
        },
        {
          id: "3",
          name: "AIR PODS",
          description: "BOOSTER",
          costPrice: 5000,
          price: 8000,
          quantity: 14,
          status: "Active"
        },
        {
          id: "4",
          name: "LED",
          description: "40 INCH",
          costPrice: 75000,
          price: 90000,
          quantity: 37,
          status: "Active"
        }
      ];
      window.productsData = sampleProducts;
      setProducts({ loading: false, data: sampleProducts, error: null });
    }
  };

  const fetchPurchaseOrders = async () => {
    setPurchaseOrders(prev => ({ ...prev, loading: true, error: null }));
    try {
      const response = await fetch(API_PURCHASE_ORDERS, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${BEARER_TOKEN}`,
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const allData = await response.json();
      
      // Store all data for vendor calculations
      window.allPurchaseOrdersData = allData;
      
      // Show only records that have pending status
      const filteredPOs = allData.filter(po => 
        po.status === 'pending'
      );
      
      setPurchaseOrders({ loading: false, data: filteredPOs, error: null });
    } catch (error) {
      console.error('Error fetching purchase orders:', error);
      setPurchaseOrders({ loading: false, data: [], error: error.message });
    }
  };

  const fetchVendors = async () => {
    setVendors(prev => ({ ...prev, loading: true, error: null }));
    try {
      const response = await fetch(API_VENDORS, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${BEARER_TOKEN}`,
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      
      // Use all purchase orders data for vendor calculations
      const allPOData = window.allPurchaseOrdersData || [];
      
      // Transform API vendors to match our structure with real calculated data
      const transformedVendors = data.map(vendor => {
        // Extract vendor details from complex API structure
        const vendorDetails = vendor.individuals && vendor.individuals.length > 0 
          ? vendor.individuals[0] 
          : vendor.organizations && vendor.organizations.length > 0 
            ? vendor.organizations[0] 
            : null;
        
        const contactInfo = vendorDetails?.contactMediums?.[0]?.mediumCharacteristics?.[0] || {};
        
        // Get ALL purchase orders for this vendor
        const vendorPOs = allPOData.filter(po => 
          po.vendor_Id === vendor.id || 
          po.Vendor_Id === vendor.id
        );
        
        // Calculate total payable from ALL pending purchase orders
        const totalPayable = vendorPOs
          .filter(po => po.status === 'pending')
          .reduce((sum, po) => {
            const poTotal = po.purchaseOrderItems.reduce((itemSum, item) => 
              itemSum + (item.totalPrice || 0), 0);
            return sum + poTotal;
          }, 0);
        
        // Overdue amount = total payable (since all pending POs need attention)
        const overdueAmount = totalPayable;
        
        // Total number of purchase orders for this vendor
        const billCount = vendorPOs.length;
        
        // Determine vendor type based on spending pattern
        let vendorType = 'supplies';
        const totalSpentFromPOs = vendorPOs.reduce((sum, po) => {
          const poTotal = po.purchaseOrderItems.reduce((itemSum, item) => 
            itemSum + (item.totalPrice || 0), 0);
          return sum + poTotal;
        }, 0);
        
        if (totalSpentFromPOs > 5000000) {
          vendorType = 'equipment';
        } else if (totalSpentFromPOs > 1000000) {
          vendorType = 'supplies';
        } else {
          vendorType = 'office';
        }
        
        // Set credit limit based on vendor type
        let creditLimit = 500000; // Default
        if (vendorType === 'equipment') {
          creditLimit = 2000000;
        } else if (vendorType === 'supplies') {
          creditLimit = 1000000;
        }
        
        // Determine credit status based on payable amount
        let creditStatus = 'good';
        if (totalPayable === 0) {
          creditStatus = 'excellent';
        } else if (totalPayable > creditLimit * 0.9) {
          creditStatus = 'hold';
        } else if (totalPayable > creditLimit * 0.7) {
          creditStatus = 'watch';
        }

        return {
          id: vendor.id,
          name: vendorDetails?.fullName || vendorDetails?.name || `Vendor ${vendor.id}`,
          email: contactInfo.emailAddress || 'No email provided',
          phone: contactInfo.number || 'No phone provided',
          address: `${contactInfo.street1 || ''} ${contactInfo.city || ''} ${contactInfo.country || ''}`.trim() || 'No address provided',
          vendorType: vendorType,
          creditLimit: creditLimit,
          paymentTerms: vendorType === 'equipment' ? 45 : (vendorType === 'supplies' ? 30 : 15),
          totalPayable: totalPayable,
          overdueAmount: overdueAmount,
          lastPayment: 'No payments',
          creditStatus: creditStatus,
          accountBalance: totalPayable,
          billCount: billCount,
          avgPaymentDays: 30,
          status: vendor.status || 'active',
          type: vendor.type || 'Individual'
        };
      });
      
      // Also add vendors with null vendor_Id as separate entries
      const nullVendorPOs = allPOData.filter(po => 
        !po.vendor_Id || po.vendor_Id === 'null' || 
        !po.Vendor_Id || po.Vendor_Id === 'null'
      );
      
      nullVendorPOs.forEach((po, index) => {
        const vendorNumber = index + 1;
        const poTotal = po.purchaseOrderItems.reduce((sum, item) => 
          sum + (item.totalPrice || 0), 0);
        const vendorTotalPayable = po.status === 'pending' ? poTotal : 0;
   
      });
      
      setVendors({ loading: false, data: transformedVendors, error: null });
    } catch (error) {
      console.error('Error fetching vendors:', error);
      setVendors({ loading: false, data: [], error: error.message });
    }
  };

  // Helper function to get product details by ID
  const getProductDetails = (productId) => {
    if (!productId) return { name: 'Unknown Product', description: '', price: 0 };
    
    const product = (window.productsData || products.data || []).find(p => 
      p.id === productId || p.id === String(productId)
    );
    
    if (product) {
      return {
        name: product.name || 'Unknown Product',
        description: product.description || '',
        price: product.price || 0,
        costPrice: product.costPrice || 0,
        quantity: product.quantity || 0
      };
    }
    
    return { name: `Product #${productId}`, description: '', price: 0 };
  };

