      console.log(`PO ${po.POID || po.id} total: ${poTotal}`);
      return sum + poTotal;
    }, 0);
    
    console.log(`Vendor ${vendor.name} total: ${vendorTotal}`);
    
    totalAP += vendorTotal;
    
    // For overdue calculation, we'll use a simple logic
    // You can modify this based on your actual overdue calculation
    const overdueAmount = vendorTotal * 0.1; // Example: 10% of total is overdue
    totalOverdue += overdueAmount;
    
