import React, { useState } from 'react';
import CustomerFilters from './CustomerFilters';
import CustomerTable from './CustomerTable';
import CustomerDetailModal from './CustomerDetailModal';
import CreateInvoiceModal from './CreateInvoiceModal';

const CustomerOverview = ({ customers, invoices, filters, setFilters }) => {
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [showInvoiceForm, setShowInvoiceForm] = useState(false);

  const getFilteredCustomers = () => {
    let filtered = [...customers];

    if (filters.customerType !== 'all') {
      filtered = filtered.filter(customer => customer.customerType === filters.customerType);
    }

    if (filters.status !== 'all') {
      filtered = filtered.filter(customer => {
        if (filters.status === 'overdue') return customer.overdueAmount > 0;
        if (filters.status === 'current') return customer.totalOwed > 0 && customer.overdueAmount === 0;
        if (filters.status === 'paid_up') return customer.totalOwed === 0;
        return true;
      });
    }

    if (filters.searchTerm) {
      const term = filters.searchTerm.toLowerCase();
      filtered = filtered.filter(customer =>
        customer.name.toLowerCase().includes(term) ||
        customer.email.toLowerCase().includes(term) ||
        customer.id.toLowerCase().includes(term)
      );
    }

    return filtered;
  };

  const filteredCustomers = getFilteredCustomers();

  return (
    <div>
      <CustomerFilters 
        filters={filters} 
        setFilters={setFilters}
        onNewInvoice={() => setShowInvoiceForm(true)}
      />
      
      <CustomerTable 
        customers={filteredCustomers}
        onSelectCustomer={setSelectedCustomer}
      />

      {selectedCustomer && (
        <CustomerDetailModal
          customer={selectedCustomer}
          invoices={invoices}
          onClose={() => setSelectedCustomer(null)}
        />
      )}

      {showInvoiceForm && (
        <CreateInvoiceModal
          customers={customers}
          onClose={() => setShowInvoiceForm(false)}
          onCreate={(invoice) => {
            // Handle invoice creation
            console.log('Create invoice:', invoice);
            setShowInvoiceForm(false);
          }}
        />
      )}
    </div>
  );
};

export default CustomerOverview;
