import React from 'react';
import { Search, RefreshCw, Eye } from 'lucide-react';
import { 
  combineVendorsWithPurchaseOrders, 
  getProductDetails, 
  getPOStatusColor, 
  getPOOverallStatus,
  formatCurrency 
} from '../../utils/apCalculations';

const APOverviewTab = ({ 
  filters, 
  setFilters, 
  filteredVendors, 
  purchaseOrders, 
  onVendorSelect, 
  onPOSelect, 
  onRefresh 
}) => {
  // Use ALL purchase orders (including applied ones) to show complete vendor information
  const vendorsWithPOs = combineVendorsWithPurchaseOrders({ data: filteredVendors }, purchaseOrders.data);

  return (
    <div>
      {/* Filters */}
      <div className="flex flex-wrap items-center gap-4 mb-6">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
          <input
            type="text"
            placeholder="Search vendors..."
            value={filters.searchTerm}
            onChange={(e) => setFilters({ ...filters, searchTerm: e.target.value })}
            className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-rose-500 focus:border-transparent"
          />
        </div>

        <select
          value={filters.vendorType}
          onChange={(e) => setFilters({ ...filters, vendorType: e.target.value })}
          className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-rose-500 focus:border-transparent"
        >
          <option value="all">All Vendor Types</option>
          <option value="equipment">Equipment</option>
          <option value="supplies">Supplies</option>
          <option value="office">Office</option>
          <option value="unknown">Unknown</option>
        </select>

        <select
          value={filters.status}
          onChange={(e) => setFilters({ ...filters, status: e.target.value })}
          className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-rose-500 focus:border-transparent"
        >
          <option value="all">All Statuses</option>
          <option value="current">Current</option>
          <option value="overdue">Pending</option>
          <option value="paid_up">Completed</option>
        </select>

        <button
          onClick={onRefresh}
          className="px-4 py-2 bg-rose-600 text-white rounded-lg hover:bg-rose-700 flex items-center space-x-2"
        >
          <RefreshCw size={16} />
          <span>Refresh Data</span>
        </button>
      </div>

      {/* Vendor List */}
      <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Vendor
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Products
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Total Amount
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Remarks
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {vendorsWithPOs.map((vendor) => {
                const hasPos = vendor.purchaseOrders && vendor.purchaseOrders.length > 0;
                
                // Calculate vendor total - only from pending items using quantity × unit price
                const vendorTotal = hasPos 
                  ? vendor.purchaseOrders.reduce((sum, po) => {
                      const overallStatus = getPOOverallStatus(po);
                      
                      if (overallStatus === 'applied') {
                        return sum; // Skip fully applied POs
                      } else if (overallStatus === 'partial') {
                        // For partial POs, only calculate pending items using quantity × unit price
                        const pendingItemsTotal = po.purchaseOrderItems.reduce((itemSum, item) => {
                          if (item.status !== 'applied') {
                            const itemTotal = (item.quantity || 0) * (item.pricePerUnit || 0);
                            return itemSum + itemTotal;
                          }
                          return itemSum;
                        }, 0);
                        return sum + pendingItemsTotal;
                      } else {
                        // For fully pending POs, calculate all items using quantity × unit price
                        const poTotal = po.purchaseOrderItems.reduce((itemSum, item) => {
                          const itemTotal = (item.quantity || 0) * (item.pricePerUnit || 0);
                          return itemSum + itemTotal;
                        }, 0);
                        return sum + poTotal;
                      }
                    }, 0)
                  : 0;
                
                // Collect all products from all POs for this vendor
                const allProducts = [];
                if (hasPos) {
                  vendor.purchaseOrders.forEach(po => {
                    if (po.purchaseOrderItems) {
                      po.purchaseOrderItems.forEach(item => {
                        const product = getProductDetails(item.product_Id);
                        allProducts.push({
                          poId: po.POID,
                          name: product?.name || "Unknown Product",
                          quantity: item.quantity,
                          description: product?.description
                        });
                      });
                    }
                  });
                }
                
                // Get all statuses from vendor's POs - use the overall status based on items
                const statusCounts = {};
                if (hasPos) {
                  vendor.purchaseOrders.forEach(po => {
                    const overallStatus = getPOOverallStatus(po);
                    statusCounts[overallStatus] = (statusCounts[overallStatus] || 0) + 1;
                  });
                }
                
                return (
                  <tr key={vendor.id} className="hover:bg-gray-50">
                    {/* Vendor */}
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="text-sm font-medium text-gray-900">
                        {vendor.name || "Unknown Vendor"}
                      </div>
                      <div className="text-xs text-gray-500">
                        {hasPos ? vendor.purchaseOrders.map(po => `PO-${po.POID}`).join(', ') : "No POs"}
                      </div>
                    </td>
                    
                    {/* Products - All in one cell */}
                    <td className="px-6 py-4">
                      {hasPos ? (
                        <div className="text-sm text-gray-900">
                          {allProducts.map((product, index) => (
                            <div key={index} className="mb-1">
                              <span className="font-medium">{product.name}</span>
                              <span className="text-gray-500 ml-2">x{product.quantity}</span>
                              {product.description && (
                                <span className="text-xs text-gray-400 ml-2">
                                  ({product.description})
                                </span>
                              )}
                              <span className="text-xs text-gray-500 ml-2">
                                [PO-{product.poId}]
                              </span>
                            </div>
                          ))}
                        </div>
                      ) : (
                        <div className="text-sm text-gray-500">No purchase orders</div>
                      )}
                    </td>
                    
                    {/* Total Amount */}
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                      {formatCurrency(vendorTotal)}
                    </td>
                    
                    {/* Status - Show all statuses */}
                    <td className="px-6 py-4 whitespace-nowrap">
                      {!hasPos ? (
                        <span className="px-2 py-1 text-xs font-medium rounded-full bg-gray-100 text-gray-800">
                          No Orders
                        </span>
                      ) : (
                        <div className="flex flex-wrap gap-1">
                          {/* Display unique statuses with counts */}
                          {Object.entries(statusCounts).map(([status, count], index) => (
                            <span 
                              key={index} 
                              className={`px-2 py-1 text-xs font-medium rounded-full ${getPOStatusColor(status)}`}
                            >
                              {status} {count > 1 ? `(${count})` : ''}
                            </span>
                          ))}
                        </div>
                      )}
                    </td>
                    
                    {/* Remarks - Combined */}
                    <td className="px-6 py-4 text-sm text-gray-900">
                      {hasPos ? (
                        <div>
                          {vendor.purchaseOrders.map((po, index) => (
                            <div key={index} className="mb-1">
                              {po.remarks ? (
                                <span>PO-{po.POID}: {po.remarks}</span>
                              ) : (
                                <span className="text-gray-500">PO-{po.POID}: No remarks</span>
                              )}
                            </div>
                          ))}
                        </div>
                      ) : (
                        <span className="text-gray-500">-</span>
                      )}
                    </td>
                    
                    {/* Actions */}
                    <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                      {!hasPos ? (
                        <button
                          className="text-gray-400 cursor-not-allowed"
                          title="No actions available"
                          disabled
                        >
                          <Eye size={16} />
                        </button>
                      ) : (
                        <div className="flex flex-wrap gap-2">
                          <button
                            onClick={() => onVendorSelect(vendor)}
                            className="text-blue-600 hover:text-blue-900 mr-1"
                            title="View Vendor Details"
                          >
                            <Eye size={16} />
                          </button>
                          {vendor.purchaseOrders.map((po, index) => (
                            <div key={index} className="flex items-center">
                              <span className="text-xs text-gray-500 mr-1">PO-{po.POID}:</span>
                              <button
                                onClick={() => onPOSelect(po)}
                                className="text-rose-600 hover:text-rose-900 mr-1"
                                title={`View PO-${po.POID} Details`}
                              >
                                <Eye size={16} />
                              </button>
                            </div>
                          ))}
                        </div>
                      )}
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>

        {filteredVendors.length === 0 && (
          <div className="text-center py-12">
            <div className="text-gray-500 text-lg">
              {hasActiveFilters ? 'No vendors match your filters' : 'No vendors found'}
            </div>
            <p className="text-gray-400 mt-2">
              {hasActiveFilters ? 'Try adjusting your search criteria' : 'No vendor data available'}
            </p>
            {hasActiveFilters && (
              <button
                onClick={() => setFilters({
                  vendorType: 'all',
                  status: 'all',
                  amountRange: 'all',
                  searchTerm: ''
                })}
                className="mt-4 px-4 py-2 bg-rose-600 text-white rounded-lg hover:bg-rose-700"
              >
                Clear All Filters
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default APOverviewTab;
