import { ChevronDown, ChevronRight, Plus, Edit, Trash2, Save, X, Search, Filter, RefreshCw, CheckCircle, AlertCircle, XCircle, AlertTriangle } from 'lucide-react';

const ChartOfAccounts = () => {
  const [accounts, setAccounts] = useState([]);
  const [expandedNodes, setExpandedNodes] = useState(new Set());
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [isAddingAccount, setIsAddingAccount] = useState(false);
  const [editingAccount, setEditingAccount] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [toasts, setToasts] = useState([]);
  const [confirmDialog, setConfirmDialog] = useState(null);
  const [formData, setFormData] = useState({
    code: '',
    name: '',
    type: 'assets',
    category: 'account',
    parentId: null,
    description: '',
    active: true
  });

  // API Base URL
  const API_BASE_URL = 'http://localhost:8080/api/accounts';

  // Toast notification system
  const showToast = (message, type = 'success') => {
    const id = Date.now();
    const toast = { id, message, type };
    setToasts(prev => [...prev, toast]);
    
    // Auto remove after 5 seconds
    setTimeout(() => {
      setToasts(prev => prev.filter(t => t.id !== id));
    }, 5000);
  };

  const removeToast = (id) => {
    setToasts(prev => prev.filter(t => t.id !== id));
  };

  // Confirmation Dialog Component
  const ConfirmationDialog = ({ isOpen, title, message, onConfirm, onCancel, confirmText = "Confirm", cancelText = "Cancel", type = "warning" }) => {
    if (!isOpen) return null;

    const typeStyles = {
      warning: {
        icon: <AlertTriangle className="text-yellow-600" size={24} />,
        iconBg: 'bg-yellow-100',
        confirmBtn: 'bg-yellow-600 hover:bg-yellow-700'
      },
      danger: {
        icon: <XCircle className="text-red-600" size={24} />,
        iconBg: 'bg-red-100',
        confirmBtn: 'bg-red-600 hover:bg-red-700'
      },
      info: {
        icon: <AlertCircle className="text-blue-600" size={24} />,
        iconBg: 'bg-blue-100',
        confirmBtn: 'bg-blue-600 hover:bg-blue-700'
      }
    };

    const style = typeStyles[type] || typeStyles.warning;

    return (
      <div className="fixed inset-0 z-50 overflow-y-auto">
        <div className="flex min-h-screen items-center justify-center p-4 text-center">
          <div className="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" onClick={onCancel}></div>
          
          <div className="relative transform overflow-hidden rounded-lg bg-white text-left shadow-xl transition-all sm:my-8 sm:w-full sm:max-w-lg">
            <div className="bg-white px-4 pb-4 pt-5 sm:p-6 sm:pb-4">
              <div className="sm:flex sm:items-start">
                <div className={`mx-auto flex h-12 w-12 flex-shrink-0 items-center justify-center rounded-full sm:mx-0 sm:h-10 sm:w-10 ${style.iconBg}`}>
                  {style.icon}
                </div>
                <div className="mt-3 text-center sm:ml-4 sm:mt-0 sm:text-left">
                  <h3 className="text-lg font-semibold leading-6 text-gray-900">
                    {title}
                  </h3>
                  <div className="mt-2">
                    <p className="text-sm text-gray-500">
                      {message}
                    </p>
                  </div>
                </div>
              </div>
            </div>
            <div className="bg-gray-50 px-4 py-3 sm:flex sm:flex-row-reverse sm:px-6">
              <button
                type="button"
                className={`inline-flex w-full justify-center rounded-md px-3 py-2 text-sm font-semibold text-white shadow-sm sm:ml-3 sm:w-auto ${style.confirmBtn}`}
                onClick={onConfirm}
              >
                {confirmText}
              </button>
              <button
                type="button"
                className="mt-3 inline-flex w-full justify-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50 sm:mt-0 sm:w-auto"
                onClick={onCancel}
              >
                {cancelText}
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  };

  // Account types configuration
  const accountTypes = {
    assets: { label: 'Assets', color: 'bg-green-100 text-green-800', codeRange: '1000-1999' },
    liabilities: { label: 'Liabilities', color: 'bg-red-100 text-red-800', codeRange: '2000-2999' },
    equity: { label: 'Equity', color: 'bg-blue-100 text-blue-800', codeRange: '3000-3999' },
    revenue: { label: 'Revenue', color: 'bg-purple-100 text-purple-800', codeRange: '4000-4999' },
    expenses: { label: 'Expenses', color: 'bg-orange-100 text-orange-800', codeRange: '5000-5999' }
  };

  // Fetch accounts from API
  const fetchAccounts = async () => {
    try {
      setLoading(true);
      setError(null);
      const response = await fetch(API_BASE_URL);
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      setAccounts(data);
      
      // Expand main categories by default
      const mainCategories = data
        .filter(account => account.level === 0)
        .map(account => account.id);
      setExpandedNodes(new Set(mainCategories));
      
    } catch (err) {
      const errorMsg = 'Failed to fetch accounts: ' + err.message;
      setError(errorMsg);
      showToast(errorMsg, 'error');
      console.error('Error fetching accounts:', err);
    } finally {
      setLoading(false);
    }
  };

  // Initialize component
  useEffect(() => {
    fetchAccounts();
  }, []);

  // Create account
  const createAccount = async (accountData) => {
    try {
      setLoading(true);
      const response = await fetch(API_BASE_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(accountData)
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || `HTTP error! status: ${response.status}`);
      }

      const result = await response.text();
      showToast(result || 'Account created successfully!', 'success');
      await fetchAccounts(); // Refresh the list
      return true;
    } catch (err) {
      const errorMsg = 'Failed to create account: ' + err.message;
      setError(errorMsg);
      showToast(errorMsg, 'error');
      console.error('Error creating account:', err);
      return false;
    } finally {
      setLoading(false);
    }
  };

  // Update account
  const updateAccount = async (accountId, accountData) => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/${accountId}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(accountData)
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || `HTTP error! status: ${response.status}`);
      }

      const result = await response.text();
      showToast(result || 'Account updated successfully!', 'success');
      await fetchAccounts(); // Refresh the list
      return true;
    } catch (err) {
      const errorMsg = 'Failed to update account: ' + err.message;
      setError(errorMsg);
      showToast(errorMsg, 'error');
      console.error('Error updating account:', err);
      return false;
    } finally {
      setLoading(false);
    }
  };

  // Delete account
  const deleteAccount = async (accountId) => {
    try {
      setLoading(true);
      const response = await fetch(`${API_BASE_URL}/${accountId}`, {
        method: 'DELETE'
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(errorText || `HTTP error! status: ${response.status}`);
      }

      const result = await response.text();
      showToast(result || 'Account deleted successfully!', 'success');
      await fetchAccounts(); // Refresh the list
      return true;
    } catch (err) {
      const errorMsg = 'Failed to delete account: ' + err.message;
      setError(errorMsg);
      showToast(errorMsg, 'error');
      console.error('Error deleting account:', err);
      return false;
    } finally {
      setLoading(false);
    }
  };

  // Helper function to flatten hierarchy for filtering
  const flattenAccounts = (accounts) => {
    const result = [];
    const flatten = (items) => {
      items.forEach(item => {
        result.push(item);
        if (item.children && item.children.length > 0) {
          flatten(item.children);
        }
      });
    };
    flatten(accounts);
    return result;
  };

  // Filter accounts based on search and category
  const getFilteredAccounts = () => {
    const flatAccounts = flattenAccounts(accounts);
    let filtered = flatAccounts;

    if (searchTerm) {
      filtered = filtered.filter(account => 
        account.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        account.code.includes(searchTerm) ||
        (account.description && account.description.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    if (selectedCategory !== 'all') {
      filtered = filtered.filter(account => account.type === selectedCategory);
    }

    return filtered;
  };

  // Build hierarchy from flat accounts (for display when not searching)
  const buildHierarchy = (accounts, parentId = null) => {
    return accounts
      .filter(account => account.parentId === parentId)
      .sort((a, b) => a.code.localeCompare(b.code))
      .map(account => ({
        ...account,
        children: buildHierarchy(accounts, account.id)
      }));
  };

  // Toggle expand/collapse
  const toggleExpand = (nodeId) => {
    const newExpanded = new Set(expandedNodes);
    if (newExpanded.has(nodeId)) {
      newExpanded.delete(nodeId);
    } else {
      newExpanded.add(nodeId);
    }
    setExpandedNodes(newExpanded);
  };

  // Handle add account
  const handleAddAccount = (parentId = null) => {
    const flatAccounts = flattenAccounts(accounts);
    const parentAccount = parentId ? flatAccounts.find(a => a.id === parentId) : null;
    const accountType = parentAccount ? parentAccount.type : 'assets';
    
    setFormData({
      code: '', // Will be generated by backend
      name: '',
      type: accountType,
      category: 'account',
      parentId,
      description: '',
      active: true
    });
    setIsAddingAccount(true);
  };

  // Handle edit account
  const handleEditAccount = (account) => {
    setFormData({
      code: account.code,
      name: account.name,
      type: account.type,
      category: account.category,
      parentId: account.parentId,
      description: account.description || '',
      active: account.active !== undefined ? account.active : true
    });
    setEditingAccount(account.id);
  };

  // Handle save account
  const handleSaveAccount = async () => {
    if (!formData.name) {
      showToast('Please fill in the account name', 'error');
      return;
    }

    const success = editingAccount 
      ? await updateAccount(editingAccount, formData)
      : await createAccount(formData);

    if (success) {
      // Reset form
      setFormData({
        code: '',
        name: '',
        type: 'assets',
        category: 'account',
        parentId: null,
        description: '',
        active: true
      });
      setIsAddingAccount(false);
      setEditingAccount(null);
    }
  };

  // Handle delete account with confirmation popup
  const handleDeleteAccount = async (account) => {
    const flatAccounts = flattenAccounts(accounts);
    const hasChildren = flatAccounts.some(a => a.parentId === account.id);
    
    if (hasChildren) {
      showToast('Cannot delete account with sub-accounts. Please delete sub-accounts first.', 'error');
      return;
    }

    setConfirmDialog({
      title: 'Delete Account',
      message: `Are you sure you want to delete the account "${account.name}" (${account.code})? This action cannot be undone.`,
      type: 'danger',
      confirmText: 'Delete',
      cancelText: 'Cancel',
      onConfirm: async () => {
        await deleteAccount(account.id);
        setConfirmDialog(null);
      },
      onCancel: () => {
        setConfirmDialog(null);
      }
    });
  };

  // Handle cancel
  const handleCancel = () => {
    setIsAddingAccount(false);
    setEditingAccount(null);
    setFormData({
      code: '',
      name: '',
      type: 'assets',
      category: 'account',
      parentId: null,
      description: '',
      active: true
    });
  };

  // Render account node
  const renderAccountNode = (account) => {
    const hasChildren = account.children && account.children.length > 0;
    const isExpanded = expandedNodes.has(account.id);
    const typeConfig = accountTypes[account.type];

    return (
      <div key={account.id} className="account-node">
        <div 
          className={`flex items-center py-2 px-4 hover:bg-gray-50 border-l-4 ${
            account.category === 'header' ? 'border-gray-400 bg-gray-100 font-bold' :
            account.category === 'group' ? 'border-gray-300 bg-gray-50 font-semibold' :
            'border-transparent'
          }`}
          style={{ paddingLeft: `${account.level * 20 + 16}px` }}
        >
          {hasChildren && (
            <button
              onClick={() => toggleExpand(account.id)}
              className="mr-2 p-1 hover:bg-gray-200 rounded"
            >
              {isExpanded ? <ChevronDown size={16} /> : <ChevronRight size={16} />}
            </button>
          )}
          
          <div className="flex-1 flex items-center space-x-4">
            <span className="font-mono text-sm text-gray-600 min-w-[80px]">
              {account.code}
            </span>
            
            <span className={`flex-1 ${account.category === 'header' ? 'text-lg' : ''}`}>
              {account.name}
              {!account.active && <span className="ml-2 text-red-500 text-xs">(Inactive)</span>}
            </span>
            
            <span className={`px-2 py-1 rounded-full text-xs font-medium ${typeConfig.color}`}>
              {typeConfig.label}
            </span>
            
            <div className="flex space-x-1">
              <button
                onClick={() => handleEditAccount(account)}
                className="p-1 hover:bg-blue-100 rounded text-blue-600"
                title="Edit Account"
                disabled={loading}
              >
                <Edit size={14} />
              </button>
              <button
                onClick={() => handleDeleteAccount(account)}
                className="p-1 hover:bg-red-100 rounded text-red-600"
                title="Delete Account"
                disabled={loading}
              >
                <Trash2 size={14} />
              </button>
              <button
                onClick={() => handleAddAccount(account.id)}
                className="p-1 hover:bg-green-100 rounded text-green-600"
                title="Add Sub-Account"
                disabled={loading}
              >
                <Plus size={14} />
              </button>
            </div>
          </div>
        </div>
        
        {account.description && (
          <div className="px-4 py-1 text-sm text-gray-500" style={{ paddingLeft: `${account.level * 20 + 36}px` }}>
            {account.description}
          </div>
        )}

        {isExpanded && hasChildren && (
          <div>
            {account.children.map(child => renderAccountNode(child))}
          </div>
        )}
      </div>
    );
  };

  const flatAccounts = flattenAccounts(accounts);
  const filteredAccounts = getFilteredAccounts();
  const displayAccounts = searchTerm || selectedCategory !== 'all' 
    ? filteredAccounts 
    : buildHierarchy(flatAccounts);

  return (
    <div className="max-w-7xl mx-auto p-6 bg-white">
      {/* Confirmation Dialog */}
      {confirmDialog && (
        <ConfirmationDialog
          isOpen={true}
          title={confirmDialog.title}
          message={confirmDialog.message}
          type={confirmDialog.type}
          confirmText={confirmDialog.confirmText}
          cancelText={confirmDialog.cancelText}
          onConfirm={confirmDialog.onConfirm}
          onCancel={confirmDialog.onCancel}
        />
      )}

      {/* Toast Notifications */}
      <div className="fixed top-4 right-4 z-40 space-y-2">
        {toasts.map((toast) => (
          <div
            key={toast.id}
            className={`flex items-center p-4 rounded-lg shadow-lg max-w-sm transform transition-all duration-300 ease-in-out ${
              toast.type === 'success' 
                ? 'bg-green-50 border border-green-200 text-green-800' 
                : toast.type === 'error'
                ? 'bg-red-50 border border-red-200 text-red-800'
                : 'bg-blue-50 border border-blue-200 text-blue-800'
            }`}
          >
            <div className="flex-shrink-0">
              {toast.type === 'success' && <CheckCircle size={20} className="text-green-600" />}
              {toast.type === 'error' && <XCircle size={20} className="text-red-600" />}
              {toast.type === 'info' && <AlertCircle size={20} className="text-blue-600" />}
            </div>
            <div className="ml-3 flex-1">
              <p className="text-sm font-medium">{toast.message}</p>
            </div>
            <button
              onClick={() => removeToast(toast.id)}
              className={`ml-3 flex-shrink-0 rounded-md p-1.5 inline-flex focus:outline-none focus:ring-2 focus:ring-offset-2 ${
                toast.type === 'success' 
                  ? 'text-green-400 hover:bg-green-100 focus:ring-green-600' 
                  : toast.type === 'error'
                  ? 'text-red-400 hover:bg-red-100 focus:ring-red-600'
                  : 'text-blue-400 hover:bg-blue-100 focus:ring-blue-600'
              }`}
            >
              <X size={16} />
            </button>
          </div>
        ))}
      </div>

      <div className="mb-6">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Chart of Accounts</h1>
        <p className="text-gray-600">Manage your company's account structure and hierarchy</p>
      </div>

      {/* Error Message */}
      {error && (
        <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg">
          <div className="flex">
            <div className="flex-1">
              <p className="text-red-700">{error}</p>
            </div>
            <button
              onClick={() => setError(null)}
              className="text-red-400 hover:text-red-600"
            >
              <X size={16} />
            </button>
          </div>
        </div>
      )}

      {/* Controls */}
      <div className="mb-6 flex flex-wrap items-center justify-between gap-4">
        <div className="flex items-center space-x-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <input
              type="text"
              placeholder="Search accounts..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>
          
          <div className="relative">
            <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <select
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="pl-10 pr-8 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="all">All Types</option>
              {Object.entries(accountTypes).map(([key, type]) => (
                <option key={key} value={key}>{type.label}</option>
              ))}
            </select>
          </div>

          <button
            onClick={fetchAccounts}
            className="px-3 py-2 bg-gray-100 text-gray-700 rounded-lg hover:bg-gray-200 flex items-center space-x-2"
            disabled={loading}
          >
            <RefreshCw size={16} className={loading ? 'animate-spin' : ''} />
            <span>Refresh</span>
          </button>
        </div>

        <button
          onClick={() => handleAddAccount()}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center space-x-2"
          disabled={loading}
        >
          <Plus size={20} />
          <span>Add Account</span>
        </button>
      </div>

      {/* Account Form */}
      {(isAddingAccount || editingAccount) && (
        <div className="mb-6 p-6 bg-gray-50 rounded-lg border">
          <h3 className="text-lg font-semibold mb-4">
            {editingAccount ? 'Edit Account' : 'Add New Account'}
          </h3>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Account Code
              </label>
              <input
                type="text"
                value={formData.code}
                onChange={(e) => setFormData({...formData, code: e.target.value})}
                placeholder="Auto-generated if empty"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
              <p className="text-xs text-gray-500 mt-1">Leave empty for auto-generation</p>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Account Name *
              </label>
              <input
                type="text"
                value={formData.name}
                onChange={(e) => setFormData({...formData, name: e.target.value})}
                placeholder="Enter account name"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                required
              />
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Account Type
              </label>
              <select
                value={formData.type}
                onChange={(e) => setFormData({...formData, type: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                {Object.entries(accountTypes).map(([key, type]) => (
                  <option key={key} value={key}>{type.label} ({type.codeRange})</option>
                ))}
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Category
              </label>
              <select
                value={formData.category}
                onChange={(e) => setFormData({...formData, category: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="header">Header</option>
                <option value="group">Group</option>
                <option value="account">Account</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Parent Account
              </label>
              <select
                value={formData.parentId || ''}
                onChange={(e) => setFormData({...formData, parentId: e.target.value || null})}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              >
                <option value="">None (Top Level)</option>
                {flatAccounts
                  .filter(a => a.id !== editingAccount && a.category !== 'account')
                  .map(account => (
                    <option key={account.id} value={account.id}>
                      {account.code} - {account.name}
                    </option>
                  ))
                }
              </select>
            </div>
            
            <div className="flex items-center">
              <input
                type="checkbox"
                id="active"
                checked={formData.active}
                onChange={(e) => setFormData({...formData, active: e.target.checked})}
                className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
              />
              <label htmlFor="active" className="ml-2 block text-sm text-gray-900">
                Active
              </label>
            </div>
            
            <div className="md:col-span-2 lg:col-span-3">
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Description
              </label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({...formData, description: e.target.value})}
                rows={2}
                placeholder="Enter account description"
                className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>
          
          <div className="mt-4 flex space-x-3">
            <button
              onClick={handleSaveAccount}
              className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 flex items-center space-x-2"
              disabled={loading}
            >
              <Save size={16} />
              <span>{loading ? 'Saving...' : 'Save'}</span>
            </button>
            <button
              onClick={handleCancel}
              className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 flex items-center space-x-2"
              disabled={loading}
            >
              <X size={16} />
              <span>Cancel</span>
            </button>
          </div>
        </div>
      )}

      {/* Accounts Tree */}
      <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
        <div className="px-4 py-3 bg-gray-50 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h2 className="text-lg font-semibold text-gray-900">Account Structure</h2>
            <span className="text-sm text-gray-500">
              {filteredAccounts.length} accounts
              {loading && <span className="ml-2 text-blue-600">Loading...</span>}
            </span>
          </div>
        </div>
        
        <div className="max-h-[600px] overflow-y-auto">
          {loading && accounts.length === 0 ? (
            <div className="p-8 text-center text-gray-500">
              <RefreshCw className="animate-spin mx-auto mb-2" size={24} />
              Loading accounts...
            </div>
          ) : displayAccounts.length > 0 ? (
            searchTerm || selectedCategory !== 'all' ? (
              // Flat view for search results
              <div>
                {filteredAccounts.map(account => (
                  <div key={account.id}>
                    {renderAccountNode({...account, children: []})}
                  </div>
                ))}
              </div>
            ) : (
              // Hierarchical view
              displayAccounts.map(account => renderAccountNode(account))
            )
          ) : (
            <div className="p-8 text-center text-gray-500">
              No accounts found matching your criteria.
            </div>
          )}
        </div>
      </div>

      {/* Account Type Legend */}
      <div className="mt-6 p-4 bg-gray-50 rounded-lg">
        <h3 className="text-sm font-semibold text-gray-700 mb-3">Account Types & Code Ranges</h3>
        <div className="flex flex-wrap gap-4">
          {Object.entries(accountTypes).map(([key, type]) => (
            <div key={key} className="flex items-center space-x-2">
              <span className={`px-2 py-1 rounded-full text-xs font-medium ${type.color}`}>
                {type.label}
              </span>
              <span className="text-sm text-gray-600 font-mono">{type.codeRange}</span>
            </div>
          ))}
        </div>
      </div>

      {/* API Integration Info */}
      <div className="mt-6 p-4 bg-blue-50 rounded-lg">
        <h3 className="text-sm font-semibold text-blue-800 mb-2">API Integration Status</h3>
        <div className="text-xs text-blue-700 space-y-1">
          <p>• Connected to: {API_BASE_URL}</p>
          <p>• Auto-refresh after CRUD operations</p>
          <p>• Real-time data from Spring Boot backend</p>
          <p>• Hierarchical structure maintained server-side</p>
        </div>
      </div>
    </div>
  );
};

export default ChartOfAccounts;
