      // Find the account in the entries first to get the name
      let accountName = '';
      let accountType = '';
      
      // Try to find the account info from journal entries
      journalEntries.forEach(entry => {
        entry.entries.forEach(entryLine => {
          if (entryLine.accountId === accountId) {
            if (entryLine.accountName) accountName = entryLine.accountName;
          }
        });
      });
      
      // Try to guess the account type based on the account ID prefix - this is more reliable
      const prefix = accountId.toString().substring(0, 1);
      if (prefix === '1') accountType = 'assets';
      else if (prefix === '2') accountType = 'liabilities';
      else if (prefix === '3') accountType = 'equity';
      else if (prefix === '4') accountType = 'revenue';
      else if (prefix === '5') accountType = 'expenses';
      else accountType = accountTypesMap[accountId] || 'unknown';
      
      // Create a fallback account
      balances[accountId] = {
        accountId: accountId,
        accountCode: accountId,
        accountName: accountName || `Account ${accountId}`,
        accountType: accountType,
        debit: 0,
