const AccountsReceivable = () => {
  const [customers, setCustomers] = useState([]);
  const [invoices, setInvoices] = useState([]);
  const [payments, setPayments] = useState([]);
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [activeTab, setActiveTab] = useState('overview');
  const [viewInvoice, setViewInvoice] = useState(null);
  const [loading, setLoading] = useState(false);
  const [salesOrders, setSalesOrders] = useState([]);
  
  const [filters, setFilters] = useState({
    customerType: 'all',
    status: 'all',
    dateRange: '30',
    searchTerm: ''
  });

  const [showInvoiceForm, setShowInvoiceForm] = useState(false);
  const [newInvoice, setNewInvoice] = useState({
    customerId: '',
    dueDate: '',
    items: [{ description: '', quantity: 1, price: 0, total: 0 }],
    notes: ''
  });

  // API Configuration
  const API_BASE_URL_SALES = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/orders?start=0&size=200';
  const API_CUSTOMERS='https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/customers?status=Active';
  // Fetch sales orders from API
  const fetchSalesOrders = async () => {
    setLoading(true);
    try {
      const response = await fetch(API_BASE_URL_SALES, {
        method: 'GET',
        headers: {
          'Authorization': 'Bearer Hamid',
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`API request failed with status ${response.status}`);
      }
      
      const data = await response.json();
      setSalesOrders(data);
      processARData(data);
    } catch (error) {
      console.error('Error fetching sales orders:', error);
      // Load sample data as fallback
      loadSampleData();
    } finally {
      setLoading(false);
    }
  };

  // Fetch both sales orders and customers from API
const fetchData = async () => {
  setLoading(true);
  try {
    // Fetch both data sources in parallel
    const [ordersResponse, customersResponse] = await Promise.all([
      fetch(API_BASE_URL_SALES, {
        method: 'GET',
        headers: {
          'Authorization': 'Bearer Hamid',
          'Content-Type': 'application/json',
        },
      }),
      fetch(API_CUSTOMERS, {
        method: 'GET',
        headers: {
          'Authorization': 'Bearer Hamid',
          'Content-Type': 'application/json',
        },
      })
    ]);
    
    if (!ordersResponse.ok || !customersResponse.ok) {
      throw new Error(`API request failed with status ${ordersResponse.status}/${customersResponse.status}`);
    }
    
    const ordersData = await ordersResponse.json();
    const customersData = await customersResponse.json();
    
    setSalesOrders(ordersData);
    processARData(ordersData, customersData);
  } catch (error) {
    console.error('Error fetching data:', error);
    // Load sample data as fallback
    loadSampleData();
  } finally {
    setLoading(false);
  }
};

// Process API data to create AR entries with proper customer mapping
const processARData = (orders, apiCustomers) => {
  console.log("Starting processARData function");
  
  // Create a map of customers from the API
  const customerMap = new Map();
  console.log("Created empty customerMap");
  
  const invoiceList = [];
  console.log("Created empty invoiceList");
  
  const paymentList = [];
  console.log("Created empty paymentList");

  // First, process the customers from the API
  console.log("Processing customers from API");
  if (apiCustomers && apiCustomers.length > 0) {
    console.log(`API customers found: ${apiCustomers.length}`);
    
    apiCustomers.forEach(customer => {
      console.log(`Processing customer: ${customer.id} - ${customer.name}`);
      
      customerMap.set(customer.id, {
        id: customer.id,
        name: customer.name || `Customer ${customer.id}`,
        email: customer.email || 'customer@example.com',
        phone: customer.phoneNumber || '+966-50-000-0000',
        address: customer.address && customer.address.length > 0 ? 
                 customer.address[0] : 'Saudi Arabia',
        customerType: 'credit', // Default to credit
        creditLimit: 50000, // Default credit limit
        paymentTerms: 30,
        totalOwed: 0,
        overdueAmount: 0,
        lastPayment: null,
        creditStatus: 'good',
        accountBalance: 0,
        invoiceCount: 0,
        avgPaymentDays: 0
      });
      
      console.log(`Added customer ${customer.id} to customerMap`);
    });
  } else {
    console.log("No API customers found or empty array");
  }

  // Process orders
  console.log("Processing orders");
  if (orders && orders.length > 0) {
    console.log(`Orders found: ${orders.length}`);
    
    orders.forEach(order => {
      console.log(`Processing order: ${order.orderId}`);
      
      // Skip draft orders
      if (order.draft) {
        console.log(`Skipping draft order: ${order.orderId}`);
        return;
      }

      // Apply the filter condition: only unpaid, unfulfilled, and non-closed orders
      console.log(`Checking order conditions - Paid: ${order.paid}, Fulfilled: ${order.fulfilled}, Status: ${order.status}`);
      
      if (!order.paid || !order.fulfilled || order.status !== 'Closed') {
        console.log(`Order ${order.orderId} meets criteria (unpaid, unfulfilled, or not closed)`);
        
        // Use customer ID from order
        const customerId = order.customerId;
        console.log(`Order customer ID: ${customerId}`);

        // Only process orders with customer IDs that exist in our customer map
        if (customerId && customerMap.has(customerId)) {
          console.log(`Customer ${customerId} found in customerMap`);
          
          // Create invoice from order
          console.log(`Creating invoice for order ${order.orderId}`);
          const invoice = {
            id: `INV-${order.orderId}`,
            customerId: customerId,
            customerName: customerMap.get(customerId).name,
            date: formatDate(order.createdAt),
            dueDate: calculateDueDate(order.createdAt, 30),
            amount: parseFloat(order.total) || 0,
            paid: order.paid ? parseFloat(order.total) : 0,
            balance: order.paid ? 0 : parseFloat(order.total) || 0,
            status: order.paid ? 'paid' : (isOverdue(order.createdAt, 30) ? 'overdue' : 'outstanding'),
            daysOverdue: calculateDaysOverdue(order.createdAt, 30),
            paymentTerms: 30,
            items: order.lineItems?.map(item => ({
              description: item.productName,
              quantity: item.quantity,
              price: item.price,
              total: item.total
            })) || [],
            notes: order.notes || '',
            source: 'pos',
            orderStatus: order.status || 'Unknown',
            fulfilled: order.fulfilled || false
          };

          invoiceList.push(invoice);
          console.log(`Added invoice ${invoice.id} to invoiceList`);

          // Update customer totals
          console.log(`Updating customer totals for customer ${customerId}`);
          const customer = customerMap.get(customerId);
          if (!order.paid) {
            console.log(`Adding balance ${invoice.balance} to customer ${customerId}`);
            customer.totalOwed += invoice.balance;
            if (invoice.status === 'overdue') {
              console.log(`Adding overdue amount ${invoice.balance} to customer ${customerId}`);
              customer.overdueAmount += invoice.balance;
            }
          }
          customer.invoiceCount++;
          console.log(`Customer ${customerId} now has ${customer.invoiceCount} invoices`);

          // Create payment record if paid
          if (order.paid) {
            console.log(`Creating payment record for order ${order.orderId}`);
            paymentList.push({
              id: `PAY-${order.orderId}`,
              customerId: customerId,
              customerName: customerMap.get(customerId).name,
              invoiceId: invoice.id,
              date: formatDate(order.createdAt),
              amount: parseFloat(order.total),
              method: order.paymentMethod || 'cash',
              reference: `TXN-${order.orderId}`,
              status: 'cleared',
              notes: 'POS payment'
            });
            
            customer.lastPayment = formatDate(order.createdAt);
            console.log(`Set last payment date for customer ${customerId} to ${customer.lastPayment}`);
          }
        } else {
          console.log(`Customer ${customerId} not found in customerMap or invalid ID`);
        }
      } else {
        console.log(`Order ${order.orderId} does not meet criteria (paid, fulfilled, and closed)`);
      }
    });
  } else {
    console.log("No orders found or empty array");
  }

  // Update credit status based on outstanding amounts
  console.log("Updating credit status for customers");
  customerMap.forEach(customer => {
    console.log(`Processing customer ${customer.id} for credit status update`);
    const utilizationRate = customer.totalOwed / customer.creditLimit;
    console.log(`Customer ${customer.id} utilization rate: ${utilizationRate}`);
    
    if (customer.overdueAmount > 0) {
      console.log(`Customer ${customer.id} has overdue amount, setting status to 'watch'`);
      customer.creditStatus = 'watch';
    } else if (utilizationRate > 0.8) {
      console.log(`Customer ${customer.id} has high utilization, setting status to 'watch'`);
      customer.creditStatus = 'watch';
    } else if (utilizationRate > 0.5) {
      console.log(`Customer ${customer.id} has moderate utilization, setting status to 'good'`);
      customer.creditStatus = 'good';
    } else {
      console.log(`Customer ${customer.id} has low utilization, setting status to 'excellent'`);
      customer.creditStatus = 'excellent';
    }
    
    console.log(`Customer ${customer.id} credit status: ${customer.creditStatus}`);
  });

  // Filter customers to only include those with invoices
  console.log("Filtering customers to only include those with invoices");
  const customersWithInvoices = Array.from(customerMap.values()).filter(customer => {
    const hasInvoices = invoiceList.some(invoice => invoice.customerId === customer.id);
    console.log(`Customer ${customer.id} has invoices: ${hasInvoices}`);
    return hasInvoices;
  });
  
  console.log(`Customers with invoices: ${customersWithInvoices.length}`);
  console.log("processARData function completed");

  return {
    customers: customersWithInvoices,
    invoices: invoiceList,
    payments: paymentList
  };
};
