import React, { useState } from 'react';
import { 
  Plus, 
  Search, 
  Filter, 
  Calendar, 
  Eye, 
  Edit, 
  Download,
  CreditCard,
  DollarSign,
  CheckCircle,
  Clock
} from 'lucide-react';

const PaymentsTab = ({ payments, customers, invoices }) => {
  const [filters, setFilters] = useState({
    status: 'all',
    method: 'all',
    dateFrom: '',
    dateTo: '',
    searchTerm: '',
    customerId: ''
  });

  const [sortConfig, setSortConfig] = useState({
    key: 'date',
    direction: 'desc'
  });

  const [showRecordPayment, setShowRecordPayment] = useState(false);

  const getPaymentStatusColor = (status) => {
    switch (status) {
      case 'cleared': return 'bg-green-100 text-green-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'failed': return 'bg-red-100 text-red-800';
      case 'cancelled': return 'bg-gray-100 text-gray-800';
      default: return 'bg-blue-100 text-blue-800';
    }
  };

  const getPaymentMethodIcon = (method) => {
    switch (method) {
      case 'cash': return <DollarSign size={16} className="text-green-600" />;
      case 'card': 
      case 'credit_card': return <CreditCard size={16} className="text-blue-600" />;
      case 'bank_transfer': return <CheckCircle size={16} className="text-purple-600" />;
      case 'check': return <Clock size={16} className="text-orange-600" />;
      default: return <DollarSign size={16} className="text-gray-600" />;
    }
  };

  const getFilteredPayments = () => {
    let filtered = [...payments];

    // Status filter
    if (filters.status !== 'all') {
      filtered = filtered.filter(payment => payment.status === filters.status);
    }

    // Payment method filter
    if (filters.method !== 'all') {
      filtered = filtered.filter(payment => payment.method === filters.method);
    }

    // Date range filter
    if (filters.dateFrom) {
      filtered = filtered.filter(payment => payment.date >= filters.dateFrom);
    }
    if (filters.dateTo) {
      filtered = filtered.filter(payment => payment.date <= filters.dateTo);
    }

    // Search filter
    if (filters.searchTerm) {
      const term = filters.searchTerm.toLowerCase();
      filtered = filtered.filter(payment =>
        payment.id.toLowerCase().includes(term) ||
        payment.customerName.toLowerCase().includes(term) ||
        payment.invoiceId.toLowerCase().includes(term) ||
        payment.reference?.toLowerCase().includes(term)
      );
    }

    // Customer filter
    if (filters.customerId) {
      filtered = filtered.filter(payment => payment.customerId === filters.customerId);
    }

    return filtered;
  };

  const getSortedPayments = (filteredPayments) => {
    return [...filteredPayments].sort((a, b) => {
      let aValue = a[sortConfig.key];
      let bValue = b[sortConfig.key];

      // Handle different data types
      if (sortConfig.key === 'date') {
        aValue = new Date(aValue);
        bValue = new Date(bValue);
      } else if (typeof aValue === 'string') {
        aValue = aValue.toLowerCase();
        bValue = bValue.toLowerCase();
      }

      if (aValue < bValue) {
        return sortConfig.direction === 'asc' ? -1 : 1;
      }
      if (aValue > bValue) {
        return sortConfig.direction === 'asc' ? 1 : -1;
      }
      return 0;
    });
  };

  const handleSort = (key) => {
    setSortConfig({
      key,
      direction: sortConfig.key === key && sortConfig.direction === 'asc' ? 'desc' : 'asc'
    });
  };

  const handleViewPayment = (payment) => {
    alert(`View payment details for ${payment.id} would be implemented here`);
  };

  const handleEditPayment = (payment) => {
    alert(`Edit payment ${payment.id} would be implemented here`);
  };

  const handleDownloadReceipt = (payment) => {
    alert(`Download receipt for payment ${payment.id} would be implemented here`);
  };

  const filteredPayments = getFilteredPayments();
  const sortedPayments = getSortedPayments(filteredPayments);

  // Calculate summary statistics
  const paymentSummary = {
    totalAmount: filteredPayments.reduce((sum, payment) => sum + payment.amount, 0),
    clearedAmount: filteredPayments.filter(p => p.status === 'cleared').reduce((sum, payment) => sum + payment.amount, 0),
    pendingAmount: filteredPayments.filter(p => p.status === 'pending').reduce((sum, payment) => sum + payment.amount, 0),
    count: filteredPayments.length
  };

  // Get unique customers for filter dropdown
  const uniqueCustomers = [...new Set(payments.map(payment => payment.customerName))]
    .map(name => payments.find(payment => payment.customerName === name))
    .filter(Boolean);

  return (
    <div>
      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-blue-600 text-sm font-medium">Total Payments</p>
              <p className="text-xl font-bold text-blue-900">${paymentSummary.totalAmount.toFixed(2)}</p>
            </div>
            <DollarSign className="text-blue-600" size={20} />
          </div>
          <p className="text-xs text-blue-700 mt-1">{paymentSummary.count} payments</p>
        </div>

        <div className="bg-green-50 p-4 rounded-lg border border-green-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-green-600 text-sm font-medium">Cleared</p>
              <p className="text-xl font-bold text-green-900">${paymentSummary.clearedAmount.toFixed(2)}</p>
            </div>
            <CheckCircle className="text-green-600" size={20} />
          </div>
          <p className="text-xs text-green-700 mt-1">Processed payments</p>
        </div>

        <div className="bg-yellow-50 p-4 rounded-lg border border-yellow-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-yellow-600 text-sm font-medium">Pending</p>
              <p className="text-xl font-bold text-yellow-900">${paymentSummary.pendingAmount.toFixed(2)}</p>
            </div>
            <Clock className="text-yellow-600" size={20} />
          </div>
          <p className="text-xs text-yellow-700 mt-1">Awaiting clearance</p>
        </div>

        <div className="bg-purple-50 p-4 rounded-lg border border-purple-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-purple-600 text-sm font-medium">Avg Payment</p>
              <p className="text-xl font-bold text-purple-900">
                ${paymentSummary.count > 0 ? (paymentSummary.totalAmount / paymentSummary.count).toFixed(2) : '0.00'}
              </p>
            </div>
            <CreditCard className="text-purple-600" size={20} />
          </div>
          <p className="text-xs text-purple-700 mt-1">Per transaction</p>
        </div>
      </div>

      {/* Controls */}
      <div className="flex justify-between items-center mb-6">
        <h3 className="text-lg font-semibold text-gray-900">Payment History</h3>
        <button
          onClick={() => setShowRecordPayment(true)}
          className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2"
        >
          <Plus size={16} />
          <span>Record Payment</span>
        </button>
      </div>

      {/* Filters */}
      <div className="mb-6 p-4 bg-gray-50 rounded-lg">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-6 gap-4">
          {/* Search */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
            <input
              type="text"
              placeholder="Search payments..."
              value={filters.searchTerm}
              onChange={(e) => setFilters({ ...filters, searchTerm: e.target.value })}
              className="pl-10 pr-4 py-2 w-full border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            />
          </div>

          {/* Status Filter */}
          <div className="relative">
            <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
            <select
              value={filters.status}
              onChange={(e) => setFilters({ ...filters, status: e.target.value })}
              className="pl-10 pr-8 py-2 w-full border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            >
              <option value="all">All Status</option>
              <option value="cleared">Cleared</option>
              <option value="pending">Pending</option>
              <option value="failed">Failed</option>
              <option value="cancelled">Cancelled</option>
            </select>
          </div>

          {/* Payment Method Filter */}
          <select
            value={filters.method}
            onChange={(e) => setFilters({ ...filters, method: e.target.value })}
            className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
          >
            <option value="all">All Methods</option>
            <option value="cash">Cash</option>
            <option value="card">Card</option>
            <option value="bank_transfer">Bank Transfer</option>
            <option value="check">Check</option>
          </select>

          {/* Customer Filter */}
          <select
            value={filters.customerId}
            onChange={(e) => setFilters({ ...filters, customerId: e.target.value })}
            className="px-3 py-2 border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
          >
            <option value="">All Customers</option>
            {uniqueCustomers.map(customer => (
              <option key={customer.customerId} value={customer.customerId}>
                {customer.customerName}
              </option>
            ))}
          </select>

          {/* Date From */}
          <div className="relative">
            <Calendar className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
            <input
              type="date"
              value={filters.dateFrom}
              onChange={(e) => setFilters({ ...filters, dateFrom: e.target.value })}
              className="pl-10 pr-4 py-2 w-full border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            />
          </div>

          {/* Date To */}
          <div className="relative">
            <Calendar className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
            <input
              type="date"
              value={filters.dateTo}
              onChange={(e) => setFilters({ ...filters, dateTo: e.target.value })}
              className="pl-10 pr-4 py-2 w-full border border-gray-300 rounded-lg text-sm focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            />
          </div>
        </div>

        {/* Clear Filters */}
        <div className="mt-4 flex justify-between items-center">
          <button
            onClick={() => setFilters({
              status: 'all',
              method: 'all',
              dateFrom: '',
              dateTo: '',
              searchTerm: '',
              customerId: ''
            })}
            className="px-3 py-1 text-sm text-gray-600 hover:text-gray-800"
          >
            Clear Filters
          </button>
          <div className="text-sm text-gray-600">
            Showing {sortedPayments.length} of {payments.length} payments
          </div>
        </div>
      </div>

      {/* Payments Table */}
      <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                  onClick={() => handleSort('id')}
                >
                  <div className="flex items-center space-x-1">
                    <span>Payment ID</span>
                    {sortConfig.key === 'id' && (
                      <span className="text-emerald-500">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </div>
                </th>
                <th 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                  onClick={() => handleSort('customerName')}
                >
                  <div className="flex items-center space-x-1">
                    <span>Customer</span>
                    {sortConfig.key === 'customerName' && (
                      <span className="text-emerald-500">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </div>
                </th>
                <th 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                  onClick={() => handleSort('invoiceId')}
                >
                  <div className="flex items-center space-x-1">
                    <span>Invoice</span>
                    {sortConfig.key === 'invoiceId' && (
                      <span className="text-emerald-500">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </div>
                </th>
                <th 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                  onClick={() => handleSort('date')}
                >
                  <div className="flex items-center space-x-1">
                    <span>Date</span>
                    {sortConfig.key === 'date' && (
                      <span className="text-emerald-500">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </div>
                </th>
                <th 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                  onClick={() => handleSort('amount')}
                >
                  <div className="flex items-center space-x-1">
                    <span>Amount</span>
                    {sortConfig.key === 'amount' && (
                      <span className="text-emerald-500">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </div>
                </th>
                <th 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                  onClick={() => handleSort('method')}
                >
                  <div className="flex items-center space-x-1">
                    <span>Method</span>
                    {sortConfig.key === 'method' && (
                      <span className="text-emerald-500">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </div>
                </th>
                <th 
                  className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider cursor-pointer hover:bg-gray-100"
                  onClick={() => handleSort('status')}
                >
                  <div className="flex items-center space-x-1">
                    <span>Status</span>
                    {sortConfig.key === 'status' && (
                      <span className="text-emerald-500">
                        {sortConfig.direction === 'asc' ? '↑' : '↓'}
                      </span>
                    )}
                  </div>
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {sortedPayments.map((payment) => (
                <tr key={payment.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">{payment.id}</div>
                    {payment.reference && (
                      <div className="text-xs text-gray-500">Ref: {payment.reference}</div>
                    )}
                  </td>
                  <td className="px-6 py-4">
                    <div className="text-sm font-medium text-gray-900">{payment.customerName}</div>
                    <div className="text-xs text-gray-500">{payment.customerId}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm text-gray-900">{payment.invoiceId}</div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {payment.date}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="text-sm font-medium text-gray-900">
                      ${payment.amount.toFixed(2)}
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div className="flex items-center space-x-2">
                      {getPaymentMethodIcon(payment.method)}
                      <span className="text-sm text-gray-900 capitalize">
                        {payment.method.replace('_', ' ')}
                      </span>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getPaymentStatusColor(payment.status)}`}>
                      {payment.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <div className="flex items-center space-x-2">
                      <button
                        onClick={() => handleViewPayment(payment)}
                        className="text-emerald-600 hover:text-emerald-900"
                        title="View Payment"
                      >
                        <Eye size={16} />
                      </button>
                      <button
                        onClick={() => handleDownloadReceipt(payment)}
                        className="text-blue-600 hover:text-blue-900"
                        title="Download Receipt"
                      >
                        <Download size={16} />
                      </button>
                      {payment.status === 'pending' && (
                        <button
                          onClick={() => handleEditPayment(payment)}
                          className="text-gray-600 hover:text-gray-900"
                          title="Edit Payment"
                        >
                          <Edit size={16} />
                        </button>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Empty State */}
          {sortedPayments.length === 0 && (
            <div className="text-center py-12">
              <div className="text-gray-400 mb-4">
                <svg className="mx-auto h-12 w-12" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
              </div>
              <h3 className="text-sm font-medium text-gray-900 mb-1">No payments found</h3>
              <p className="text-sm text-gray-500">Try adjusting your search criteria or filters.</p>
            </div>
          )}
        </div>
      </div>

      {/* Record Payment Modal Placeholder */}
      {showRecordPayment && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Record New Payment</h3>
            <p className="text-gray-600 mb-4">Payment recording form would be implemented here.</p>
            <div className="flex space-x-3">
              <button
                onClick={() => setShowRecordPayment(false)}
                className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600"
              >
                Cancel
              </button>
              <button
                onClick={() => {
                  alert('Payment recording functionality would be implemented here');
                  setShowRecordPayment(false);
                }}
                className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700"
              >
                Record Payment
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Notes Section */}
      <div className="mt-8 p-4 bg-blue-50 border border-blue-200 rounded-lg">
        <h4 className="font-medium text-blue-800 mb-2">Payment Processing Notes</h4>
        <div className="text-sm text-blue-700 space-y-1">
          <p>• Cash payments are processed immediately and marked as cleared</p>
          <p>• Card payments may take 1-2 business days to clear</p>
          <p>• Bank transfers typically clear within 3-5 business days</p>
          <p>• Checks require manual verification and may take 5-7 business days</p>
        </div>
      </div>
    </div>
  );
};

export default PaymentsTab;
