import React, { useState, useEffect } from 'react';
import { Users, DollarSign, CreditCard, AlertTriangle, Clock, Plus, Search, Filter, Eye, Edit, Download, CheckCircle, XCircle, Calendar, FileText, TrendingUp, Mail, Phone, Loader, RefreshCw } from 'lucide-react';

// API Configuration
import { jsxDEV as _jsxDEV } from "react/jsx-dev-runtime";
const API_BASE_URL_SALES = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/orders?start=0&size=200';
const API_CUSTOMERS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/customers?status=Active';

// Replace with your actual Bearer token
const BEARER_TOKEN = 'your-bearer-token-here';
const AccountsReceivable = () => {
  _s();
  // State management
  const [sales, setSales] = useState({
    loading: false,
    data: [],
    error: null
  });
  const [customers, setCustomers] = useState({
    loading: false,
    data: [],
    error: null
  });
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [viewInvoice, setViewInvoice] = useState(null);
  const [showInvoiceForm, setShowInvoiceForm] = useState(false);
  const [filters, setFilters] = useState({
    customerType: 'all',
    status: 'all',
    dateRange: '30',
    searchTerm: ''
  });

  // API Functions
  const fetchSales = async () => {
    setSales(prev => ({
      ...prev,
      loading: true,
      error: null
    }));
    try {
      const response = await fetch(API_BASE_URL_SALES, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${BEARER_TOKEN}`,
          'Content-Type': 'application/json'
        }
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();

      // Filter for unpaid and unfulfilled orders only
      const filteredOrders = data.filter(order => !order.paid || !order.unfulfilled || order.status === 'Active');
      setSales({
        loading: false,
        data: filteredOrders,
        error: null
      });
    } catch (error) {
      console.error('Error fetching sales:', error);
      setSales({
        loading: false,
        data: [],
        error: error.message
      });
    }
  };
  const fetchCustomers = async () => {
    setCustomers(prev => ({
      ...prev,
      loading: true,
      error: null
    }));
    try {
      const response = await fetch(API_CUSTOMERS, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${BEARER_TOKEN}`,
          'Content-Type': 'application/json'
        }
      });
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();

      // Transform API customers to match our structure
      const transformedCustomers = data.map(customer => ({
        id: customer.id,
        name: customer.name,
        email: customer.email,
        phone: customer.phoneNumber,
        address: customer.address.length > 0 ? customer.address[0] : 'No address provided',
        customerType: 'individual',
        // Default type since API doesn't provide this
        creditLimit: 50000,
        // Default credit limit
        paymentTerms: 30,
        totalOwed: calculateCustomerBalance(customer.id),
        overdueAmount: calculateCustomerOverdue(customer.id),
        lastPayment: '2025-01-15',
        creditStatus: 'good',
        accountBalance: -calculateCustomerBalance(customer.id),
        invoiceCount: getCustomerInvoiceCount(customer.id),
        avgPaymentDays: 25,
        status: customer.status
      }));
      setCustomers({
        loading: false,
        data: transformedCustomers,
        error: null
      });
    } catch (error) {
      console.error('Error fetching customers:', error);
      setCustomers({
        loading: false,
        data: [],
        error: error.message
      });
    }
  };

  // Helper functions
  const calculateCustomerBalance = customerId => {
    return sales.data.filter(order => order.customerId === customerId && (!order.paid || !order.fulfilled)).reduce((sum, order) => sum + order.total, 0);
  };
  const calculateCustomerOverdue = customerId => {
    return sales.data.filter(order => order.customerId === customerId && !order.paid).reduce((sum, order) => sum + order.total, 0);
  };
  const getCustomerInvoiceCount = customerId => {
    return sales.data.filter(order => order.customerId === customerId).length;
  };

  // Load data on component mount
  useEffect(() => {
    fetchSales();
    fetchCustomers();
  }, []);

  // Helper function to get customer name by ID
  const getCustomerName = customerId => {
    if (!customerId || customerId === 'null') return 'Walk-in Customer';
    const customer = customers.data.find(c => c.id === customerId);
    return customer ? customer.name : 'Unknown Customer';
  };

  // Helper function to get customer details by ID
  const getCustomerDetails = customerId => {
    if (!customerId || customerId === 'null') return null;
    return customers.data.find(c => c.id === customerId);
  };

  // Calculate AR summary
  const calculateARSummary = () => {
    const totalAR = customers.data.reduce((sum, customer) => sum + customer.totalOwed, 0);
    const totalOverdue = customers.data.reduce((sum, customer) => sum + customer.overdueAmount, 0);
    const customersWithBalance = customers.data.filter(customer => customer.totalOwed > 0).length;
    const avgDaysOutstanding = customers.data.length > 0 ? customers.data.reduce((sum, customer) => sum + customer.avgPaymentDays, 0) / customers.data.length : 0;
    return {
      totalAR,
      totalOverdue,
      customersWithBalance,
      avgDaysOutstanding: Math.round(avgDaysOutstanding)
    };
  };

  // Get aging data
  const getAgingData = () => {
    const aging = {
      current: 0,
      days30: 0,
      days60: 0,
      days90: 0,
      over90: 0
    };
    sales.data.forEach(order => {
      if (order.paid) return;
      const daysPastDue = order.daysOverdue || 0;
      if (daysPastDue <= 0) {
        aging.current += order.total;
      } else if (daysPastDue <= 30) {
        aging.days30 += order.total;
      } else if (daysPastDue <= 60) {
        aging.days60 += order.total;
      } else if (daysPastDue <= 90) {
