import React, { useState, useEffect } from 'react';
import { 
  Users, 
  DollarSign, 
  CreditCard, 
  AlertTriangle, 
  Clock, 
  Plus, 
  Search, 
  Filter, 
  Eye, 
  Edit, 
  Download,
  CheckCircle,
  XCircle,
  Calendar,
  FileText,
  TrendingUp,
  Mail,
  Phone,
  Loader,
  RefreshCw
} from 'lucide-react';

// API Configuration
const API_BASE_URL_SALES = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/orders?start=0&size=200';
const API_CUSTOMERS = 'https://api-pos.hulmsolutions.com/POS/accounting2/31fc8df0-61db-41e5-bb67-070fde367ea1/customers?status=Active';

// Replace with your actual Bearer token
const BEARER_TOKEN = 'your-bearer-token-here';

const AccountsReceivable = () => {
  // State management
  const [sales, setSales] = useState({ loading: false, data: [], error: null });
  const [customers, setCustomers] = useState({ loading: false, data: [], error: null });
  const [activeTab, setActiveTab] = useState('overview');
  const [selectedCustomer, setSelectedCustomer] = useState(null);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [viewInvoice, setViewInvoice] = useState(null);
  const [showInvoiceForm, setShowInvoiceForm] = useState(false);
  const [filters, setFilters] = useState({
    customerType: 'all',
    status: 'all',
    dateRange: '30',
    searchTerm: ''
  });

  // API Functions
  const fetchSales = async () => {
    setSales(prev => ({ ...prev, loading: true, error: null }));
    try {
      const response = await fetch(API_BASE_URL_SALES, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${BEARER_TOKEN}`,
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const allData = await response.json();
      
      // Store all data for customer calculations
      window.allSalesData = allData;
      
      // Show only records that are unpaid and fulfilled for display
      const filteredOrders = allData.filter(order => 
        !order.paid && order.fulfilled
      );
      
      setSales({ loading: false, data: filteredOrders, error: null });
    } catch (error) {
      console.error('Error fetching sales:', error);
      setSales({ loading: false, data: [], error: error.message });
    }
  };

  const fetchCustomers = async () => {
    setCustomers(prev => ({ ...prev, loading: true, error: null }));
    try {
      const response = await fetch(API_CUSTOMERS, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${BEARER_TOKEN}`,
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const data = await response.json();
      
      // Use all sales data for customer calculations, not just filtered display data
      const allSalesData = window.allSalesData || [];
      
      // Transform API customers to match our structure with real calculated data
      const transformedCustomers = data.map(customer => {
        // Get ALL orders for this customer (not just filtered ones)
        const customerOrders = allSalesData.filter(order => order.customerId === customer.id);
        
        // Calculate total owed from ALL unpaid orders
        const totalOwed = customerOrders
          .filter(order => !order.paid)
          .reduce((sum, order) => sum + (order.total || 0), 0);
        
        // Overdue amount = total owed (since all unpaid orders need collection)
        const overdueAmount = totalOwed;
        
        // Total number of orders for this customer
        const invoiceCount = customerOrders.length;
        
        // Determine customer type based on actual order history, not API totalSpendOnOrder
        let customerType = 'individual';
        const totalSpentFromOrders = customerOrders.reduce((sum, order) => sum + (order.total || 0), 0);
        
        // Use actual order amounts to determine customer type
        if (totalSpentFromOrders > 500000) {
          customerType = 'corporate';
        } else if (totalSpentFromOrders > 200000) {
          customerType = 'small_business';
        } else {
          customerType = 'individual';
        }
        
        // Set credit limit based on customer type
        let creditLimit = 25000; // Default for individual
        if (customerType === 'corporate') {
          creditLimit = 500000;
        } else if (customerType === 'small_business') {
          creditLimit = 100000;
        }
        
        // Determine credit status based on payment behavior
        let creditStatus = 'good';
        if (totalOwed === 0) {
          creditStatus = 'excellent';
        } else if (totalOwed > creditLimit * 0.9) {
          creditStatus = 'hold'; // Hold means credit limit exceeded, stop further credit
        } else if (totalOwed > creditLimit * 0.7) {
          creditStatus = 'watch'; // Watch means monitor closely
        }
        
        // Get last payment date from most recent paid order
        const paidOrders = customerOrders.filter(o => o.paid);
        const lastPayment = paidOrders.length > 0 ? 
          paidOrders.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))[0].createdAt.split('.').slice(0, 3).join('-') :
          'No payments';

        return {
          id: customer.id,
          name: customer.name,
          email: customer.email || 'No email provided',
          phone: customer.phoneNumber || 'No phone provided',
          address: customer.address && customer.address.length > 0 ? 
            (typeof customer.address[0] === 'string' ? customer.address[0] : 'No address provided') : 
            'No address provided',
          customerType: customerType,
          creditLimit: creditLimit,
          paymentTerms: customerType === 'corporate' ? 45 : (customerType === 'small_business' ? 30 : 15),
          totalOwed: totalOwed,
          overdueAmount: overdueAmount,
          lastPayment: lastPayment,
          creditStatus: creditStatus,
          accountBalance: -totalOwed,
          invoiceCount: invoiceCount,
          avgPaymentDays: 30, // Default estimate
          status: customer.status,
          totalSpendOnOrder: customer.totalSpendOnOrder || 0,
          averageOrderValue: customer.averageOrderValue || 0,
          numberOfOrders: customer.numberOfOrders || invoiceCount
        };
      });
      
      // Also add walk-in customers (null customerId) as separate entries for each order
      const walkInOrders = allSalesData.filter(order => !order.customerId || order.customerId === 'null');
      walkInOrders.forEach((order, index) => {
        const guestNumber = index + 1;
        const guestTotalOwed = order.paid ? 0 : order.total;
        const guestLastPayment = order.paid ? 
          order.createdAt.split('.').slice(0, 3).join('-') : 
          'No payments';
        
        transformedCustomers.push({
          id: `guest-${order.orderId}`,
          name: `Guest Customer #${guestNumber}`,
          email: 'guest@pos.system',
          phone: '+000-000-0000',
          address: 'Walk-in Sale',
          customerType: 'cash',
          creditLimit: 0,
          paymentTerms: 0,
          totalOwed: guestTotalOwed,
          overdueAmount: guestTotalOwed,
          lastPayment: guestLastPayment,
          creditStatus: 'cash_only',
          accountBalance: -guestTotalOwed,
          invoiceCount: 1,
          avgPaymentDays: 0,
          status: 'Active',
          totalSpendOnOrder: order.total,
          averageOrderValue: order.total,
          numberOfOrders: 1,
          orderId: order.orderId // Store order ID for reference
        });
      });
      
      setCustomers({ loading: false, data: transformedCustomers, error: null });
    } catch (error) {
      console.error('Error fetching customers:', error);
      setCustomers({ loading: false, data: [], error: error.message });
    }
  };

  // Also add refresh button functionality to recalculate customer data
  const refreshAllData = async () => {
    await fetchSales();
    setTimeout(() => fetchCustomers(), 500);
  };

  // Load data on component mount
  useEffect(() => {
    const loadData = async () => {
      await fetchSales();
      // Fetch customers after sales data is loaded to calculate balances correctly
      setTimeout(() => fetchCustomers(), 500);
    };
    loadData();
  }, []);

  // Helper function to get customer name by ID
  const getCustomerName = (customerId) => {
    if (!customerId || customerId === 'null') return 'Guest Customer';
    const customer = customers.data.find(c => c.id === customerId);
    return customer ? customer.name : 'Unknown Customer';
  };

  // Helper function to get customer details by ID
  const getCustomerDetails = (customerId) => {
    if (!customerId || customerId === 'null') return null;
    return customers.data.find(c => c.id === customerId);
  };

  // Calculate AR summary
  const calculateARSummary = () => {
    const totalAR = customers.data.reduce((sum, customer) => sum + customer.totalOwed, 0);
    const totalOverdue = customers.data.reduce((sum, customer) => sum + customer.overdueAmount, 0);
    const customersWithBalance = customers.data.filter(customer => customer.totalOwed > 0).length;
    
    // Calculate real average days outstanding from actual order data
    const allSalesData = window.allSalesData || [];
    let totalDaysOutstanding = 0;
    let orderCount = 0;
    
    allSalesData.forEach(order => {
      // Convert order creation date from "2025.08.18.10.08.54.569" to proper date
      const orderDateStr = order.createdAt.split('.').slice(0, 3).join('-'); // "2025-08-18"
      const orderDate = new Date(orderDateStr);
      const today = new Date();
      
      // Calculate days between order creation and today
      const timeDiff = today.getTime() - orderDate.getTime();
      const daysDiff = Math.floor(timeDiff / (1000 * 3600 * 24));
      
      totalDaysOutstanding += daysDiff;
      orderCount++;
    });
    
    const avgDaysOutstanding = orderCount > 0 ? Math.round(totalDaysOutstanding / orderCount) : 0;

    return {
      totalAR,
      totalOverdue,
      customersWithBalance,
      avgDaysOutstanding
    };
  };

  // Get aging data
  const getAgingData = () => {
    const aging = {
      current: 0,
      days30: 0,
      days60: 0,
      days90: 0,
      over90: 0
    };

    sales.data.forEach(order => {
      if (order.paid) return;
      
      const daysPastDue = order.daysOverdue || 0;
      
      if (daysPastDue <= 0) {
        aging.current += order.total;
      } else if (daysPastDue <= 30) {
        aging.days30 += order.total;
      } else if (daysPastDue <= 60) {
        aging.days60 += order.total;
      } else if (daysPastDue <= 90) {
        aging.days90 += order.total;
      } else {
        aging.over90 += order.total;
      }
    });

    return aging;
  };

  // Filter customers
  const getFilteredCustomers = () => {
    let filtered = [...customers.data];

    if (filters.customerType !== 'all') {
      filtered = filtered.filter(customer => customer.customerType === filters.customerType);
    }

    if (filters.status !== 'all') {
      filtered = filtered.filter(customer => {
        if (filters.status === 'overdue') return customer.overdueAmount > 0;
        if (filters.status === 'current') return customer.totalOwed > 0 && customer.overdueAmount === 0;
        if (filters.status === 'paid_up') return customer.totalOwed === 0;
        return true;
      });
    }

    if (filters.searchTerm) {
      const term = filters.searchTerm.toLowerCase();
      filtered = filtered.filter(customer =>
        customer.name.toLowerCase().includes(term) ||
        customer.email.toLowerCase().includes(term) ||
        customer.id.toLowerCase().includes(term)
      );
    }

    return filtered;
  };

  const getCreditStatusColor = (status) => {
    switch (status) {
      case 'excellent': return 'bg-green-100 text-green-800';
      case 'good': return 'bg-blue-100 text-blue-800';
      case 'watch': return 'bg-yellow-100 text-yellow-800';
      case 'hold': return 'bg-red-100 text-red-800';
      case 'cash_only': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getInvoiceStatusColor = (status) => {
    switch (status) {
      case 'paid': return 'bg-green-100 text-green-800';
      case 'outstanding': return 'bg-blue-100 text-blue-800';
      case 'overdue': return 'bg-red-100 text-red-800';
      case 'partial': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return dateString.split('.').slice(0, 3).join('/');
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const summary = calculateARSummary();
  const aging = getAgingData();
  const filteredCustomers = getFilteredCustomers();

  // Loading state
  if (sales.loading || customers.loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="flex items-center space-x-3">
          <Loader className="animate-spin text-emerald-600" size={24} />
          <span className="text-gray-600">Loading data from API...</span>
        </div>
      </div>
    );
  }

  // Error state
  if (sales.error || customers.error) {
    return (
      <div className="max-w-7xl mx-auto p-6">
        <div className="bg-red-50 border border-red-200 rounded-lg p-6">
          <div className="flex items-center mb-4">
            <AlertTriangle className="text-red-600 mr-2" size={20} />
            <h3 className="font-medium text-red-800">API Connection Error</h3>
          </div>
          <div className="text-red-700 mb-4">
            {sales.error && <div>Sales API Error: {sales.error}</div>}
            {customers.error && <div>Customers API Error: {customers.error}</div>}
          </div>
          <button
            onClick={() => {
              fetchSales();
              fetchCustomers();
            }}
            className="px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 flex items-center space-x-2"
          >
            <RefreshCw size={16} />
            <span>Retry Connection</span>
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto p-6 bg-white">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2 flex items-center">
          <CreditCard className="mr-3 text-emerald-600" />
          Accounts Receivable
        </h1>
        <p className="text-gray-600">Manage customer invoicing, payments, and credit limits</p>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="bg-blue-50 p-6 rounded-lg border border-blue-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-blue-600 text-sm font-medium">Total A/R</p>
              <p className="text-2xl font-bold text-blue-900">{formatCurrency(summary.totalAR)}</p>
            </div>
            <DollarSign className="text-blue-600" size={24} />
          </div>
          <p className="text-sm text-blue-700 mt-2">{summary.customersWithBalance} customers with balances</p>
        </div>

        <div className="bg-red-50 p-6 rounded-lg border border-red-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-red-600 text-sm font-medium">Overdue Amount</p>
              <p className="text-2xl font-bold text-red-900">{formatCurrency(summary.totalOverdue)}</p>
            </div>
            <AlertTriangle className="text-red-600" size={24} />
          </div>
          <p className="text-sm text-red-700 mt-2">
            {summary.totalAR > 0 ? ((summary.totalOverdue / summary.totalAR) * 100).toFixed(1) : 0}% of total A/R
          </p>
        </div>

        <div className="bg-green-50 p-6 rounded-lg border border-green-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-green-600 text-sm font-medium">Avg. Days Outstanding</p>
              <p className="text-2xl font-bold text-green-900">{summary.avgDaysOutstanding}</p>
            </div>
            <Clock className="text-green-600" size={24} />
          </div>
          <p className="text-sm text-green-700 mt-2">Collection performance</p>
        </div>

        <div className="bg-purple-50 p-6 rounded-lg border border-purple-200">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-purple-600 text-sm font-medium">Active Customers</p>
              <p className="text-2xl font-bold text-purple-900">{customers.data.length}</p>
            </div>
            <Users className="text-purple-600" size={24} />
          </div>
          <p className="text-sm text-purple-700 mt-2">Total customer accounts</p>
        </div>
      </div>

      {/* Tabs */}
      <div className="mb-6">
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            {[
              { id: 'overview', name: 'Customer Overview', icon: Users },
              { id: 'invoices', name: 'Invoices', icon: FileText },
              { id: 'aging', name: 'Aging Report', icon: Clock },
              { id: 'payments', name: 'Payments', icon: CheckCircle }
            ].map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`flex items-center py-2 px-1 border-b-2 font-medium text-sm ${
                  activeTab === tab.id
                    ? 'border-emerald-500 text-emerald-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <tab.icon className="mr-2" size={16} />
                {tab.name}
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Tab Content */}
      {activeTab === 'overview' && (
        <div>
          {/* Filters */}
          <div className="flex flex-wrap items-center gap-4 mb-6">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={16} />
              <input
                type="text"
                placeholder="Search customers..."
                value={filters.searchTerm}
                onChange={(e) => setFilters({ ...filters, searchTerm: e.target.value })}
                className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
              />
            </div>

            <select
              value={filters.customerType}
              onChange={(e) => setFilters({ ...filters, customerType: e.target.value })}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            >
              <option value="all">All Customer Types</option>
              <option value="corporate">Corporate</option>
              <option value="small_business">Small Business</option>
              <option value="individual">Individual</option>
              <option value="cash">Cash Only</option>
            </select>

            <select
              value={filters.status}
              onChange={(e) => setFilters({ ...filters, status: e.target.value })}
              className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-transparent"
            >
              <option value="all">All Statuses</option>
              <option value="current">Current</option>
              <option value="overdue">Overdue</option>
              <option value="paid_up">Paid Up</option>
            </select>

            <button
              onClick={() => setShowInvoiceForm(true)}
              className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2"
            >
              <Plus size={16} />
              <span>New Invoice</span>
            </button>
          </div>

          {/* Customer List */}
          <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Customer
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Type
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Credit Limit
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Balance
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Overdue
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Credit Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Last Payment
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {filteredCustomers.map((customer) => (
                    <tr key={customer.id} className="hover:bg-gray-50">
                      <td className="px-6 py-4">
                        <div>
                          <div className="text-sm font-medium text-gray-900">{customer.name}</div>
                          <div className="text-sm text-gray-500">{customer.id}</div>
                          <div className="text-xs text-gray-400 flex items-center mt-1">
                            <Mail className="mr-1" size={12} />
                            {customer.email}
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className="px-2 py-1 text-xs font-medium rounded-full bg-gray-100 text-gray-800">
                          {customer.customerType.replace('_', ' ')}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {formatCurrency(customer.creditLimit)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm font-medium text-gray-900">
                          {formatCurrency(customer.totalOwed)}
                        </div>
                        <div className="text-xs text-gray-500">
                          {customer.invoiceCount} invoices
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {customer.overdueAmount > 0 ? (
                          <span className="text-red-600 font-medium">
                            {formatCurrency(customer.overdueAmount)}
                          </span>
                        ) : (
                          <span className="text-green-600">$0.00</span>
                        )}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`px-2 py-1 text-xs font-medium rounded-full ${getCreditStatusColor(customer.creditStatus)}`}>
                          {customer.creditStatus.replace('_', ' ')}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {customer.lastPayment}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <button
                          onClick={() => setSelectedCustomer(customer)}
                          className="text-emerald-600 hover:text-emerald-900 mr-3"
                        >
                          <Eye size={16} />
                        </button>
                        <button className="text-blue-600 hover:text-blue-900">
                          <Edit size={16} />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'aging' && (
        <div>
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            {/* Aging Summary */}
            <div className="bg-white border border-gray-200 rounded-lg p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Aging Summary</h3>
              <div className="space-y-4">
                <div className="flex justify-between items-center p-3 bg-green-50 rounded">
                  <span className="font-medium text-green-800">Current (0-30 days)</span>
                  <span className="font-bold text-green-900">{formatCurrency(aging.current)}</span>
                </div>
                <div className="flex justify-between items-center p-3 bg-yellow-50 rounded">
                  <span className="font-medium text-yellow-800">31-60 days</span>
                  <span className="font-bold text-yellow-900">{formatCurrency(aging.days30)}</span>
                </div>
                <div className="flex justify-between items-center p-3 bg-orange-50 rounded">
                  <span className="font-medium text-orange-800">61-90 days</span>
                  <span className="font-bold text-orange-900">{formatCurrency(aging.days60)}</span>
                </div>
                <div className="flex justify-between items-center p-3 bg-red-50 rounded">
                  <span className="font-medium text-red-800">Over 90 days</span>
                  <span className="font-bold text-red-900">{formatCurrency(aging.over90)}</span>
                </div>
              </div>
              <div className="mt-4 pt-4 border-t">
                <div className="flex justify-between items-center">
                  <span className="text-lg font-semibold text-gray-900">Total Outstanding</span>
                  <span className="text-xl font-bold text-gray-900">
                    {formatCurrency(aging.current + aging.days30 + aging.days60 + aging.days90 + aging.over90)}
                  </span>
                </div>
              </div>
            </div>

            {/* Collection Priority */}
            <div className="bg-white border border-gray-200 rounded-lg p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Collection Priority</h3>
              <div className="space-y-3">
                {customers.data
                  .filter(customer => customer.overdueAmount > 0)
                  .sort((a, b) => b.overdueAmount - a.overdueAmount)
                  .slice(0, 5)
                  .map((customer, index) => (
                    <div key={customer.id} className="flex items-center justify-between p-3 bg-red-50 rounded">
                      <div>
                        <div className="font-medium text-gray-900">{customer.name}</div>
                        <div className="text-sm text-gray-500">
                          Last payment: {customer.lastPayment}
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="font-bold text-red-900">{formatCurrency(customer.overdueAmount)}</div>
                        <div className="text-xs text-red-700">#{index + 1} Priority</div>
                      </div>
                    </div>
                  ))}
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'invoices' && (
        <div>
          <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
            <div className="px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-semibold text-gray-900">Outstanding Orders/Invoices</h3>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Order #
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Customer
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Date
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Items
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Amount
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Payment Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Fulfillment
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {sales.data.map((order) => (
                    <tr key={order.orderId} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm font-medium text-gray-900">#{order.orderId}</div>
                        <div className="text-xs text-gray-500">{order.status}</div>
                      </td>
                      <td className="px-6 py-4">
                        <div className="text-sm font-medium text-gray-900">{getCustomerName(order.customerId)}</div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {formatDate(order.createdAt)}
                      </td>
                      <td className="px-6 py-4">
                        <div className="text-sm text-gray-900">
                          {order.lineItems.map((item, index) => (
                            <div key={index} className="mb-1">
                              <span className="font-medium">{item.productName}</span>
                              <span className="text-gray-500 ml-2">x{item.quantity}</span>
                            </div>
                          ))}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {formatCurrency(order.total)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                          order.paid ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                        }`}>
                          {order.paid ? 'Paid' : 'Unpaid'}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                          order.fulfilled ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                        }`}>
                          {order.fulfilled ? 'Fulfilled' : 'UnFulfilled'}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <button
                          className="text-emerald-600 hover:text-emerald-900 mr-3"
                          onClick={() => setViewInvoice(order)}
                        >
                          <Eye size={16} />
                        </button>
                        <button className="text-blue-600 hover:text-blue-900">
                          <Download size={16} />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {activeTab === 'payments' && (
        <div>
          <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
            <div className="px-6 py-4 border-b border-gray-200">
              <h3 className="text-lg font-semibold text-gray-900">Payment History</h3>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Order ID
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Customer
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Date
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Amount
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Payment Status
                    </th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Fulfillment Status
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {window.allSalesData?.filter(order => order.paid).map((order) => (
                    <tr key={order.orderId} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        #{order.orderId}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {getCustomerName(order.customerId)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {formatDate(order.createdAt)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {formatCurrency(order.total)}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className="px-2 py-1 text-xs font-medium rounded-full bg-green-100 text-green-800">
                          Paid
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                          order.fulfilled ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                        }`}>
                          {order.fulfilled ? 'Fulfilled' : 'Pending'}
                        </span>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            
            {(!window.allSalesData?.filter(order => order.paid).length || window.allSalesData?.filter(order => order.paid).length === 0) && (
              <div className="text-center py-12">
                <div className="text-gray-500 text-lg">No payments found</div>
                <p className="text-gray-400 mt-2">All orders are currently unpaid</p>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Customer Detail Modal */}
      {selectedCustomer && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="px-6 py-4 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">
                  Customer Details - {selectedCustomer.name}
                </h3>
                <button
                  onClick={() => setSelectedCustomer(null)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <XCircle size={20} />
                </button>
              </div>
            </div>
            
            <div className="px-6 py-4">
              {/* Customer Info Grid */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Customer ID</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.id}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Email</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.email}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Phone</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.phone}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Address</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.address}</p>
                  </div>
                </div>
                
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Customer Type</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.customerType.replace('_', ' ')}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Credit Limit</label>
                    <p className="text-sm text-gray-900">{formatCurrency(selectedCustomer.creditLimit)}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Payment Terms</label>
                    <p className="text-sm text-gray-900">{selectedCustomer.paymentTerms} days</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Credit Status</label>
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getCreditStatusColor(selectedCustomer.creditStatus)}`}>
                      {selectedCustomer.creditStatus.replace('_', ' ')}
                    </span>
                  </div>
                </div>
              </div>

              {/* Financial Summary */}
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
                <div className="bg-blue-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-blue-600">Current Balance</div>
                  <div className="text-xl font-bold text-blue-900">
                    {formatCurrency(selectedCustomer.totalOwed)}
                  </div>
                </div>
                <div className="bg-red-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-red-600">Overdue Amount</div>
                  <div className="text-xl font-bold text-red-900">
                    {formatCurrency(selectedCustomer.overdueAmount)}
                  </div>
                </div>
                <div className="bg-green-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-green-600">Credit Available</div>
                  <div className="text-xl font-bold text-green-900">
                    {formatCurrency(selectedCustomer.creditLimit - selectedCustomer.totalOwed)}
                  </div>
                </div>
                <div className="bg-purple-50 p-4 rounded-lg">
                  <div className="text-sm font-medium text-purple-600">Avg Payment Days</div>
                  <div className="text-xl font-bold text-purple-900">
                    {selectedCustomer.avgPaymentDays}
                  </div>
                </div>
              </div>

              {/* Customer Orders */}
              <div className="mb-6">
                <h4 className="text-md font-semibold text-gray-900 mb-3">Customer Orders</h4>
                <div className="overflow-x-auto">
                  <table className="w-full border border-gray-200 rounded">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Order</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Date</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Items</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Amount</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Payment</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Fulfillment</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200">
                      {(window.allSalesData || [])
                        .filter(order => {
                          if (selectedCustomer.id.startsWith('guest-')) {
                            return order.orderId === selectedCustomer.orderId;
                          }
                          return order.customerId === selectedCustomer.id;
                        })
                        .map((order) => (
                          <tr key={order.orderId}>
                            <td className="px-4 py-2 text-sm text-gray-900">#{order.orderId}</td>
                            <td className="px-4 py-2 text-sm text-gray-900">{formatDate(order.createdAt)}</td>
                            <td className="px-4 py-2 text-sm text-gray-900">
                              {order.lineItems.map(item => item.productName).join(', ')}
                            </td>
                            <td className="px-4 py-2 text-sm text-gray-900 text-right">{formatCurrency(order.total)}</td>
                            <td className="px-4 py-2">
                              <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                                order.paid ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                              }`}>
                                {order.paid ? 'Paid' : 'Unpaid'}
                              </span>
                            </td>
                            <td className="px-4 py-2">
                              <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                                order.fulfilled ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                              }`}>
                                {order.fulfilled ? 'Fulfilled' : 'Pending'}
                              </span>
                            </td>
                          </tr>
                        ))}
                    </tbody>
                  </table>
                </div>
                
                {(window.allSalesData || [])
                  .filter(order => {
                    if (selectedCustomer.id.startsWith('guest-')) {
                      return order.orderId === selectedCustomer.orderId;
                    }
                    return order.customerId === selectedCustomer.id;
                  }).length === 0 && (
                    <div className="text-center py-4 text-gray-500">
                      No orders found for this customer
                    </div>
                  )}
              </div>

              {/* Action Buttons */}
              <div className="flex space-x-3">
                <button className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2">
                  <Plus size={16} />
                  <span>New Invoice</span>
                </button>
                <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center space-x-2">
                  <CheckCircle size={16} />
                  <span>Record Payment</span>
                </button>
                <button className="px-4 py-2 bg-yellow-600 text-white rounded-lg hover:bg-yellow-700 flex items-center space-x-2">
                  <Mail size={16} />
                  <span>Send Statement</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Order/Invoice Detail Modal */}
      {viewInvoice && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="px-6 py-4 border-b border-gray-200">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold text-gray-900">
                  Order Details - #{viewInvoice.orderId}
                </h3>
                <button
                  onClick={() => setViewInvoice(null)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <XCircle size={20} />
                </button>
              </div>
            </div>
            
            <div className="px-6 py-4">
              {/* Order Info */}
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Order ID</label>
                    <p className="text-sm text-gray-900">{viewInvoice.orderId}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Customer</label>
                    <p className="text-sm text-gray-900">{getCustomerName(viewInvoice.customerId)}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Created Date</label>
                    <p className="text-sm text-gray-900">{formatDate(viewInvoice.createdAt)}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Status</label>
                    <p className="text-sm text-gray-900">{viewInvoice.status}</p>
                  </div>
                </div>
                
                <div className="space-y-4">
                  <div>
                    <label className="text-sm font-medium text-gray-500">Payment Status</label>
                    <div>
                      <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                        viewInvoice.paid ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
                      }`}>
                        {viewInvoice.paid ? 'Paid' : 'Unpaid'}
                      </span>
                    </div>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Fulfillment Status</label>
                    <div>
                      <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                        viewInvoice.fulfilled ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {viewInvoice.fulfilled ? 'Fulfilled' : 'Pending'}
                      </span>
                    </div>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Total Amount</label>
                    <p className="text-lg font-bold text-gray-900">{formatCurrency(viewInvoice.total)}</p>
                  </div>
                  <div>
                    <label className="text-sm font-medium text-gray-500">Payable Amount</label>
                    <p className="text-sm text-gray-900">{formatCurrency(viewInvoice.payableAmount)}</p>
                  </div>
                </div>
              </div>

              {/* Line Items */}
              <div className="mb-6">
                <h4 className="text-md font-semibold text-gray-900 mb-3">Order Items</h4>
                <div className="overflow-x-auto">
                  <table className="w-full border border-gray-200 rounded">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Product</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Quantity</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Unit Price</th>
                        <th className="px-4 py-2 text-right text-xs font-medium text-gray-500">Total</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500">Status</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-gray-200">
                      {viewInvoice.lineItems.map((item, index) => (
                        <tr key={index}>
                          <td className="px-4 py-2 text-sm text-gray-900">{item.productName}</td>
                          <td className="px-4 py-2 text-sm text-gray-900 text-right">{item.quantity}</td>
                          <td className="px-4 py-2 text-sm text-gray-900 text-right">{formatCurrency(item.price)}</td>
                          <td className="px-4 py-2 text-sm font-medium text-gray-900 text-right">{formatCurrency(item.total)}</td>
                          <td className="px-4 py-2">
                            <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                              item.fulfilled ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                            }`}>
                              {item.status}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>

              {/* Totals */}
              <div className="bg-gray-50 p-4 rounded-lg">
                <div className="space-y-2">
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600">Subtotal:</span>
                    <span className="text-sm text-gray-900">{formatCurrency(viewInvoice.subTotal)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600">Tax ({viewInvoice.taxRate}%):</span>
                    <span className="text-sm text-gray-900">{formatCurrency(viewInvoice.taxAmount)}</span>
                  </div>
                  {viewInvoice.discountAmount > 0 && (
                    <div className="flex justify-between">
                      <span className="text-sm text-gray-600">Discount:</span>
                      <span className="text-sm text-red-600">-{formatCurrency(viewInvoice.discountAmount)}</span>
                    </div>
                  )}
                  <div className="flex justify-between font-bold text-lg border-t pt-2">
                    <span>Total:</span>
                    <span>{formatCurrency(viewInvoice.total)}</span>
                  </div>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="flex space-x-3 mt-6">
                {!viewInvoice.paid && (
                  <button className="px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 flex items-center space-x-2">
                    <CheckCircle size={16} />
                    <span>Mark as Paid</span>
                  </button>
                )}
                {!viewInvoice.fulfilled && (
                  <button className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 flex items-center space-x-2">
                    <FileText size={16} />
                    <span>Mark as Fulfilled</span>
                  </button>
                )}
                <button className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 flex items-center space-x-2">
                  <Download size={16} />
                  <span>Download Invoice</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* API Integration Status */}
      <div className="mt-8 p-6 bg-green-50 border border-green-200 rounded-lg">
        <div className="flex items-center mb-4">
          <CheckCircle className="text-green-600 mr-2" size={20} />
          <h3 className="font-medium text-green-800">POS Integration Active</h3>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm text-green-700">
          <div>
            <strong>Sales Orders:</strong> {sales.data.length} outstanding orders from POS system
          </div>
          <div>
            <strong>Customers:</strong> {customers.data.length} active customers synchronized
          </div>
          <div>
            <strong>Real-time Updates:</strong> Data refreshes automatically with POS transactions
          </div>
        </div>
        <div className="mt-4 text-xs text-green-600">
          <strong>Note:</strong> Only showing unpaid or unfulfilled orders. Replace Bearer token for live data connection.
        </div>
      </div>
    </div>
  );
};

export default AccountsReceivable;
