        avgPaymentDays: 0
      }
    ];

    // Sample Invoices
    const sampleInvoices = [
      {
        id: 'INV-2025-001',
        customerId: 'CUST001',
        customerName: 'Al Noor Medical Center',
        date: '2025-01-15',
