// export default InvoiceDetailModal;



import React, { useState, useEffect } from 'react';
import { XCircle, CheckCircle, FileText, Download, DollarSign } from 'lucide-react';
import { formatCurrency, formatDate } from '../components/AccountsReceivable/AccountsReceivableUtils/formatters';
import RecordPaymentModal from '../components/AccountsReceivable/RecordPaymentModal';

const InvoiceDetailModal = ({ invoice, onClose, getCustomerName, onRefresh }) => {
  const [isPaymentModalOpen, setPaymentModalOpen] = useState(false);
  const [localInvoice, setLocalInvoice] = useState(invoice);
  const [paymentHistory, setPaymentHistory] = useState([]);

  useEffect(() => {
    if (invoice) {
      setLocalInvoice(invoice);
      loadPaymentHistory();
    }
  }, [invoice]);

  const loadPaymentHistory = () => {
    const storedPayments = JSON.parse(localStorage.getItem('payments') || '[]');
    const invoicePayments = storedPayments.filter(
      payment => payment.invoiceId === invoice.orderId
    );
    setPaymentHistory(invoicePayments);
  };

  const handlePaymentComplete = (paymentData, isFullPayment) => {
    // Update invoice based on payment
    setLocalInvoice(prev => ({
      ...prev,
      paid: isFullPayment,
      paymentStatus: isFullPayment ? 'paid' : 'partial',
      remainingAmount: isFullPayment ? 0 : paymentData.remainingAmount
    }));

    // Update localStorage
    const existingOrders = JSON.parse(localStorage.getItem('orders') || '[]');
    const orderIndex = existingOrders.findIndex(o => o.orderId === localInvoice.orderId);
    if (orderIndex !== -1) {
      existingOrders[orderIndex] = {
        ...existingOrders[orderIndex],
        paid: isFullPayment,
        paymentStatus: isFullPayment ? 'paid' : 'partial',
        remainingAmount: isFullPayment ? 0 : paymentData.remainingAmount,
        paymentDate: isFullPayment ? new Date().toISOString() : existingOrders[orderIndex].paymentDate
      };
      localStorage.setItem('orders', JSON.stringify(existingOrders));
    }

    loadPaymentHistory();
    setPaymentModalOpen(false);
    if (onRefresh) onRefresh();
  };

  const handleMarkAsPaid = () => {
    handlePaymentComplete({ remainingAmount: 0 }, true);
  };

  const handleMarkAsFulfilled = () => {
    const existingOrders = JSON.parse(localStorage.getItem('orders') || '[]');
    const orderIndex = existingOrders.findIndex(o => o.orderId === localInvoice.orderId);
    if (orderIndex !== -1) {
      existingOrders[orderIndex] = {
        ...existingOrders[orderIndex],
        fulfilled: true,
        fulfillmentDate: new Date().toISOString()
      };
      localStorage.setItem('orders', JSON.stringify(existingOrders));
    }
    setLocalInvoice(prev => ({ ...prev, fulfilled: true }));
    if (onRefresh) onRefresh();
  };

  const getStatusBadge = (invoice) => {
    if (invoice.paid) return <span className="px-2 py-1 text-xs font-medium rounded-full bg-green-100 text-green-800">Paid</span>;
    if (invoice.paymentStatus === 'partial') return <span className="px-2 py-1 text-xs font-medium rounded-full bg-orange-100 text-orange-800">Partial Payment</span>;
    return <span className="px-2 py-1 text-xs font-medium rounded-full bg-red-100 text-red-800">Unpaid</span>;
  };

  const getFulfillmentBadge = (invoice) => {
    return invoice.fulfilled
      ? <span className="px-2 py-1 text-xs font-medium rounded-full bg-blue-100 text-blue-800">Fulfilled</span>
      : <span className="px-2 py-1 text-xs font-medium rounded-full bg-yellow-100 text-yellow-800">Pending Fulfillment</span>;
  };

  return (
    <>
      <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
        <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
          <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
            <h3 className="text-lg font-semibold text-gray-900">Order Details - #{localInvoice.orderId}</h3>
            <button onClick={onClose} className="text-gray-400 hover:text-gray-600">
              <XCircle size={20} />
            </button>
          </div>

          <div className="px-6 py-4">
            {/* Order Info */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
              <div className="space-y-4">
                <div>
                  <label className="text-sm font-medium text-gray-500">Order ID</label>
                  <p className="text-sm text-gray-900">{localInvoice.orderId}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-500">Customer</label>
                  <p className="text-sm text-gray-900">{getCustomerName(localInvoice.customerId)}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-500">Created Date</label>
                  <p className="text-sm text-gray-900">{formatDate(localInvoice.createdAt)}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-500">Status</label>
                  <p className="text-sm text-gray-900">{localInvoice.status}</p>
                </div>
              </div>
              <div className="space-y-4">
                <div>
                  <label className="text-sm font-medium text-gray-500">Payment Status</label>
                  <div className="flex items-center gap-2 mt-1">
                    {getStatusBadge(localInvoice)}
                    {localInvoice.paymentStatus === 'partial' && (
                      <span className="text-xs text-gray-500">
                        ({formatCurrency(localInvoice.remainingAmount || 0)} remaining)
                      </span>
                    )}
                  </div>
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-500">Fulfillment Status</label>
                  <div className="mt-1">{getFulfillmentBadge(localInvoice)}</div>
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-500">Total Amount</label>
                  <p className="text-sm font-medium text-gray-900">{formatCurrency(localInvoice.total)}</p>
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-500">Last Updated</label>
                  <p className="text-sm text-gray-900">{formatDate(localInvoice.updatedAt || localInvoice.createdAt)}</p>
                </div>
              </div>
            </div>

            {/* Order Items with Description */}
            <div className="mb-6">
              <h4 className="text-md font-medium text-gray-900 mb-3">Order Items</h4>
              <div className="border border-gray-200 rounded-lg overflow-hidden">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Item</th>
                      <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Description</th>
                      <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Quantity</th>
                      <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Unit Price</th>
                      <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Total</th>
                      <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {localInvoice.items && localInvoice.items.map((item, index) => (
                      <tr key={index}>
                        <td className="px-4 py-3 text-sm text-gray-900">{item.name}</td>
                        <td className="px-4 py-3 text-sm text-gray-900">{item.description || '-'}</td>
                        <td className="px-4 py-3 text-sm text-gray-900">{item.quantity}</td>
                        <td className="px-4 py-3 text-sm text-gray-900">{formatCurrency(item.price)}</td>
                        <td className="px-4 py-3 text-sm text-gray-900">{formatCurrency(item.quantity * item.price)}</td>
                        <td className="px-4 py-3">
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                            item.fulfilled ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'
                          }`}>
                            {item.status || (item.fulfilled ? 'Fulfilled' : 'Pending')}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            {/* Payment History */}
            {paymentHistory.length > 0 && (
              <div className="mb-6">
                <h4 className="text-md font-medium text-gray-900 mb-3">Payment History</h4>
                <div className="border border-gray-200 rounded-lg overflow-hidden">
                  <table className="min-w-full divide-y divide-gray-200">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Date</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Amount</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Method</th>
                        <th className="px-4 py-2 text-left text-xs font-medium text-gray-500 uppercase">Memo</th>
                      </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                      {paymentHistory.map((payment, index) => (
                        <tr key={index}>
                          <td className="px-4 py-3 text-sm text-gray-900">{formatDate(payment.date)}</td>
                          <td className="px-4 py-3 text-sm text-gray-900">{formatCurrency(payment.amount)}</td>
                          <td className="px-4 py-3 text-sm text-gray-900">{payment.paymentMethod}</td>
                          <td className="px-4 py-3 text-sm text-gray-600">{payment.memo || '-'}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}

            {/* Action Buttons */}
            <div className="flex flex-wrap gap-3 pt-4 border-t border-gray-200">
              {!localInvoice.paid && (
                <button
                  onClick={() => setPaymentModalOpen(true)}
                  className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                >
                  <DollarSign size={16} /> Record Payment
                </button>
              )}
              {!localInvoice.paid && (
                <button
                  onClick={handleMarkAsPaid}
                  className="flex items-center gap-2 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
                >
                  <CheckCircle size={16} /> Mark as Paid
                </button>
              )}
              {!localInvoice.fulfilled && (
                <button
                  onClick={handleMarkAsFulfilled}
                  className="flex items-center gap-2 px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition-colors"
                >
                  <CheckCircle size={16} /> Mark as Fulfilled
                </button>
              )}
              <button className="flex items-center gap-2 px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors">
                <Download size={16} /> Download Invoice
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Payment Modal */}
      {isPaymentModalOpen && (
        <RecordPaymentModal
          invoiceId={localInvoice.orderId}
          amount={localInvoice.remainingAmount || localInvoice.total}
          onClose={() => setPaymentModalOpen(false)}
          onPaymentComplete={handlePaymentComplete}
        />
      )}
    </>
  );
};

export default InvoiceDetailModal;
