import React, { useState, useEffect } from 'react';
import { X, Calendar, DollarSign, CreditCard, FileText, AlertCircle } from 'lucide-react';
import { formatCurrency } from './AccountsReceivableUtils/formatters';

const RecordPaymentModal = ({ invoiceId, amount, onClose, onSubmit, onPaymentComplete }) => {
  const [formData, setFormData] = useState({
    date: new Date().toISOString().split('T')[0],
    amount: amount?.toString() || '',
    method: '',
    account: '',
    memo: ''
  });
  
  const [accounts, setAccounts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});

  // Fetch accounts on mount
  useEffect(() => {
    fetchAccounts();
  }, []);

  // Set initial amount
  useEffect(() => {
    if (amount) {
      setFormData(prev => ({ ...prev, amount: amount.toString() }));
    }
  }, [amount]);

  const fetchAccounts = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/accounts');
      const data = await response.json();

      // Filter only asset accounts that can receive payments
      const paymentAccounts = data.filter(acct => 
        acct.type === 'assets' && 
        acct.category === 'account' &&
        (acct.name.toLowerCase().includes('bank') || 
         acct.name.toLowerCase().includes('cash') ||
         acct.name.toLowerCase().includes('checking') ||
         acct.name.toLowerCase().includes('savings'))
      );

      setAccounts(paymentAccounts);
    } catch (err) {
      console.error('Failed to fetch accounts:', err);
    }
  };

  const handleChange = (field, value) => {
    setFormData(prev => ({ ...prev, [field]: value }));
    // Clear error for this field when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.date) {
      newErrors.date = 'Payment date is required';
    }
    
    if (!formData.amount || parseFloat(formData.amount) <= 0) {
      newErrors.amount = 'Valid payment amount is required';
    }
    
    if (!formData.method) {
      newErrors.method = 'Payment method is required';
    }
    
    if (!formData.account) {
      newErrors.account = 'Deposit account is required';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async () => {
    if (!validateForm()) return;

    setLoading(true);
    
    try {
      const paymentAmount = parseFloat(formData.amount);
      const remainingAmount = amount - paymentAmount;
      const isFullPayment = remainingAmount <= 0;
      const paymentStatus = isFullPayment ? 'paid' : 'partial';
      
      // Create payment record
      const paymentData = {
        id: `PAY-${Date.now()}`,
        invoiceId,
        orderId: invoiceId,
        date: formData.date,
        amount: paymentAmount,
        paymentMethod: formData.method,
        accountId: formData.account,
        accountName: accounts.find(a => a.id === formData.account)?.name || 'Unknown Account',
        memo: formData.memo,
        status: paymentStatus,
        remainingAmount: Math.max(0, remainingAmount),
        createdAt: new Date().toISOString(),
        createdBy: 'current_user' // Replace with actual user
      };

      // Save payment to localStorage
      const existingPayments = JSON.parse(localStorage.getItem('payments') || '[]');
      const updatedPayments = [...existingPayments, paymentData];
      localStorage.setItem('payments', JSON.stringify(updatedPayments));

      // Update the invoice/order status
      const existingOrders = JSON.parse(localStorage.getItem('orders') || '[]');
      const orderIndex = existingOrders.findIndex(order => 
        order.orderId === invoiceId || order.id === invoiceId
      );
      
      if (orderIndex !== -1) {
        existingOrders[orderIndex] = {
          ...existingOrders[orderIndex],
          paid: isFullPayment,
          paymentStatus: paymentStatus,
          lastPaymentDate: formData.date,
          lastPaymentAmount: paymentAmount,
          remainingAmount: Math.max(0, remainingAmount),
          paymentHistory: [
            ...(existingOrders[orderIndex].paymentHistory || []),
            paymentData.id
          ]
        };
        
        localStorage.setItem('orders', JSON.stringify(existingOrders));
      }

      // Call the callback with payment details
      if (onSubmit) {
        onSubmit(paymentData, isFullPayment);
      }
      
      // Trigger any additional completion actions
      if (onPaymentComplete) {
        onPaymentComplete(paymentData, isFullPayment);
      }

      // Show success message and close
      const message = isFullPayment 
        ? 'Payment recorded successfully. Invoice is now fully paid.'
        : `Partial payment of ${formatCurrency(paymentAmount)} recorded. Remaining balance: ${formatCurrency(remainingAmount)}`;
      
      // You can show this message using a toast notification if available
      console.log(message);
      
      onClose();
    } catch (error) {
      console.error('Error recording payment:', error);
      setErrors({ general: 'Failed to record payment. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  const paymentAmount = parseFloat(formData.amount) || 0;
  const remainingAmount = amount - paymentAmount;
  const isFullPayment = paymentAmount >= amount;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-xl shadow-2xl max-w-md w-full">
        {/* Header */}
        <div className="px-6 py-4 border-b border-gray-200">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold text-gray-900">
              Record a payment for this invoice
            </h2>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600 transition-colors"
            >
              <X size={24} />
            </button>
          </div>
        </div>

        {/* Form Body */}
        <div className="px-6 py-4 space-y-5">
          {errors.general && (
            <div className="p-3 bg-red-50 border border-red-200 rounded-lg flex items-start gap-2">
              <AlertCircle size={20} className="text-red-600 flex-shrink-0 mt-0.5" />
              <span className="text-sm text-red-700">{errors.general}</span>
            </div>
          )}

          {/* Date Field */}
          <div>
            <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-1">
              <Calendar size={16} />
              Date
            </label>
            <input
              type="date"
              value={formData.date}
              onChange={(e) => handleChange('date', e.target.value)}
              className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                errors.date ? 'border-red-500' : 'border-gray-300'
              }`}
            />
            {errors.date && <span className="text-xs text-red-600 mt-1">{errors.date}</span>}
          </div>

          {/* Amount Field */}
          <div>
            <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-1">
              <DollarSign size={16} />
              Amount
            </label>
            <div className="relative">
              <span className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-500 font-medium">
                Rs
              </span>
              <input
                type="number"
                step="0.01"
                value={formData.amount}
                onChange={(e) => handleChange('amount', e.target.value)}
                className={`w-full pl-10 pr-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                  errors.amount ? 'border-red-500' : 'border-gray-300'
                }`}
                placeholder="0.00"
              />
            </div>
            {errors.amount && <span className="text-xs text-red-600 mt-1">{errors.amount}</span>}
            
            {paymentAmount > 0 && (
              <div className="mt-2 text-sm">
                {isFullPayment ? (
                  <span className="text-green-600 font-medium">Invoice will be fully paid</span>
                ) : (
                  <span className="text-orange-600">
                    Partial payment - Remaining: {formatCurrency(remainingAmount)}
                  </span>
                )}
              </div>
            )}
          </div>

          {/* Payment Method */}
          <div>
            <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-1">
              <CreditCard size={16} />
              Method
            </label>
            <select
              value={formData.method}
              onChange={(e) => handleChange('method', e.target.value)}
              className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                errors.method ? 'border-red-500' : 'border-gray-300'
              }`}
            >
              <option value="">Select a payment method...</option>
              <option value="cash">Cash</option>
              <option value="check">Check</option>
              <option value="bank_transfer">Bank Transfer</option>
              <option value="credit_card">Credit Card</option>
              <option value="debit_card">Debit Card</option>
              <option value="online">Online Payment</option>
            </select>
            {errors.method && <span className="text-xs text-red-600 mt-1">{errors.method}</span>}
          </div>

          {/* Account */}
          <div>
            <label className="flex items-center gap-2 text-sm font-medium text-gray-700 mb-1">
              <FileText size={16} />
              Account
            </label>
            <select
              value={formData.account}
              onChange={(e) => handleChange('account', e.target.value)}
              className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                errors.account ? 'border-red-500' : 'border-gray-300'
              }`}
            >
              <option value="">Select a payment account...</option>
              {accounts.map(account => (
                <option key={account.id} value={account.id}>
                  {account.code} - {account.name}
                </option>
              ))}
            </select>
            {errors.account && <span className="text-xs text-red-600 mt-1">{errors.account}</span>}
            <p className="text-xs text-gray-500 mt-1">
              Any account into which you deposit and withdraw funds from.{' '}
              <a href="#" className="text-blue-600 hover:text-blue-700">Learn more</a>
            </p>
          </div>

          {/* Memo */}
          <div>
            <label className="text-sm font-medium text-gray-700 mb-1 block">
              Memo / notes
            </label>
            <textarea
              value={formData.memo}
              onChange={(e) => handleChange('memo', e.target.value)}
              rows={3}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              placeholder="Add any additional notes..."
            />
          </div>
        </div>

        {/* Footer */}
        <div className="px-6 py-4 border-t border-gray-200 flex items-center justify-end gap-3">
          <button
            onClick={onClose}
            className="px-4 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 transition-colors"
          >
            Cancel
          </button>
          <button
            onClick={handleSubmit}
            disabled={loading || !formData.amount || parseFloat(formData.amount) <= 0}
            className={`px-6 py-2 rounded-lg font-medium transition-colors ${
              loading || !formData.amount || parseFloat(formData.amount) <= 0
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                : 'bg-blue-600 text-white hover:bg-blue-700'
            }`}
          >
            {loading ? 'Processing...' : 'Submit'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default RecordPaymentModal;
