import React, { useState, useEffect } from 'react';
import { XCircle, CheckCircle, Download, DollarSign } from 'lucide-react';
import { formatCurrency, formatDate } from '../components/AccountsReceivable/AccountsReceivableUtils/formatters';
import RecordPaymentModal from '../components/AccountsReceivable/RecordPaymentModal';
import { jsxDEV as _jsxDEV, Fragment as _Fragment } from "react/jsx-dev-runtime";
const InvoiceDetailModal = ({
  invoice,
  onClose,
  getCustomerName,
  onRefresh
}) => {
  _s();
  const [localInvoice, setLocalInvoice] = useState(invoice);
  const [isPaymentModalOpen, setPaymentModalOpen] = useState(false);
  const [paymentHistory, setPaymentHistory] = useState([]);
  useEffect(() => {
    if (invoice) {
      setLocalInvoice(invoice);
      loadPaymentHistory();
    }
  }, [invoice]);
  const loadPaymentHistory = () => {
    const storedPayments = JSON.parse(localStorage.getItem('payments') || '[]');
    const invoicePayments = storedPayments.filter(p => p.invoiceId === invoice.orderId || p.orderId === invoice.orderId);
    setPaymentHistory(invoicePayments);
  };
  const handlePaymentComplete = (paymentData, isFullPayment) => {
    if (isFullPayment) {
      setLocalInvoice(prev => ({
        ...prev,
        paid: true,
        paymentStatus: 'paid',
        remainingAmount: 0
      }));
    } else {
      setLocalInvoice(prev => ({
        ...prev,
        paid: false,
        paymentStatus: 'partial',
        remainingAmount: paymentData.remainingAmount
      }));
    }
    loadPaymentHistory();
    if (onRefresh) onRefresh();
    setPaymentModalOpen(false);
  };
  const handleMarkAsPaid = () => {
    const orders = JSON.parse(localStorage.getItem('orders') || '[]');
    const index = orders.findIndex(o => o.orderId === localInvoice.orderId || o.id === localInvoice.orderId);
    if (index !== -1) {
      orders[index] = {
        ...orders[index],
        paid: true,
        paymentStatus: 'paid',
        remainingAmount: 0
      };
      localStorage.setItem('orders', JSON.stringify(orders));
    }
    setLocalInvoice(prev => ({
      ...prev,
      paid: true,
      paymentStatus: 'paid',
      remainingAmount: 0
    }));
    if (onRefresh) onRefresh();
  };
  const handleMarkAsFulfilled = () => {
    const orders = JSON.parse(localStorage.getItem('orders') || '[]');
    const index = orders.findIndex(o => o.orderId === localInvoice.orderId || o.id === localInvoice.orderId);
    if (index !== -1) {
