 import React, { useState } from 'react';
import { 
  CheckCircle, 
  Download, 
  X, 
  Filter 
} from 'lucide-react';

// Utility functions to mimic existing utility imports
const formatCurrency = (amount) => {
  return new Intl.NumberFormat('en-US', { 
    style: 'currency', 
    currency: 'USD' 
  }).format(amount);
};

const getVendorName = (vendorId, vendors = []) => {
  const vendor = vendors.find(v => v.id === vendorId);
  return vendor ? vendor.name : 'Unknown Vendor';
};

const APPaymentsTab = () => {
  // Mock data - in a real application, this would come from state or props
  const [vendors] = useState([
    { id: 1, name: 'Hamid Shaikh' },
    { id: 2, name: 'Walk-in Vendor' }
  ]);

  const [payments, setPayments] = useState([
    {
      id: 1,
      vendorId: 1,
      amount: 630000.00,
      date: '2025-08-28',
      method: 'Bank Transfer',
      accountName: 'Accounts Receivable',
      accountType: 'assets',
      status: 'Paid',
      memo: 'BAD'
    },
    {
      id: 3,
      vendorId: 2,
      amount: 50000.00,
      date: '2025-08-27',
      method: 'Cash',
      accountName: 'Current Assets',
      accountType: 'Not specified',
      status: 'Paid',
      memo: 'ok'
    }
  ]);

  const [filters, setFilters] = useState({
    customer: 'All',
    paymentMethod: 'All',
    account: 'All',
    accountType: 'All',
    status: 'All'
  });

  const [selectedPayment, setSelectedPayment] = useState(null);

  const handleRecordPayment = (payment) => {
    setSelectedPayment(payment);
  };

  const handleSubmitPayment = (paymentDetails) => {
    const newPayment = {
      id: payments.length + 1,
      ...paymentDetails,
      date: new Date().toISOString().split('T')[0],
      status: 'Paid'
    };
    setPayments([...payments, newPayment]);
    setSelectedPayment(null);
  };

  const filteredPayments = payments.filter(payment => {
    return (
      (filters.customer === 'All' || getVendorName(payment.vendorId, vendors) === filters.customer) &&
      (filters.paymentMethod === 'All' || payment.method === filters.paymentMethod) &&
      (filters.account === 'All' || payment.accountName === filters.account) &&
      (filters.accountType === 'All' || payment.accountType === filters.accountType) &&
      (filters.status === 'All' || payment.status === filters.status)
    );
  });

  const totalPayments = filteredPayments.reduce((sum, payment) => sum + payment.amount, 0);

  return (
    <div className="p-6">
      <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200 flex justify-between items-center">
          <h3 className="text-lg font-semibold text-gray-900">Payment History</h3>
          <button className="text-gray-600 hover:text-gray-900">
            <CheckCircle size={20} />
          </button>
        </div>

        {/* Filters */}
        <div className="px-6 py-4 bg-gray-50 border-b border-gray-200">
          <div className="flex space-x-4">
            {Object.keys(filters).map((filterKey) => (
              <div key={filterKey} className="relative">
                <select
                  value={filters[filterKey]}
                  onChange={(e) => setFilters({...filters, [filterKey]: e.target.value})}
                  className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 appearance-none"
                >
                  <option value="All">{filterKey.charAt(0).toUpperCase() + filterKey.slice(1)}</option>
                  {filterKey === 'customer' && vendors.map(vendor => (
                    <option key={vendor.id} value={vendor.name}>{vendor.name}</option>
                  ))}
                  {filterKey === 'paymentMethod' && ['Cash', 'Bank Transfer', 'Not specified'].map(method => (
                    <option key={method} value={method}>{method}</option>
                  ))}
                  {filterKey === 'account' && ['Accounts Receivable', 'Current Assets', 'Not specified'].map(account => (
                    <option key={account} value={account}>{account}</option>
                  ))}
                  {filterKey === 'accountType' && ['assets', 'Not specified'].map(type => (
                    <option key={type} value={type}>{type}</option>
                  ))}
                  {filterKey === 'status' && ['Paid', 'Unpaid'].map(status => (
                    <option key={status} value={status}>{status}</option>
                  ))}
                </select>
                {filters[filterKey] !== 'All' && (
                  <button
                    onClick={() => setFilters({...filters, [filterKey]: 'All'})}
                    className="absolute right-2 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-gray-600"
                  >
                    <X size={16} />
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Payments Table */}
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Invoice ID
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Vendor
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Payment Date
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Amount
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Payment Method
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Account Name
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Account Type
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Memo
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredPayments.map((payment) => (
                <tr key={payment.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    {payment.id}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {getVendorName(payment.vendorId, vendors)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {payment.date}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {formatCurrency(payment.amount)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {payment.method}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {payment.accountName}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {payment.accountType}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${
                      payment.status === 'Paid' 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-red-100 text-red-800'
                    }`}>
                      {payment.status}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {payment.memo}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                    <button
                      onClick={() => handleRecordPayment(payment)}
                      className="text-blue-600 hover:text-blue-900"
                    >
                      Record Payment
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Total */}
        <div className="px-6 py-4 bg-gray-50 border-t border-gray-200 flex justify-between items-center">
          <span className="text-sm font-medium text-gray-600">Total Payments</span>
          <span className="text-lg font-bold text-gray-900">
            {formatCurrency(totalPayments)}
          </span>
        </div>
      </div>

      {/* Record Payment Modal */}
      {selectedPayment && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg max-w-md w-full p-6">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-semibold text-gray-900">Record Payment</h3>
              <button 
                onClick={() => setSelectedPayment(null)}
                className="text-gray-400 hover:text-gray-600"
              >
                <X size={20} />
              </button>
            </div>

            <form onSubmit={(e) => {
              e.preventDefault();
              const formData = new FormData(e.target);
              handleSubmitPayment({
                vendorId: selectedPayment.vendorId,
                amount: parseFloat(formData.get('amount')),
                method: formData.get('method'),
                accountName: formData.get('account'),
                memo: formData.get('memo')
              });
            }}>
              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Current Remaining Balance
                </label>
                <input 
                  type="text" 
                  value={formatCurrency(selectedPayment.amount)} 
                  readOnly 
                  className="w-full px-3 py-2 bg-gray-100 border border-gray-300 rounded-lg"
                />
              </div>

              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">Date</label>
                <input 
                  type="date" 
                  name="date"
                  defaultValue={new Date().toISOString().split('T')[0]}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                />
              </div>

              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">Amount</label>
                <input 
                  type="number" 
                  name="amount"
                  defaultValue={selectedPayment.amount}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                />
              </div>

              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">Payment Method</label>
                <select 
                  name="method"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                >
                  <option>Select a payment method...</option>
                  <option>Cash</option>
                  <option>Bank Transfer</option>
                  <option>Credit Card</option>
                </select>
              </div>

              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">Account</label>
                <select 
                  name="account"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                >
                  <option>Select a payment account...</option>
                  <option>Accounts Receivable</option>
                  <option>Current Assets</option>
                </select>
              </div>

              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-2">Memo</label>
                <textarea 
                  name="memo"
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg"
                  placeholder="Add any additional notes..."
                ></textarea>
              </div>

              <div className="flex justify-end space-x-2">
                <button 
                  type="button"
                  onClick={() => setSelectedPayment(null)}
                  className="px-4 py-2 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300"
                >
                  Cancel
                </button>
                <button 
                  type="submit"
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                >
                  Submit
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default APPaymentsTab;
