import React, { useState, useEffect } from 'react';
import { formatCurrency, formatDate } from './AccountsReceivableUtils/formatters';

const APPaymentsTab = ({ allSalesData, getCustomerName }) => {
  const [payments, setPayments] = useState([]);

  useEffect(() => {
    // Load payments from localStorage
    const storedPayments = JSON.parse(localStorage.getItem('payments') || '[]');
    setPayments(storedPayments);
  }, []);

  // Get paid orders from both API and localStorage
  const paidOrders = allSalesData?.filter(order => order.paid && order.fulfilled) || [];
  
  // Combine API orders with localStorage payments
  const allPayments = [...paidOrders, ...payments];

  return (
    <div>
      <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">Payment History</h3>
        </div>
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Invoice ID
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Customer
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Date
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Amount
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Payment Method
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {allPayments.map((payment, index) => (
                <tr key={payment.id || index} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                    #{payment.invoiceId || payment.orderId}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {payment.customerId ? getCustomerName(payment.customerId) || 'Unknown / Guest' : 'N/A'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {formatDate(payment.date || payment.createdAt)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {formatCurrency(payment.amount || payment.total)}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                    {payment.paymentMethod || 'N/A'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className="px-2 py-1 text-xs font-medium rounded-full bg-green-100 text-green-800">
                      {payment.status || 'Paid'}
                    </span>
                  </td>
                </tr>
              ))}

              {allPayments.length === 0 && (
                <tr>
                  <td colSpan={6} className="px-6 py-4 text-center text-gray-500">
                    No payments found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
        
        {allPayments.length === 0 && (
          <div className="text-center py-12">
            <div className="text-gray-500 text-lg">No payments found</div>
            <p className="text-gray-400 mt-2">All orders are currently unpaid</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default APPaymentsTab;
